import{A as r}from"./chunks/ArticleCard.CDfW01WY.js";import{Y as t,A as s}from"./chunks/YoutubePlayTo.0ALdw_RS.js";import{C as d}from"./chunks/CityCard.DpoZM121.js";import{P as o}from"./chunks/Photo.CJtI8asC.js";import{c as l,b as u,k as n,h as a,j as i}from"./chunks/framework.Dm3xLcfC.js";import"./chunks/index.CQfaiQA1.js";import"./chunks/assets.CyscBh63.js";import"./chunks/theme.FCS-NGNl.js";import"./chunks/index.DkjRs5Eh.js";import"./chunks/index.CIGM0Yps.js";const j=JSON.parse(`{"title":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders","description":"Experience Joel's unforgettable journey in Chengdu, exploring panda reserves and ancient culture. Discover top attractions, delicious hotpot.","frontmatter":{"layout":"composition","head":[["link",{"rel":"alternate","href":"https://www.etourbook.com/articles/joels-chengdu-adventure","hreflang":"x-default"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/articles/joels-chengdu-adventure","hreflang":"en"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ja/articles/joels-chengdu-adventure","hreflang":"ja"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ko/articles/joels-chengdu-adventure","hreflang":"ko"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/th/articles/joels-chengdu-adventure","hreflang":"th"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ms/articles/joels-chengdu-adventure","hreflang":"ms"}],["link",{"rel":"canonical","href":"https://www.etourbook.com/articles/joels-chengdu-adventure"}],["meta",{"name":"title","content":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders"}],["meta",{"property":"og:title","content":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders"}],["meta",{"property":"og:description","content":"Experience Joel's unforgettable journey in Chengdu, exploring panda reserves and ancient culture. Discover top attractions, delicious hotpot."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:url","content":"https://www.etourbook.com/articles/joels-chengdu-adventure"}]],"title":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders","description":"Experience Joel's unforgettable journey in Chengdu, exploring panda reserves and ancient culture. Discover top attractions, delicious hotpot.","og":{"og:title":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders","og:description":"Experience Joel's unforgettable journey in Chengdu, exploring panda reserves and ancient culture. Discover top attractions, delicious hotpot.","og:type":"article"},"article":{"link":"joels-chengdu-adventure","category":["Pandas and Other Animals","China Vlog"],"title":{"root":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders","ja":"ジョエルの成都旅：パンダ、火鍋、文化の魅力","ko":"조엘의 청두 모험: 판다, 핫팟, 그리고 문화의 경이","th":"การผจญภัยของโจเอลในเฉิงตู: แพนด้า ฮอทพอท และความมหัศจรรย์ทางวัฒนธรรม","ms":"Petualangan Joel di Chengdu: Panda, Tumis Panas, dan Keajaiban Budaya"},"videoId":"UqmPUsnWg14","introduce":{"root":"British traveler Joel Friend revisits Chengdu, China, for another adventure. He visits the Panda Valley to observe giant pandas and learns about their conservation status and reintroduction program. Joel also checks out the red pandas and explores scenic spots in downtown Chengdu, enjoying traditional Chinese ethnic music. Next, he heads to the Jinsha Site Museum to delve into the ancient culture and history of the Shu people. Finally, Joel experiences Chengdu's daily life at Jinli Street, where he tries sugar painting, ear cleaning, and even robot-made coffee. Despite his previous unsuccessful attempt, Joel successfully enjoys Chengdu hotpot this time, sampling various dishes such as duck tongues and pig brains. To wrap up his trip, he visits a shopping mall and spots giant panda statues on the rooftops. Chengdu's relaxed lifestyle and panda culture leave a lasting impression on Joel.","ja":"イギリス人トラベラー、ジョエル・フレンドが再び中国成都を訪れる旅行記録です。彼は成都のパンダバレーでジャイアントパンダを観賞し、その生存状況や再導入計画について学びます。また、レッサーパンダも見学し、成都市内の観光地を訪れ、中国の民族音楽を楽しみました。その後、彼は金沙遺跡博物館に向かい、古代蜀人の文化と歴史について深く探ります。最後に、彼は錦里街で成都の日常生活を体験します。糖絵（とうが）や耳掃除、さらにはロボットによるコーヒー作りも試しました。前回うまくいかなかった成都の火鍋も、今回は成功し、鴨の舌や豚の脳みそなどさまざまな料理を堪能しました。さらに、彼はショッピングモールを訪れ、屋上にあるパンダの像を見ました。成都のゆったりとしたライフスタイルとパンダ文化がジョエルに深い印象を残しました。","ko":"영국 여행가 조엘 프렌드가 다시 중국 청두를 방문하여 또 다른 모험을 즐깁니다. 그는 판다 계곡을 방문하여 자이언트 판다를 관찰하고 그들의 보존 상태와 재도입 프로그램에 대해 배웁니다. 또한 레서 판다도 확인하고 청두 시내의 명소를 탐험하며 전통적인 중국 민족 음악을 즐깁니다. 다음으로 그는 진사 유적 박물관을 방문하여 고대 쓰 사람들의 문화와 역사를 깊이 탐구합니다. 마지막으로 조엘은 진리 거리에서 청두의 일상 생활을 경험하며 설탕 그림, 귀 청소, 심지어 로봇이 만든 커피도 시도합니다. 이전에 실패했던 청두 핫팟도 이번에는 성공적으로 즐기며 오리 혀, 돼지 뇌 등 다양한 요리를 맛봅니다. 여행을 마무리하면서 그는 쇼핑몰을 방문하고 지붕 위에 있는 거대한 판다 동상을 발견합니다. 청두의 여유로운 라이프스타일과 판다 문화는 조엘에게 깊은 인상을 남깁니다.","th":"โจเอล นักเดินทางชาวอังกฤษ เยี่ยมชมเฉิงตู ประเทศจีน อีกครั้งเพื่อผจญภัยใหม่ เขาไปเยี่ยมชมหุบเขาแพนด้าเพื่อสังเกตการณ์แพนด้าขนาดใหญ่และเรียนรู้เกี่ยวกับสถานะการอนุรักษ์และการฟื้นฟูประชากรแพนด้า นอกจากนี้เขายังสำรวจแพนด้าแดงและสำรวจสถานที่ท่องเที่ยวในเมืองเฉิงตู พร้อมทั้งเพลิดเพลินกับดนตรีพื้นเมืองจีน จากนั้นเขาไปเยี่ยมชมพิพิธภัณฑ์ไซต์จินซา เพื่อสำรวจวัฒนธรรมและประวัติศาสตร์โบราณของชนชาติซู ในที่สุดโจเอลได้สัมผัสชีวิตประจำวันในเฉิงตูที่ถนนจินหลี่ โดยลองทำภาพวาดน้ำตาล การทำความสะอาดหู และกาแฟที่ทำโดยหุ่นยนต์ แม้ว่าจะเคยไม่สำเร็จในการลองฮอทพอทมาก่อน แต่ครั้งนี้โจเอลสามารถเพลิดเพลินกับฮอทพอทเฉิงตูได้สำเร็จ ลองชิมเมนูต่างๆ เช่น ลิ้นเป็ดและสมองหมู เพื่อปิดทริป เขาได้เยี่ยมชมศูนย์การค้าและพบกับรูปปั้นแพนด้าขนาดใหญ่บนหลังคา ไลฟ์สไตล์ที่ผ่อนคลายและวัฒนธรรมแพนด้าของเฉิงตูได้สร้างความประทับใจให้แก่โจเอล","ms":"Pelancong British Joel Friend kembali mengunjungi Chengdu, China untuk satu lagi petualangan. Dia melawat Lembah Panda untuk memerhati panda raksasa dan belajar tentang status pemuliharaan mereka serta program pengenalan semula. Joel juga melihat panda merah dan meneroka tempat-tempat menarik di pusat bandar Chengdu, menikmati muzik etnik tradisional Cina. Seterusnya, dia menuju ke Muzium Tapak Jinsha untuk menyelidiki budaya dan sejarah kuno orang Shu. Akhirnya, Joel mengalami kehidupan harian di Chengdu di Jalan Jinli, di mana dia mencuba lukisan gula, pembersihan telinga, dan bahkan kopi yang dibuat oleh robot. Walaupun cubaan sebelumnya gagal, Joel berjaya menikmati tumis panas Chengdu kali ini, mencuba pelbagai hidangan seperti lidah itik dan otak babi. Untuk menamatkan perjalanannya, dia melawat pusat membeli-belah dan melihat patung panda raksasa di atas bumbung. Gaya hidup santai dan budaya panda Chengdu meninggalkan kesan mendalam kepada Joel."},"description":{"root":"Experience Joel's unforgettable journey in Chengdu, exploring panda reserves and ancient culture. Discover top attractions, delicious hotpot.","ja":"ジョエルが成都で体験する忘れられない旅。パンダ保護区の探索、魅力あふれる成都の観光スポットや美味しい火鍋、独自の伝統をぜひご自分の旅でも体験してください！","ko":"청두에서 조엘이 경험하는 잊지 못할 여행. 판다 보호구역 탐험, 매력적인 청두의 관광 명소와 맛있는 핫팟, 독특한 전통을 당신의 여행에서도 경험해보세요!","th":"สำรวจการเดินทางที่น่าจดจำของโจเอลในเฉิงตู สำรวจเขตสงวนแพนด้าและวัฒนธรรมโบราณ ค้นหาสถานที่ท่องเที่ยวยอดนิยมและฮอทพอทแสนอร่อย","ms":"Alami perjalanan tidak terlupakan Joel di Chengdu, meneroka tapak simpanan panda dan budaya kuno. Terokai tarikan utama, tumis panas lazat."},"city":["Chengdu"],"attraction":["Chengdu Panda Research Base"],"index":100},"attractions":[{"link":"chengdu-panda-research-base","name":"Chengdu Panda Research Base","city":"Chengdu","nameLocale":{"root":"Chengdu Panda Research Base","ja":"成都パンダ研究基地","ko":"청두 판다 연구 기지","th":"ฐานวิจัยแพนด้าเฉิงตู","ms":"Tapak Penyelidikan Panda Chengdu"},"introduce":{"root":"World's premier giant panda conservation center.","ja":"パンダ保護研究の中心地。","ko":"세계 최고의 자이언트 판다 보호 센터.","th":"ศูนย์กลางการอนุรักษ์และวิจัยแพนด้าที่สำคัญที่สุดในโลก","ms":"Pusat pemuliharaan panda gergasi utama dunia."},"description":{"root":"Meet adorable pandas at Chengdu Panda Research Base, just 18 km from the city center. Experience conservation efforts and encounter newborn cubs. (Visit Chengdu Panda Research Base, located just 18 km from the city center, to see adorable pandas and learn about conservation efforts.)","ja":"成都市中心から約18kmのパンダ研究基地へ。約120頭のジャイアントパンダや76頭のレッサーパンダが暮らす四川の人気スポットです！","ko":"청두 판다 연구 기지를 방문하세요. 도심에서 단 18km 떨어진 곳에서 귀여운 판다들을 만나보고 보존 노력에 대해 알아보세요!","th":"สำรวจฐานวิจัยแพนด้าเฉิงตู ซึ่งอยู่ห่างจากใจกลางเมืองเพียง 18 กม. เพื่อชมแพนด้าที่น่ารักและเรียนรู้เกี่ยวกับความพยายามในการอนุรักษ์","ms":"Lawati Tapak Penyelidikan Panda Chengdu yang terletak hanya 18 km dari pusat bandar untuk melihat panda yang comel dan memahami usaha-usaha pemuliharaan."},"review":{"root":"The Chengdu Panda Research Base offers an unforgettable experience with its adorable residents. Perfect for families and animal lovers alike.","ja":"この施設は愛らしいパンダたちとの忘れられない体験を提供します。家族連れや動物愛好家に最適です。","ko":"청두 판다 연구 기지는 사랑스러운 판다들과 함께하는 잊을 수 없는 경험을 제공합니다. 가족이나 동물 애호가들에게 완벽한 장소입니다!","th":"ฐานวิจัยแพนด้าเฉิงตูมอบประสบการณ์ที่ไม่รู้ลืมกับผู้อาศัยที่น่ารักของสถานที่นี้ เป็นสถานที่ที่สมบูรณ์แบบสำหรับครอบครัวและผู้ที่ชื่นชอบสัตว์","ms":"Tapak Penyelidikan Panda Chengdu menawarkan pengalaman tidak terlupakan dengan penduduknya yang comel. Sempurna untuk keluarga dan peminat haiwan!"},"location":"30.73297989317107, 104.14587174771397","index":100,"ticketPrice":55,"rating":4.5,"opensHour":[7.5,18],"visitDuration":[3,5],"address":{"root":"No. 1375, North Panda Avenue, Chenghua District, Chengdu","ja":null}}]},"headers":[],"params":{"lang":"en","article":"joels-chengdu-adventure","frontmatter":{"title":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders","description":"Experience Joel's unforgettable journey in Chengdu, exploring panda reserves and ancient culture. Discover top attractions, delicious hotpot.","og":{"og:title":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders","og:description":"Experience Joel's unforgettable journey in Chengdu, exploring panda reserves and ancient culture. Discover top attractions, delicious hotpot.","og:type":"article"},"article":{"link":"joels-chengdu-adventure","category":["Pandas and Other Animals","China Vlog"],"title":{"root":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders","ja":"ジョエルの成都旅：パンダ、火鍋、文化の魅力","ko":"조엘의 청두 모험: 판다, 핫팟, 그리고 문화의 경이","th":"การผจญภัยของโจเอลในเฉิงตู: แพนด้า ฮอทพอท และความมหัศจรรย์ทางวัฒนธรรม","ms":"Petualangan Joel di Chengdu: Panda, Tumis Panas, dan Keajaiban Budaya"},"videoId":"UqmPUsnWg14","introduce":{"root":"British traveler Joel Friend revisits Chengdu, China, for another adventure. He visits the Panda Valley to observe giant pandas and learns about their conservation status and reintroduction program. Joel also checks out the red pandas and explores scenic spots in downtown Chengdu, enjoying traditional Chinese ethnic music. Next, he heads to the Jinsha Site Museum to delve into the ancient culture and history of the Shu people. Finally, Joel experiences Chengdu's daily life at Jinli Street, where he tries sugar painting, ear cleaning, and even robot-made coffee. Despite his previous unsuccessful attempt, Joel successfully enjoys Chengdu hotpot this time, sampling various dishes such as duck tongues and pig brains. To wrap up his trip, he visits a shopping mall and spots giant panda statues on the rooftops. Chengdu's relaxed lifestyle and panda culture leave a lasting impression on Joel.","ja":"イギリス人トラベラー、ジョエル・フレンドが再び中国成都を訪れる旅行記録です。彼は成都のパンダバレーでジャイアントパンダを観賞し、その生存状況や再導入計画について学びます。また、レッサーパンダも見学し、成都市内の観光地を訪れ、中国の民族音楽を楽しみました。その後、彼は金沙遺跡博物館に向かい、古代蜀人の文化と歴史について深く探ります。最後に、彼は錦里街で成都の日常生活を体験します。糖絵（とうが）や耳掃除、さらにはロボットによるコーヒー作りも試しました。前回うまくいかなかった成都の火鍋も、今回は成功し、鴨の舌や豚の脳みそなどさまざまな料理を堪能しました。さらに、彼はショッピングモールを訪れ、屋上にあるパンダの像を見ました。成都のゆったりとしたライフスタイルとパンダ文化がジョエルに深い印象を残しました。","ko":"영국 여행가 조엘 프렌드가 다시 중국 청두를 방문하여 또 다른 모험을 즐깁니다. 그는 판다 계곡을 방문하여 자이언트 판다를 관찰하고 그들의 보존 상태와 재도입 프로그램에 대해 배웁니다. 또한 레서 판다도 확인하고 청두 시내의 명소를 탐험하며 전통적인 중국 민족 음악을 즐깁니다. 다음으로 그는 진사 유적 박물관을 방문하여 고대 쓰 사람들의 문화와 역사를 깊이 탐구합니다. 마지막으로 조엘은 진리 거리에서 청두의 일상 생활을 경험하며 설탕 그림, 귀 청소, 심지어 로봇이 만든 커피도 시도합니다. 이전에 실패했던 청두 핫팟도 이번에는 성공적으로 즐기며 오리 혀, 돼지 뇌 등 다양한 요리를 맛봅니다. 여행을 마무리하면서 그는 쇼핑몰을 방문하고 지붕 위에 있는 거대한 판다 동상을 발견합니다. 청두의 여유로운 라이프스타일과 판다 문화는 조엘에게 깊은 인상을 남깁니다.","th":"โจเอล นักเดินทางชาวอังกฤษ เยี่ยมชมเฉิงตู ประเทศจีน อีกครั้งเพื่อผจญภัยใหม่ เขาไปเยี่ยมชมหุบเขาแพนด้าเพื่อสังเกตการณ์แพนด้าขนาดใหญ่และเรียนรู้เกี่ยวกับสถานะการอนุรักษ์และการฟื้นฟูประชากรแพนด้า นอกจากนี้เขายังสำรวจแพนด้าแดงและสำรวจสถานที่ท่องเที่ยวในเมืองเฉิงตู พร้อมทั้งเพลิดเพลินกับดนตรีพื้นเมืองจีน จากนั้นเขาไปเยี่ยมชมพิพิธภัณฑ์ไซต์จินซา เพื่อสำรวจวัฒนธรรมและประวัติศาสตร์โบราณของชนชาติซู ในที่สุดโจเอลได้สัมผัสชีวิตประจำวันในเฉิงตูที่ถนนจินหลี่ โดยลองทำภาพวาดน้ำตาล การทำความสะอาดหู และกาแฟที่ทำโดยหุ่นยนต์ แม้ว่าจะเคยไม่สำเร็จในการลองฮอทพอทมาก่อน แต่ครั้งนี้โจเอลสามารถเพลิดเพลินกับฮอทพอทเฉิงตูได้สำเร็จ ลองชิมเมนูต่างๆ เช่น ลิ้นเป็ดและสมองหมู เพื่อปิดทริป เขาได้เยี่ยมชมศูนย์การค้าและพบกับรูปปั้นแพนด้าขนาดใหญ่บนหลังคา ไลฟ์สไตล์ที่ผ่อนคลายและวัฒนธรรมแพนด้าของเฉิงตูได้สร้างความประทับใจให้แก่โจเอล","ms":"Pelancong British Joel Friend kembali mengunjungi Chengdu, China untuk satu lagi petualangan. Dia melawat Lembah Panda untuk memerhati panda raksasa dan belajar tentang status pemuliharaan mereka serta program pengenalan semula. Joel juga melihat panda merah dan meneroka tempat-tempat menarik di pusat bandar Chengdu, menikmati muzik etnik tradisional Cina. Seterusnya, dia menuju ke Muzium Tapak Jinsha untuk menyelidiki budaya dan sejarah kuno orang Shu. Akhirnya, Joel mengalami kehidupan harian di Chengdu di Jalan Jinli, di mana dia mencuba lukisan gula, pembersihan telinga, dan bahkan kopi yang dibuat oleh robot. Walaupun cubaan sebelumnya gagal, Joel berjaya menikmati tumis panas Chengdu kali ini, mencuba pelbagai hidangan seperti lidah itik dan otak babi. Untuk menamatkan perjalanannya, dia melawat pusat membeli-belah dan melihat patung panda raksasa di atas bumbung. Gaya hidup santai dan budaya panda Chengdu meninggalkan kesan mendalam kepada Joel."},"description":{"root":"Experience Joel's unforgettable journey in Chengdu, exploring panda reserves and ancient culture. Discover top attractions, delicious hotpot.","ja":"ジョエルが成都で体験する忘れられない旅。パンダ保護区の探索、魅力あふれる成都の観光スポットや美味しい火鍋、独自の伝統をぜひご自分の旅でも体験してください！","ko":"청두에서 조엘이 경험하는 잊지 못할 여행. 판다 보호구역 탐험, 매력적인 청두의 관광 명소와 맛있는 핫팟, 독특한 전통을 당신의 여행에서도 경험해보세요!","th":"สำรวจการเดินทางที่น่าจดจำของโจเอลในเฉิงตู สำรวจเขตสงวนแพนด้าและวัฒนธรรมโบราณ ค้นหาสถานที่ท่องเที่ยวยอดนิยมและฮอทพอทแสนอร่อย","ms":"Alami perjalanan tidak terlupakan Joel di Chengdu, meneroka tapak simpanan panda dan budaya kuno. Terokai tarikan utama, tumis panas lazat."},"city":["Chengdu"],"attraction":["Chengdu Panda Research Base"],"index":100},"attractions":[{"link":"chengdu-panda-research-base","name":"Chengdu Panda Research Base","city":"Chengdu","nameLocale":{"root":"Chengdu Panda Research Base","ja":"成都パンダ研究基地","ko":"청두 판다 연구 기지","th":"ฐานวิจัยแพนด้าเฉิงตู","ms":"Tapak Penyelidikan Panda Chengdu"},"introduce":{"root":"World's premier giant panda conservation center.","ja":"パンダ保護研究の中心地。","ko":"세계 최고의 자이언트 판다 보호 센터.","th":"ศูนย์กลางการอนุรักษ์และวิจัยแพนด้าที่สำคัญที่สุดในโลก","ms":"Pusat pemuliharaan panda gergasi utama dunia."},"description":{"root":"Meet adorable pandas at Chengdu Panda Research Base, just 18 km from the city center. Experience conservation efforts and encounter newborn cubs. (Visit Chengdu Panda Research Base, located just 18 km from the city center, to see adorable pandas and learn about conservation efforts.)","ja":"成都市中心から約18kmのパンダ研究基地へ。約120頭のジャイアントパンダや76頭のレッサーパンダが暮らす四川の人気スポットです！","ko":"청두 판다 연구 기지를 방문하세요. 도심에서 단 18km 떨어진 곳에서 귀여운 판다들을 만나보고 보존 노력에 대해 알아보세요!","th":"สำรวจฐานวิจัยแพนด้าเฉิงตู ซึ่งอยู่ห่างจากใจกลางเมืองเพียง 18 กม. เพื่อชมแพนด้าที่น่ารักและเรียนรู้เกี่ยวกับความพยายามในการอนุรักษ์","ms":"Lawati Tapak Penyelidikan Panda Chengdu yang terletak hanya 18 km dari pusat bandar untuk melihat panda yang comel dan memahami usaha-usaha pemuliharaan."},"review":{"root":"The Chengdu Panda Research Base offers an unforgettable experience with its adorable residents. Perfect for families and animal lovers alike.","ja":"この施設は愛らしいパンダたちとの忘れられない体験を提供します。家族連れや動物愛好家に最適です。","ko":"청두 판다 연구 기지는 사랑스러운 판다들과 함께하는 잊을 수 없는 경험을 제공합니다. 가족이나 동물 애호가들에게 완벽한 장소입니다!","th":"ฐานวิจัยแพนด้าเฉิงตูมอบประสบการณ์ที่ไม่รู้ลืมกับผู้อาศัยที่น่ารักของสถานที่นี้ เป็นสถานที่ที่สมบูรณ์แบบสำหรับครอบครัวและผู้ที่ชื่นชอบสัตว์","ms":"Tapak Penyelidikan Panda Chengdu menawarkan pengalaman tidak terlupakan dengan penduduknya yang comel. Sempurna untuk keluarga dan peminat haiwan!"},"location":"30.73297989317107, 104.14587174771397","index":100,"ticketPrice":55,"rating":4.5,"opensHour":[7.5,18],"visitDuration":[3,5],"address":{"root":"No. 1375, North Panda Avenue, Chenghua District, Chengdu","ja":null}}]}},"relativePath":"articles/joels-chengdu-adventure.md","filePath":"[lang]/articles/[article].md","lastUpdated":1739695371000}`),h={name:"articles/joels-chengdu-adventure.md"},x=Object.assign(h,{setup(g){return(c,e)=>(u(),l("div",null,[n(d,{name:"Chengdu"}),e[0]||(e[0]=a("h2",{id:"head-to-the-chengdu-panda-base-to-see-the-pandas",tabindex:"-1"},[i("Head to the Chengdu Panda Base to see the pandas "),a("a",{class:"header-anchor",href:"#head-to-the-chengdu-panda-base-to-see-the-pandas","aria-label":'Permalink to "Head to the Chengdu Panda Base to see the pandas"'},"​")],-1)),n(t,{to:"0:18"}),e[1]||(e[1]=a("p",null,"Learn about the pandas at Panda Valley, including Chenglang, the historically lightest newborn panda (weighing only one-third of an iPhone). Afterward, visit the red pandas.",-1)),n(s,{name:"Chengdu Panda Research Base"}),n(o,{name:"babyPanda",alt:"Panda baby"}),n(o,{name:"pandaMother",alt:"A panda mother and her cub"}),e[2]||(e[2]=a("p",null,`The Chengdu Research Base of Giant Panda Breeding is not only home to the world's largest population of captive giant pandas but also a place of miracles. Here, a baby panda like "Chenglang," who was born weighing only 42.8 grams—far below the average birth weight of 80-200 grams—was successfully nurtured into a healthy individual through advanced scientific conservation methods. Using incubators, specialized formula feeding, and other cutting-edge technologies, the base overcame the challenges posed by her extremely low birth weight, showcasing humanity's remarkable achievements in wildlife conservation. The base boasts world-leading breeding technologies for giant pandas, including health management, disease prevention, and genetic resource preservation. By the end of 2023, the number of captive giant pandas at the base reached 244, and it also protects and breeds other endangered species such as red pandas, making it the global hub for the largest captive population of red pandas, with over 160 individuals. Visiting here allows you to observe these adorable national treasures up close while learning about their stories and the hard work behind their conservation.`,-1)),e[3]||(e[3]=a("h2",{id:"experience-chinese-folk-music-at-the-yanhua-pool-museum",tabindex:"-1"},[i("Experience Chinese folk music at The Yanhua Pool Museum "),a("a",{class:"header-anchor",href:"#experience-chinese-folk-music-at-the-yanhua-pool-museum","aria-label":'Permalink to "Experience Chinese folk music at The Yanhua Pool Museum"'},"​")],-1)),n(t,{to:"3:35"}),e[4]||(e[4]=a("p",null,"The Yanhua Pool Museum, a hidden treasure located at No. 54, Da Dong Street, Chongyang Town, Chongzhou City, Chengdu, is an excellent spot for viewing ginkgo leaves.",-1)),e[5]||(e[5]=a("h2",{id:"visit-the-jinsha-site-museum-and-explore-the-ancient-shu-civilization",tabindex:"-1"},[i("Visit the Jinsha Site Museum and explore the ancient Shu civilization "),a("a",{class:"header-anchor",href:"#visit-the-jinsha-site-museum-and-explore-the-ancient-shu-civilization","aria-label":'Permalink to "Visit the Jinsha Site Museum and explore the ancient Shu civilization"'},"​")],-1)),n(t,{to:"5:17"}),e[6]||(e[6]=a("p",null,"The Jinsha Site Museum is a site-specific museum built on the location of the Jinsha Site, an important archaeological discovery from the ancient Shu civilization. It is also a thematic museum that showcases the ancient Shu culture of the Sichuan region during the Shang and Zhou dynasties. The Jinsha Site is the ruins of the capital city of the ancient Shu Kingdom, which was the center of civilization in the upper reaches of the Yangtze River from the 12th to the 7th century BCE (approximately 3,200 to 2,990 years ago).",-1)),e[7]||(e[7]=a("h2",{id:"visit-jinli-ancient-street-and-experience-ear-cleaning",tabindex:"-1"},[i("Visit Jinli Ancient Street and experience ear cleaning "),a("a",{class:"header-anchor",href:"#visit-jinli-ancient-street-and-experience-ear-cleaning","aria-label":'Permalink to "Visit Jinli Ancient Street and experience ear cleaning"'},"​")],-1)),n(t,{to:"7:20"}),e[8]||(e[8]=a("p",null,`Jinli is one of Chengdu's historical and cultural districts, once referred to as Chengdu's version of the "Along the River During the Qingming Festival" painting. It is a charming street for experiencing Three Kingdoms culture and Chengdu's local customs.`,-1)),e[9]||(e[9]=a("p",null,"Sugar painting is an art form that uses sugar to create shapes. It started as a court art during the Ming and Qing dynasties in China and later became a traditional craft loved by everyone. To make sugar paintings, maltose or cane sugar is heated until it melts, and then artists quickly draw patterns on a smooth stone slab. Once the sugar cools, it hardens quickly and forms the final shape.",-1)),e[10]||(e[10]=a("p",null,"Ear cleaning is a traditional ear care technique that originated in China and has a history of over a thousand years. It’s not just about digging out earwax—it’s a mix of ear cleaning, acupressure massage, and wellness therapy for a full experience. Modern ear cleaning shops even have 3D visualization tools so you can see inside your ear canal clearly, making it safer and more reassuring.",-1)),e[11]||(e[11]=a("h2",{id:"experience-hakka-culture-in-luodai-ancient-town",tabindex:"-1"},[i("Experience Hakka culture in Luodai Ancient Town "),a("a",{class:"header-anchor",href:"#experience-hakka-culture-in-luodai-ancient-town","aria-label":'Permalink to "Experience Hakka culture in Luodai Ancient Town"'},"​")],-1)),n(t,{to:"10:15"}),e[12]||(e[12]=a("p",null,"Luodai Ancient Town, located in Longquanyi District of Chengdu, is one of the best-preserved Hakka ancient towns in China. It boasts a long history and unique Hakka culture, with ancient streets, historic buildings, and a rich variety of folk cultural activities within the town.",-1)),e[13]||(e[13]=a("p",null,"The Hakka people are an ethnic group in China with unique culture and language, originating from Central Plains. They migrated southward multiple times due to wars and famines. Known for their diligence and resilience, they are famous for round earthen buildings and have preserved rich traditions like mountain songs, lion dances, and distinctive culinary habits.",-1)),n(o,{name:"hakkaTulou",alt:"Hakka Tulou"}),e[14]||(e[14]=a("h2",{id:"visit-wangjiang-tower-park-in-chengdu",tabindex:"-1"},[i("Visit Wangjiang Tower Park in Chengdu "),a("a",{class:"header-anchor",href:"#visit-wangjiang-tower-park-in-chengdu","aria-label":'Permalink to "Visit Wangjiang Tower Park in Chengdu"'},"​")],-1)),n(t,{to:"13:16"}),e[15]||(e[15]=a("p",null,"Wangjiang Tower Park is a famous cultural park located on the south bank of the Jinjiang River in Chengdu. It is renowned for its classical gardens, historic buildings, and rich cultural heritage. The iconic Wangjiang Tower offers stunning views of the surrounding area and is dedicated to the memory of Xue Tao, a famous female poet from the Tang Dynasty.",-1)),e[16]||(e[16]=a("h2",{id:"tackle-hot-pot",tabindex:"-1"},[i("Tackle hot pot "),a("a",{class:"header-anchor",href:"#tackle-hot-pot","aria-label":'Permalink to "Tackle hot pot"'},"​")],-1)),n(t,{to:"14:14"}),e[17]||(e[17]=a("p",null,"Joel tried hot pot again after his last attempt in Chongqing.",-1)),n(r,{link:"chongqing-hot-pot"})]))}});export{j as __pageData,x as default};
