import{A as r}from"./chunks/ArticleCard.BoZYi-YA.js";import{Y as i,A as o}from"./chunks/YoutubePlayTo.Bicrhurs.js";import{C as s}from"./chunks/CityCard.C-gQIelC.js";import{P as u}from"./chunks/Photo.CJtI8asC.js";import{c as g,b as l,k as e,h as n,j as t}from"./chunks/framework.Dm3xLcfC.js";import"./chunks/index.CURome3D.js";import"./chunks/assets.CyscBh63.js";import"./chunks/theme.CRQB9q5H.js";import"./chunks/index.BzgBWBQS.js";import"./chunks/index.CIGM0Yps.js";const v=JSON.parse(`{"title":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","description":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","frontmatter":{"layout":"composition","head":[["link",{"rel":"alternate","href":"https://www.etourbook.com/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"x-default"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"en"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ja/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"ja"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ko/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"ko"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/th/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"th"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ms/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"ms"}],["link",{"rel":"canonical","href":"https://www.etourbook.com/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain"}],["meta",{"name":"title","content":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain"}],["meta",{"property":"og:title","content":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain"}],["meta",{"property":"og:description","content":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:url","content":"https://www.etourbook.com/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain"}]],"title":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","description":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","og":{"og:title":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","og:description":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","og:type":"article"},"article":{"link":"korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","category":["Natural Scenery","Chinese Cultural Heritage","China Vlog"],"title":{"root":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","ja":"韓国教育長の退職旅：虎跳峽と玉龍雪山","ko":"한국 교육감의 퇴역 여행: 호점협과 용설산","th":"การเดินทางของอดีตผู้อำนวยการศึกษาเกาหลีสู่ช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกร","ms":"Perjalanan Ketua Pendidikan Berundur dari Korea ke Jurang Harimau Melompat dan Gunung Salji Naga Giok"},"videoId":"L8aGEwBb4uM","introduce":{"root":"After retiring as the education commissioner of South Korea, he traveled to Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan, China. During his journey, he experienced China's train culture, tasted local cuisine, visited traditional markets, and found comfortable accommodation in Lijiang. He also shared the challenges and breathtaking views of hiking up Jade Dragon Snow Mountain, along with a small tip about using sunscreen sticks. In Lachun Village, he enjoyed Korean dishes and admired the magnificent natural scenery in places like Walnut Leaf and Haoyilu. He visited Jade Dragon Snow Mountain, experienced altitude sickness, and successfully reached the summit. Throughout the trip, he immersed himself in the local culture and cuisine, forming deep friendships with the locals.","ja":"韓国教育長官が退職後、中国雲南省の虎跳峽（こびょうか）と玉龍雪山（ぎょくりゅうせつざん）へ旅行しました。旅の中で彼は中国の鉄道文化を体験し、地元の料理を味わい、伝統市場を見学し、麗江（りきょう）で快適な宿泊先を見つけました。また、玉龍雪山へのハイキングの困難さや美しい景色について語り、日焼け止めスティックを使った小さなコツも共有しました。ラ村では韓国料理を食べ、胡桃葉（ごうだよう）や好意路（こうぎろ）などで雄大な自然を楽しみました。彼は玉龍雪山を訪れ、高山病を経験しながらも無事に頂上に到達しました。旅を通じて、彼は現地の文化や料理を体験し、現地の人々との深い友情を築きました。","ko":"한국 교육감이 퇴직 후 중국 윈난성의 호점협과 용설산을 여행했습니다. 그는 여행 중에 중국 기차 문화를 체험하고 현지 음식을 맛보며 전통 시장을 방문했고, 리장에서 편안한 숙소를 찾았습니다. 또한 용설산 트레킹의 어려움과 아름다운 경치에 대해 이야기하며 자외선 차단제 스틱 사용법이라는 작은 팁도 공유했습니다. 라촌에서는 한국 요리를 먹었고, 호두엽과 해의로 같은 곳에서 웅대한 자연 풍경을 감상했습니다. 그는 용설산을 방문해 고산병을 경험했지만 결국 정상에 성공적으로 도달했습니다. 여행 동안 그는 현지 문화와 음식을 체험하고 주민들과 깊은 우정을 쌓았습니다.","th":"หลังจากเกษียณจากตำแหน่งผู้อำนวยการศึกษาของเกาหลีใต้ เขาได้เดินทางไปยังช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในมณฑลยูนนาน ประเทศจีน ในระหว่างการเดินทาง เขาได้สัมผัสวัฒนธรรมรถไฟของจีน ชิมอาหารท้องถิ่น เยี่ยมชมตลาดแบบดั้งเดิม และพบที่พักสบายๆ ในเมืองลี่เจียง เขายังแบ่งปันความท้าทายและความสวยงามของการเดินป่าขึ้นภูเขาน้ำแข็งหยกมังกร พร้อมคำแนะนำเล็กๆ น้อยๆ เกี่ยวกับการใช้แท่งครีมกันแดด ในหมู่บ้านลาชุน เขาได้เพลิดเพลินกับอาหารเกาหลีและชื่นชมทิวทัศน์ธรรมชาติอันงดงามที่สถานที่อย่างใบมะรุมและถนนเฮาอี้ลู่ เขาได้เยี่ยมชมภูเขาน้ำแข็งหยกมังกร ประสบกับอาการแพ้ความสูง และประสบความสำเร็จในการขึ้นถึงยอดเขา ตลอดการเดินทาง เขาได้สัมผัสกับวัฒนธรรมและอาหารท้องถิ่น และสร้างมิตรภาพที่ลึกซึ้งกับชาวบ้าน","ms":"Selepas bersara sebagai Pengarah Pendidikan Korea Selatan, beliau telah melancong ke Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan, China. Semasa perjalanannya, beliau mengalami budaya kereta api China, merasai masakan tempatan, melawat pasar tradisional, dan menemui penginapan yang selesa di Lijiang. Beliau juga berkongsi cabaran dan pemandangan menakjubkan semasa mendaki Gunung Salji Naga Giok, bersama dengan sedikit petua tentang menggunakan batang pelindung matahari. Di Kampung Lachun, beliau menikmati hidangan Korea dan memuji pemandangan alam semula jadi yang megah di tempat-tempat seperti Daun Kenari dan Jalan HaoYiLu. Beliau telah mengunjungi Gunung Salji Naga Giok, mengalami penyakit ketinggian, dan berjaya mencapai puncak gunung tersebut. Sepanjang perjalanan, beliau terbenam dalam budaya dan masakan tempatan, membina persahabatan yang mendalam dengan penduduk tempatan."},"description":{"root":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","ja":"中国雲南省の虎跳峽や玉龍雪山を巡る壮大な旅に参加しましょう。韓国教育長官も退職後楽しんだ冒険を通じ、地元の鉄道文化、美食、絶景を体験。ハイキングのコツもお届けします！","ko":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","th":"เข้าร่วมการผจญภัยที่น่าตื่นตาตื่นใจผ่านช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในยูนนาน สำรวจทิวทัศน์ที่งดงามและสัมผัสวัฒนธรรมท้องถิ่น อาหารอร่อย และเทคนิคการเดินป่า","ms":"Sertai Sugilway dalam pengembaraan menakjubkan melalui Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan. Alami landskap yang menakjubkan dan sertai pengalaman budaya tempatan, makanan lazat, serta tips pendakian."},"city":["Lijiang"],"attraction":["Shuhe Ancient Town","Tiger Leaping Gorge","Yulong Snow Mountain","Lijiang Ancient Town"],"preferLang":"ko","index":60},"attractions":[{"link":"lijiang-ancient-town","name":"Lijiang Ancient Town","nameChinese":"丽江","namePinyin":"lì jiāng","city":"Lijiang","location":"26.876096896416577, 100.23341124532006","rating":4.7,"ticketPrice":0,"opensHour":[0,24],"visitDuration":[2,3],"address":{"root":"50 meters south of the intersection of Xinhua Street and Jinhong Road, Gucheng District, Lijiang City","ja":null},"nameLocale":{"root":"Lijiang Ancient Town","ja":"丽江古城","ko":"리장고성","th":"เมืองโบราณลี่เจียง","ms":"Bandar Lijiang Kuno"},"introduce":{"root":"Canals & Naxi culture in UNESCO old town.","ja":"水路の街、世界遺産の古都。","ko":"유네스코 세계유산에 등재된 수로와 나시 문화의 고성.","th":"เมืองโบราณที่มีคลองและวัฒนธรรมนาซี ในพื้นที่มรดกโลกยูเนสโก","ms":"Kanil dan budaya Naxi di bandar kuno UNESCO."},"description":{"root":"Explore Lijiang Ancient Town, a UNESCO site known as the 'Venice of the Orient.' Discover its 800-year history, canals, and unique architecture. (Discover Lijiang Ancient Town, a UNESCO World Heritage site often called the 'Venice of the East,' featuring 800 years of history, intricate canals, and distinctive architecture.)","ja":"世界遺産に登録された「東洋のヴェネツィア」として知られる麗江古城を訪れてみませんか。800年にわたる歴史、独特な建築様式、そして複雑な運河システムが魅力です。","ko":"유네스코 세계유산으로 알려진 '동양의 베니스' 리장고성을 탐험하세요. 800년의 역사를 가진 이곳에는 복잡한 수로와 독특한 건축물이 있습니다!","th":"สำรวจเมืองโบราณลี่เจียง แหล่งมรดกโลกของยูเนสโกที่ได้รับสมญาว่า \\"เวนิสแห่งตะวันออก\\" มีประวัติศาสตร์ยาวนาน 800 ปี คลองที่ซับซ้อน และสถาปัตยกรรมที่เป็นเอกลักษณ์","ms":"Lawati Bandar Lijiang Kuno, tapak Warisan Dunia UNESCO yang dikenali sebagai \\"Venice Timur.\\" Ia menampilkan sejarah selama 800 tahun, saliran rumit, dan seni bina unik."},"review":{"root":"In the old town, small lanes and streams crisscross, and every little path is worth exploring. However, it's quite crowded with a strong commercial vibe.","ja":"古い町では小さな道と小川が複雑に絡んでいて、どの小道も探してみる価値があります。でも、観光客が多くて商業的な感じが強いです。","ko":"고성 안에는 작은 길과 개울이 얽혀 있어서 모든 골목이 탐험할 만해요. 하지만 관광객이 많고 상업적인 느낌이 강해요.","th":"ในเมืองโบราณ มีถนนเล็ก ๆ และลำธารที่ข้ามกันไปมา ทุกเส้นทางมีค่าแก่การสำรวจ แต่มันค่อนข้างแออัดและมีบรรยากาศเชิงพาณิชย์ค่อนข้างมาก","ms":"Di dalam bandar lama, lorong-lorong kecil dan anak sungai bersilang, dan setiap jalan kecil mempunyai nilai penjelajahan tersendiri. Walau bagaimanapun, ia agak sesak dengan suasana komersial yang kuat."}},{"link":"shuhe-ancient-town","name":"Shuhe Ancient Town","nameChinese":"束河","namePinyin":"shù hé","city":"Lijiang","nameLocale":{"root":"Shuhe Ancient Town","ja":"束河古镇","ko":"수허고성","th":"เมืองโบราณซูเหอ","ms":"Bandar Shuhe Kuno"},"introduce":{"root":"Tea Horse Road gem with Naxi traditions.","ja":"茶馬古道の要衝、ナシ族の町。","ko":"차마고도의 중요한 위치를 차지하는 나시족의 고성.","th":"เมืองที่สำคัญบนเส้นทางชามาเก่าแก่ และเป็นแหล่งวัฒนธรรมนาซี","ms":"Gemerlapan di Laluan Teh Kuda dengan tradisi Naxi."},"description":{"root":"Visit Shuhe Ancient Town, a well-preserved stop on the historic Tea Horse Road. Admire Jade Dragon Snow Mountain and embrace Naxi culture. (Discover Shuhe Ancient Town, a beautifully preserved site along the historic Tea Horse Road. Enjoy views of Jade Dragon Snow Mountain and immerse yourself in Naxi culture.)","ja":"麗江北部の束河古镇へ訪れてみませんか。煎茶馬古道の要所で、ナシ族文化の「生きた化石」。玉龍雪山の美景や、静かな古城の夜を満喫できる特別な場所です。","ko":"수허고성을 방문하세요. 역사적인 차마고도에 잘 보존된 장소로, 옥룡설산의 아름다운 경치와 함께 나시 문화를 체험할 수 있는 특별한 장소입니다!","th":"สำรวจเมืองโบราณซูเหอ เมืองที่ได้รับการอนุรักษ์อย่างดีบนเส้นทางชามาเก่าแก่ ชมความงามของภูเขาหิมะมังกรหยกและสัมผัสวัฒนธรรมนาซี","ms":"Lawati Bandar Shuhe Kuno, sebuah tapak yang terpelihara dengan baik di sepanjang Laluan Teh Kuda bersejarah. Nikmati pemandangan Gunung Salji Naga Giok dan tenggelam dalam budaya Naxi."},"review":{"root":"In Shuhe, you can enjoy the quiet beauty of the old town and its surrounding landscapes. It's less crowded than Lijiang but still offers plenty to see and do.","ja":"束河では、古い町とその周辺の美しい景観を楽しむことができます。麗江よりも静かですが、見どころがたくさんあります。","ko":"수허에서는 고성과 그 주변의 아름다운 풍경을 조용히 즐길 수 있습니다. 리장보다 덜 붐비지만, 볼거리가 많아요.","th":"ที่ซูเหอ คุณสามารถเพลิดเพลินกับความสวยงามเงียบสงบของเมืองโบราณและทัศนียภาพโดยรอบ มันไม่พลุกพล่านเท่าลี่เจียงแต่ยังคงมีสถานที่ท่องเที่ยวให้ดูมากมาย","ms":"Di Shuhe, anda boleh menikmati keindahan tenang bandar lama dan landskap sekitarnya. Ia kurang sesak berbanding Lijiang tetapi masih menawarkan banyak perkara untuk dilihat dan dilakukan."},"location":"26.91652881591715, 100.20180535253954","rating":4.5,"ticketPrice":0,"opensHour":[0,24],"visitDuration":[1,2],"address":{"root":"Shuhe Road, Gucheng District, Lijiang City","ja":null}},{"link":"tiger-leaping-gorge","name":"Tiger Leaping Gorge","nameChinese":"虎跳峡","namePinyin":"hǔ tiào xiá","city":"Lijiang","nameLocale":{"root":"Tiger Leaping Gorge","ja":"虎跳峡","ko":"호조협","th":"ช่องเขาเสือกระโดด","ms":"Tekukur Harimau"},"introduce":{"root":"World's deepest canyon with raging rapids.","ja":"世界最深の峡谷、激流あり。","ko":"세계에서 가장 깊은 협곡 중 하나로, 격류가 흐르는 곳.","th":"ช่องเขาที่ลึกที่สุดในโลก มีกระแสน้ำเชี่ยวกราก","ms":"Lembah terdalam di dunia dengan aliran deras."},"description":{"root":"Hike Tiger Leaping Gorge, one of the world’s deepest canyons near Lijiang. Marvel at breathtaking views of Jade Dragon and Haba Snow Mountains. (Explore Tiger Leaping Gorge, one of the world’s deepest canyons near Lijiang. Enjoy stunning views of Jade Dragon and Haba Snow Mountains.)","ja":"世界最深の峡谷之一・虎跳峽と「長江第一湾」を訪れてみませんか。麗江の玉龍雪山と哈巴雪山に囲まれた絶景地で、標識付きトレッキングコースで大自然の壮大さを満喫できます。","ko":"리장 근처 세계에서 가장 깊은 협곡 중 하나인 호조협을 트레킹하세요. 옥룡설산과 하바설산의 장엄한 경치를 감상할 수 있습니다!","th":"สำรวจช่องเขาเสือกระโดด หนึ่งในช่องเขาที่ลึกที่สุดในโลกใกล้กับเมืองลี่เจียง เพลิดเพลินกับทัศนียภาพอันตระการตาของภูเขาหิมะมังกรหยกและฮาบ่า","ms":"Lawati Tekukur Harimau, salah satu lembah terdalam di dunia berhampiran Lijiang. Nikmati pemandangan menakjubkan Gunung Salji Naga Giok dan Haba."},"review":{"root":"The trek is challenging but rewarding, offering incredible views of the gorge and surrounding mountains. Be prepared for steep paths and rugged terrain.","ja":"トレッキングはチャレンジングですが、その見返りは素晴らしいです。峡谷や周辺の山々の絶景が待っています。急な坂道や険しい地形に備えてください。","ko":"트레킹은 도전적이지만 보람차며, 협곡과 주변 산들의 놀라운 경치를 제공합니다. 가파른 길과 거친 지형에 대비하세요.","th":"การเดินป่าท้าทายแต่คุ้มค่ามาก โดยให้วิวที่น่าทึ่งของช่องเขาและเทือกเขาโดยรอบ ควรเตรียมพร้อมสำหรับทางลาดชันและพื้นที่ขรุขระ","ms":"Pendakian adalah mencabar tetapi memberi ganjaran, menawarkan pemandangan luar biasa dari tebing dan gunung-gunung sekitarnya. Bersiap sedia untuk laluan curam dan medan yang kasar."},"location":"27.174093200598822, 100.09885439940953","ticketPrice":73,"rating":4.6,"opensHour":[8.5,17.5],"visitDuration":[2,3],"address":{"root":"Tiger Leaping Gorge(Hutiaoxia Town), Shangri-La County, Diqing Prefecture","ja":null}},{"link":"yulong-snow-moutain","name":"Yulong Snow Mountain","nameChinese":"玉龙雪山","namePinyin":"yù lóng xuě shān","city":"Lijiang","nameLocale":{"root":"Yulong Snow Mountain","ja":"玉龙雪山","ko":"옥룡설산","th":"ภูเขาหิมะหยูหลง","ms":"Gunung Salji Yulong"},"introduce":{"root":"Sacred snow-capped peaks & glacier adventures.","ja":"聖なる雪山と氷河の公園。","ko":"신성한 설산과 빙하 모험의 장소.","th":"ยอดเขาศักดิ์สิทธิ์และผจญภัยบนธารน้ำแข็ง","ms":"Puncak bersalji yang suci dan pengembaraan glasier."},"description":{"root":"Climb Yulong Snow Mountain, a sacred peak of the Naxi people in Lijiang. Explore Glacier Park and admire its snow-capped summits. (Discover Yulong Snow Mountain, a sacred peak revered by the Naxi people in Lijiang. Explore the Glacier Park and marvel at its snow-covered summits.)","ja":"麗江の世界遺産・玉龍雪山を訪れてみませんか。ナシ族の聖山で、氷河公園を散策し、13の雪覆い峰々や標高5,596メートルの扇子陡からの絶景をぜひお楽しみください。","ko":"리장에 위치한 나시족의 성산인 옥룡설산을 방문하세요. 빙하공원을 탐험하고 눈덮힌 산봉우리를 감상할 수 있습니다!","th":"สำรวจภูเขาหิมะหยูหลง ยอดเขาศักดิ์สิทธิ์ของชนชาตินาซีในลี่เจียง ชมสวนธารน้ำแข็งและทัศนียภาพอันตระการตาของยอดเขาที่ปกคลุมไปด้วยหิมะ","ms":"Lawati Gunung Salji Yulong, puncak suci bagi etnik Naxi di Lijiang. Eksplorasi Taman Glasier dan kagumi puncak-puncak berlapis salji."},"review":{"root":"The views from the mountain are absolutely stunning, especially when you reach the higher elevations. Be prepared for some physical exertion, but it's well worth it.","ja":"山からの眺めは絶対に素晴らしいです。特に高い場所に到達したときには言葉を失います。体力を使うことを覚悟してください。しかし、それだけの価値があります。","ko":"산에서 내려다보는 풍경은 정말 놀랍습니다, 특히 고도가 높아질수록 더욱 그렇습니다. 체력 소모를 각오해야 하지만, 그만한 가치가 있어요.","th":"วิวจากยอดเขานั้นสวยงามอย่างยิ่ง โดยเฉพาะเมื่อคุณไปถึงบริเวณที่สูงขึ้น ควรเตรียมพร้อมสำหรับการใช้แรงงานทางกาย แต่มันคุ้มค่าแน่นอน","ms":"Pemandangan dari gunung adalah luar biasa, terutama apabila anda mencapai ketinggian yang lebih tinggi. Bersiap sedia untuk beberapa usaha fizikal, tetapi ia sangat berbaloi."},"location":"27.1014838299386, 100.19730515172101","ticketPrice":60,"rating":4.5,"opensHour":[7.5,16.5],"visitDuration":8,"address":{"root":"15 km north of Lijiang Old Town, Yulong County, Lijiang City","ja":null}}]},"headers":[],"params":{"lang":"en","article":"korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","frontmatter":{"title":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","description":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","og":{"og:title":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","og:description":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","og:type":"article"},"article":{"link":"korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","category":["Natural Scenery","Chinese Cultural Heritage","China Vlog"],"title":{"root":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","ja":"韓国教育長の退職旅：虎跳峽と玉龍雪山","ko":"한국 교육감의 퇴역 여행: 호점협과 용설산","th":"การเดินทางของอดีตผู้อำนวยการศึกษาเกาหลีสู่ช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกร","ms":"Perjalanan Ketua Pendidikan Berundur dari Korea ke Jurang Harimau Melompat dan Gunung Salji Naga Giok"},"videoId":"L8aGEwBb4uM","introduce":{"root":"After retiring as the education commissioner of South Korea, he traveled to Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan, China. During his journey, he experienced China's train culture, tasted local cuisine, visited traditional markets, and found comfortable accommodation in Lijiang. He also shared the challenges and breathtaking views of hiking up Jade Dragon Snow Mountain, along with a small tip about using sunscreen sticks. In Lachun Village, he enjoyed Korean dishes and admired the magnificent natural scenery in places like Walnut Leaf and Haoyilu. He visited Jade Dragon Snow Mountain, experienced altitude sickness, and successfully reached the summit. Throughout the trip, he immersed himself in the local culture and cuisine, forming deep friendships with the locals.","ja":"韓国教育長官が退職後、中国雲南省の虎跳峽（こびょうか）と玉龍雪山（ぎょくりゅうせつざん）へ旅行しました。旅の中で彼は中国の鉄道文化を体験し、地元の料理を味わい、伝統市場を見学し、麗江（りきょう）で快適な宿泊先を見つけました。また、玉龍雪山へのハイキングの困難さや美しい景色について語り、日焼け止めスティックを使った小さなコツも共有しました。ラ村では韓国料理を食べ、胡桃葉（ごうだよう）や好意路（こうぎろ）などで雄大な自然を楽しみました。彼は玉龍雪山を訪れ、高山病を経験しながらも無事に頂上に到達しました。旅を通じて、彼は現地の文化や料理を体験し、現地の人々との深い友情を築きました。","ko":"한국 교육감이 퇴직 후 중국 윈난성의 호점협과 용설산을 여행했습니다. 그는 여행 중에 중국 기차 문화를 체험하고 현지 음식을 맛보며 전통 시장을 방문했고, 리장에서 편안한 숙소를 찾았습니다. 또한 용설산 트레킹의 어려움과 아름다운 경치에 대해 이야기하며 자외선 차단제 스틱 사용법이라는 작은 팁도 공유했습니다. 라촌에서는 한국 요리를 먹었고, 호두엽과 해의로 같은 곳에서 웅대한 자연 풍경을 감상했습니다. 그는 용설산을 방문해 고산병을 경험했지만 결국 정상에 성공적으로 도달했습니다. 여행 동안 그는 현지 문화와 음식을 체험하고 주민들과 깊은 우정을 쌓았습니다.","th":"หลังจากเกษียณจากตำแหน่งผู้อำนวยการศึกษาของเกาหลีใต้ เขาได้เดินทางไปยังช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในมณฑลยูนนาน ประเทศจีน ในระหว่างการเดินทาง เขาได้สัมผัสวัฒนธรรมรถไฟของจีน ชิมอาหารท้องถิ่น เยี่ยมชมตลาดแบบดั้งเดิม และพบที่พักสบายๆ ในเมืองลี่เจียง เขายังแบ่งปันความท้าทายและความสวยงามของการเดินป่าขึ้นภูเขาน้ำแข็งหยกมังกร พร้อมคำแนะนำเล็กๆ น้อยๆ เกี่ยวกับการใช้แท่งครีมกันแดด ในหมู่บ้านลาชุน เขาได้เพลิดเพลินกับอาหารเกาหลีและชื่นชมทิวทัศน์ธรรมชาติอันงดงามที่สถานที่อย่างใบมะรุมและถนนเฮาอี้ลู่ เขาได้เยี่ยมชมภูเขาน้ำแข็งหยกมังกร ประสบกับอาการแพ้ความสูง และประสบความสำเร็จในการขึ้นถึงยอดเขา ตลอดการเดินทาง เขาได้สัมผัสกับวัฒนธรรมและอาหารท้องถิ่น และสร้างมิตรภาพที่ลึกซึ้งกับชาวบ้าน","ms":"Selepas bersara sebagai Pengarah Pendidikan Korea Selatan, beliau telah melancong ke Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan, China. Semasa perjalanannya, beliau mengalami budaya kereta api China, merasai masakan tempatan, melawat pasar tradisional, dan menemui penginapan yang selesa di Lijiang. Beliau juga berkongsi cabaran dan pemandangan menakjubkan semasa mendaki Gunung Salji Naga Giok, bersama dengan sedikit petua tentang menggunakan batang pelindung matahari. Di Kampung Lachun, beliau menikmati hidangan Korea dan memuji pemandangan alam semula jadi yang megah di tempat-tempat seperti Daun Kenari dan Jalan HaoYiLu. Beliau telah mengunjungi Gunung Salji Naga Giok, mengalami penyakit ketinggian, dan berjaya mencapai puncak gunung tersebut. Sepanjang perjalanan, beliau terbenam dalam budaya dan masakan tempatan, membina persahabatan yang mendalam dengan penduduk tempatan."},"description":{"root":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","ja":"中国雲南省の虎跳峽や玉龍雪山を巡る壮大な旅に参加しましょう。韓国教育長官も退職後楽しんだ冒険を通じ、地元の鉄道文化、美食、絶景を体験。ハイキングのコツもお届けします！","ko":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","th":"เข้าร่วมการผจญภัยที่น่าตื่นตาตื่นใจผ่านช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในยูนนาน สำรวจทิวทัศน์ที่งดงามและสัมผัสวัฒนธรรมท้องถิ่น อาหารอร่อย และเทคนิคการเดินป่า","ms":"Sertai Sugilway dalam pengembaraan menakjubkan melalui Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan. Alami landskap yang menakjubkan dan sertai pengalaman budaya tempatan, makanan lazat, serta tips pendakian."},"city":["Lijiang"],"attraction":["Shuhe Ancient Town","Tiger Leaping Gorge","Yulong Snow Mountain","Lijiang Ancient Town"],"preferLang":"ko","index":60},"attractions":[{"link":"lijiang-ancient-town","name":"Lijiang Ancient Town","nameChinese":"丽江","namePinyin":"lì jiāng","city":"Lijiang","location":"26.876096896416577, 100.23341124532006","rating":4.7,"ticketPrice":0,"opensHour":[0,24],"visitDuration":[2,3],"address":{"root":"50 meters south of the intersection of Xinhua Street and Jinhong Road, Gucheng District, Lijiang City","ja":null},"nameLocale":{"root":"Lijiang Ancient Town","ja":"丽江古城","ko":"리장고성","th":"เมืองโบราณลี่เจียง","ms":"Bandar Lijiang Kuno"},"introduce":{"root":"Canals & Naxi culture in UNESCO old town.","ja":"水路の街、世界遺産の古都。","ko":"유네스코 세계유산에 등재된 수로와 나시 문화의 고성.","th":"เมืองโบราณที่มีคลองและวัฒนธรรมนาซี ในพื้นที่มรดกโลกยูเนสโก","ms":"Kanil dan budaya Naxi di bandar kuno UNESCO."},"description":{"root":"Explore Lijiang Ancient Town, a UNESCO site known as the 'Venice of the Orient.' Discover its 800-year history, canals, and unique architecture. (Discover Lijiang Ancient Town, a UNESCO World Heritage site often called the 'Venice of the East,' featuring 800 years of history, intricate canals, and distinctive architecture.)","ja":"世界遺産に登録された「東洋のヴェネツィア」として知られる麗江古城を訪れてみませんか。800年にわたる歴史、独特な建築様式、そして複雑な運河システムが魅力です。","ko":"유네스코 세계유산으로 알려진 '동양의 베니스' 리장고성을 탐험하세요. 800년의 역사를 가진 이곳에는 복잡한 수로와 독특한 건축물이 있습니다!","th":"สำรวจเมืองโบราณลี่เจียง แหล่งมรดกโลกของยูเนสโกที่ได้รับสมญาว่า \\"เวนิสแห่งตะวันออก\\" มีประวัติศาสตร์ยาวนาน 800 ปี คลองที่ซับซ้อน และสถาปัตยกรรมที่เป็นเอกลักษณ์","ms":"Lawati Bandar Lijiang Kuno, tapak Warisan Dunia UNESCO yang dikenali sebagai \\"Venice Timur.\\" Ia menampilkan sejarah selama 800 tahun, saliran rumit, dan seni bina unik."},"review":{"root":"In the old town, small lanes and streams crisscross, and every little path is worth exploring. However, it's quite crowded with a strong commercial vibe.","ja":"古い町では小さな道と小川が複雑に絡んでいて、どの小道も探してみる価値があります。でも、観光客が多くて商業的な感じが強いです。","ko":"고성 안에는 작은 길과 개울이 얽혀 있어서 모든 골목이 탐험할 만해요. 하지만 관광객이 많고 상업적인 느낌이 강해요.","th":"ในเมืองโบราณ มีถนนเล็ก ๆ และลำธารที่ข้ามกันไปมา ทุกเส้นทางมีค่าแก่การสำรวจ แต่มันค่อนข้างแออัดและมีบรรยากาศเชิงพาณิชย์ค่อนข้างมาก","ms":"Di dalam bandar lama, lorong-lorong kecil dan anak sungai bersilang, dan setiap jalan kecil mempunyai nilai penjelajahan tersendiri. Walau bagaimanapun, ia agak sesak dengan suasana komersial yang kuat."}},{"link":"shuhe-ancient-town","name":"Shuhe Ancient Town","nameChinese":"束河","namePinyin":"shù hé","city":"Lijiang","nameLocale":{"root":"Shuhe Ancient Town","ja":"束河古镇","ko":"수허고성","th":"เมืองโบราณซูเหอ","ms":"Bandar Shuhe Kuno"},"introduce":{"root":"Tea Horse Road gem with Naxi traditions.","ja":"茶馬古道の要衝、ナシ族の町。","ko":"차마고도의 중요한 위치를 차지하는 나시족의 고성.","th":"เมืองที่สำคัญบนเส้นทางชามาเก่าแก่ และเป็นแหล่งวัฒนธรรมนาซี","ms":"Gemerlapan di Laluan Teh Kuda dengan tradisi Naxi."},"description":{"root":"Visit Shuhe Ancient Town, a well-preserved stop on the historic Tea Horse Road. Admire Jade Dragon Snow Mountain and embrace Naxi culture. (Discover Shuhe Ancient Town, a beautifully preserved site along the historic Tea Horse Road. Enjoy views of Jade Dragon Snow Mountain and immerse yourself in Naxi culture.)","ja":"麗江北部の束河古镇へ訪れてみませんか。煎茶馬古道の要所で、ナシ族文化の「生きた化石」。玉龍雪山の美景や、静かな古城の夜を満喫できる特別な場所です。","ko":"수허고성을 방문하세요. 역사적인 차마고도에 잘 보존된 장소로, 옥룡설산의 아름다운 경치와 함께 나시 문화를 체험할 수 있는 특별한 장소입니다!","th":"สำรวจเมืองโบราณซูเหอ เมืองที่ได้รับการอนุรักษ์อย่างดีบนเส้นทางชามาเก่าแก่ ชมความงามของภูเขาหิมะมังกรหยกและสัมผัสวัฒนธรรมนาซี","ms":"Lawati Bandar Shuhe Kuno, sebuah tapak yang terpelihara dengan baik di sepanjang Laluan Teh Kuda bersejarah. Nikmati pemandangan Gunung Salji Naga Giok dan tenggelam dalam budaya Naxi."},"review":{"root":"In Shuhe, you can enjoy the quiet beauty of the old town and its surrounding landscapes. It's less crowded than Lijiang but still offers plenty to see and do.","ja":"束河では、古い町とその周辺の美しい景観を楽しむことができます。麗江よりも静かですが、見どころがたくさんあります。","ko":"수허에서는 고성과 그 주변의 아름다운 풍경을 조용히 즐길 수 있습니다. 리장보다 덜 붐비지만, 볼거리가 많아요.","th":"ที่ซูเหอ คุณสามารถเพลิดเพลินกับความสวยงามเงียบสงบของเมืองโบราณและทัศนียภาพโดยรอบ มันไม่พลุกพล่านเท่าลี่เจียงแต่ยังคงมีสถานที่ท่องเที่ยวให้ดูมากมาย","ms":"Di Shuhe, anda boleh menikmati keindahan tenang bandar lama dan landskap sekitarnya. Ia kurang sesak berbanding Lijiang tetapi masih menawarkan banyak perkara untuk dilihat dan dilakukan."},"location":"26.91652881591715, 100.20180535253954","rating":4.5,"ticketPrice":0,"opensHour":[0,24],"visitDuration":[1,2],"address":{"root":"Shuhe Road, Gucheng District, Lijiang City","ja":null}},{"link":"tiger-leaping-gorge","name":"Tiger Leaping Gorge","nameChinese":"虎跳峡","namePinyin":"hǔ tiào xiá","city":"Lijiang","nameLocale":{"root":"Tiger Leaping Gorge","ja":"虎跳峡","ko":"호조협","th":"ช่องเขาเสือกระโดด","ms":"Tekukur Harimau"},"introduce":{"root":"World's deepest canyon with raging rapids.","ja":"世界最深の峡谷、激流あり。","ko":"세계에서 가장 깊은 협곡 중 하나로, 격류가 흐르는 곳.","th":"ช่องเขาที่ลึกที่สุดในโลก มีกระแสน้ำเชี่ยวกราก","ms":"Lembah terdalam di dunia dengan aliran deras."},"description":{"root":"Hike Tiger Leaping Gorge, one of the world’s deepest canyons near Lijiang. Marvel at breathtaking views of Jade Dragon and Haba Snow Mountains. (Explore Tiger Leaping Gorge, one of the world’s deepest canyons near Lijiang. Enjoy stunning views of Jade Dragon and Haba Snow Mountains.)","ja":"世界最深の峡谷之一・虎跳峽と「長江第一湾」を訪れてみませんか。麗江の玉龍雪山と哈巴雪山に囲まれた絶景地で、標識付きトレッキングコースで大自然の壮大さを満喫できます。","ko":"리장 근처 세계에서 가장 깊은 협곡 중 하나인 호조협을 트레킹하세요. 옥룡설산과 하바설산의 장엄한 경치를 감상할 수 있습니다!","th":"สำรวจช่องเขาเสือกระโดด หนึ่งในช่องเขาที่ลึกที่สุดในโลกใกล้กับเมืองลี่เจียง เพลิดเพลินกับทัศนียภาพอันตระการตาของภูเขาหิมะมังกรหยกและฮาบ่า","ms":"Lawati Tekukur Harimau, salah satu lembah terdalam di dunia berhampiran Lijiang. Nikmati pemandangan menakjubkan Gunung Salji Naga Giok dan Haba."},"review":{"root":"The trek is challenging but rewarding, offering incredible views of the gorge and surrounding mountains. Be prepared for steep paths and rugged terrain.","ja":"トレッキングはチャレンジングですが、その見返りは素晴らしいです。峡谷や周辺の山々の絶景が待っています。急な坂道や険しい地形に備えてください。","ko":"트레킹은 도전적이지만 보람차며, 협곡과 주변 산들의 놀라운 경치를 제공합니다. 가파른 길과 거친 지형에 대비하세요.","th":"การเดินป่าท้าทายแต่คุ้มค่ามาก โดยให้วิวที่น่าทึ่งของช่องเขาและเทือกเขาโดยรอบ ควรเตรียมพร้อมสำหรับทางลาดชันและพื้นที่ขรุขระ","ms":"Pendakian adalah mencabar tetapi memberi ganjaran, menawarkan pemandangan luar biasa dari tebing dan gunung-gunung sekitarnya. Bersiap sedia untuk laluan curam dan medan yang kasar."},"location":"27.174093200598822, 100.09885439940953","ticketPrice":73,"rating":4.6,"opensHour":[8.5,17.5],"visitDuration":[2,3],"address":{"root":"Tiger Leaping Gorge(Hutiaoxia Town), Shangri-La County, Diqing Prefecture","ja":null}},{"link":"yulong-snow-moutain","name":"Yulong Snow Mountain","nameChinese":"玉龙雪山","namePinyin":"yù lóng xuě shān","city":"Lijiang","nameLocale":{"root":"Yulong Snow Mountain","ja":"玉龙雪山","ko":"옥룡설산","th":"ภูเขาหิมะหยูหลง","ms":"Gunung Salji Yulong"},"introduce":{"root":"Sacred snow-capped peaks & glacier adventures.","ja":"聖なる雪山と氷河の公園。","ko":"신성한 설산과 빙하 모험의 장소.","th":"ยอดเขาศักดิ์สิทธิ์และผจญภัยบนธารน้ำแข็ง","ms":"Puncak bersalji yang suci dan pengembaraan glasier."},"description":{"root":"Climb Yulong Snow Mountain, a sacred peak of the Naxi people in Lijiang. Explore Glacier Park and admire its snow-capped summits. (Discover Yulong Snow Mountain, a sacred peak revered by the Naxi people in Lijiang. Explore the Glacier Park and marvel at its snow-covered summits.)","ja":"麗江の世界遺産・玉龍雪山を訪れてみませんか。ナシ族の聖山で、氷河公園を散策し、13の雪覆い峰々や標高5,596メートルの扇子陡からの絶景をぜひお楽しみください。","ko":"리장에 위치한 나시족의 성산인 옥룡설산을 방문하세요. 빙하공원을 탐험하고 눈덮힌 산봉우리를 감상할 수 있습니다!","th":"สำรวจภูเขาหิมะหยูหลง ยอดเขาศักดิ์สิทธิ์ของชนชาตินาซีในลี่เจียง ชมสวนธารน้ำแข็งและทัศนียภาพอันตระการตาของยอดเขาที่ปกคลุมไปด้วยหิมะ","ms":"Lawati Gunung Salji Yulong, puncak suci bagi etnik Naxi di Lijiang. Eksplorasi Taman Glasier dan kagumi puncak-puncak berlapis salji."},"review":{"root":"The views from the mountain are absolutely stunning, especially when you reach the higher elevations. Be prepared for some physical exertion, but it's well worth it.","ja":"山からの眺めは絶対に素晴らしいです。特に高い場所に到達したときには言葉を失います。体力を使うことを覚悟してください。しかし、それだけの価値があります。","ko":"산에서 내려다보는 풍경은 정말 놀랍습니다, 특히 고도가 높아질수록 더욱 그렇습니다. 체력 소모를 각오해야 하지만, 그만한 가치가 있어요.","th":"วิวจากยอดเขานั้นสวยงามอย่างยิ่ง โดยเฉพาะเมื่อคุณไปถึงบริเวณที่สูงขึ้น ควรเตรียมพร้อมสำหรับการใช้แรงงานทางกาย แต่มันคุ้มค่าแน่นอน","ms":"Pemandangan dari gunung adalah luar biasa, terutama apabila anda mencapai ketinggian yang lebih tinggi. Bersiap sedia untuk beberapa usaha fizikal, tetapi ia sangat berbaloi."},"location":"27.1014838299386, 100.19730515172101","ticketPrice":60,"rating":4.5,"opensHour":[7.5,16.5],"visitDuration":8,"address":{"root":"15 km north of Lijiang Old Town, Yulong County, Lijiang City","ja":null}}]}},"relativePath":"articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain.md","filePath":"[lang]/articles/[article].md","lastUpdated":1739695371000}`),d={name:"articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain.md"},T=Object.assign(d,{setup(h){return(m,a)=>(l(),g("div",null,[e(s,{name:"Lijiang"}),a[0]||(a[0]=n("h2",{id:"from-dali-to-lijiang-experience-china-s-train",tabindex:"-1"},[t("From Dali to Lijiang: Experience China's Train "),n("a",{class:"header-anchor",href:"#from-dali-to-lijiang-experience-china-s-train","aria-label":`Permalink to "From Dali to Lijiang: Experience China's Train"`},"​")],-1)),e(i,{to:"0:00"}),a[1]||(a[1]=n("p",null,"The train journey from Dali to Lijiang is a trip full of unique experiences. Along the way, you can enjoy the magnificent views of Cangshan Mountain and Erhai Lake, as well as experience the distinctive ethnic culture of Yunnan Province. On the train, you can interact with local passengers to learn about their lifestyles and cultural traditions. Additionally, the meals provided on board offer a chance to savor Yunnan's delicious cuisine. This journey not only connects two beautiful cities but also allows you to delve deeper into China's train culture.",-1)),a[2]||(a[2]=n("h2",{id:"shuhe-ancient-town-a-serene-escape-quieter-than-lijiang-ancient-town",tabindex:"-1"},[t("Shuhe Ancient Town: A Serene Escape, Quieter than Lijiang Ancient Town "),n("a",{class:"header-anchor",href:"#shuhe-ancient-town-a-serene-escape-quieter-than-lijiang-ancient-town","aria-label":'Permalink to "Shuhe Ancient Town: A Serene Escape, Quieter than Lijiang Ancient Town"'},"​")],-1)),e(i,{to:"4:00"}),a[3]||(a[3]=n("p",null,"Located to the north of Lijiang, Shuhe Ancient Town is a hidden gem along the ancient Tea Horse Road. Unlike the bustling and commercialized Lijiang Ancient Town, Shuhe offers a quieter and more authentic experience. Here, you'll find fewer crowds and a well-preserved glimpse into traditional Naxi culture. Strolling along the cobblestone streets, you can soak in the tranquility, admire ancient architecture, and enjoy the clear streams that wind through the town. If you're looking for peace away from the chaos, Shuhe Ancient Town is the perfect destination.",-1)),e(o,{name:"Shuhe Ancient Town"}),a[4]||(a[4]=n("h2",{id:"zhongyi-market-in-lijiang-ancient-town-experience-authentic-naxi-culture",tabindex:"-1"},[t("Zhongyi Market in Lijiang Ancient Town: Experience Authentic Naxi Culture "),n("a",{class:"header-anchor",href:"#zhongyi-market-in-lijiang-ancient-town-experience-authentic-naxi-culture","aria-label":'Permalink to "Zhongyi Market in Lijiang Ancient Town: Experience Authentic Naxi Culture"'},"​")],-1)),e(i,{to:"6:53"}),a[5]||(a[5]=n("p",null,"Zhongyi Market is one of the most distinctive places in Lijiang Ancient Town, offering a rich array of local specialties, handicrafts, and traditional foods. From fresh farm produce and exquisite silver jewelry to unique Naxi ethnic clothing and crafts, the market provides an immersive cultural experience. Here, you can interact with local vendors, taste authentic street food, and enjoy traditional folk performances. Zhongyi Market is not just a great place for shopping but also an ideal spot to delve deeper into Lijiang's culture and way of life.",-1)),e(o,{name:"Lijiang Ancient Town"}),a[6]||(a[6]=n("h2",{id:"tiger-leaping-gorge-hiking-through-the-world-s-deepest-canyon-with-views-of-jade-dragon-snow-mountain",tabindex:"-1"},[t("Tiger Leaping Gorge: Hiking through the World's Deepest Canyon with Views of Jade Dragon Snow Mountain "),n("a",{class:"header-anchor",href:"#tiger-leaping-gorge-hiking-through-the-world-s-deepest-canyon-with-views-of-jade-dragon-snow-mountain","aria-label":`Permalink to "Tiger Leaping Gorge: Hiking through the World's Deepest Canyon with Views of Jade Dragon Snow Mountain"`},"​")],-1)),e(i,{to:"13:11"}),a[7]||(a[7]=n("p",null,"Tiger Leaping Gorge, located between Lijiang and Shangri-La in Yunnan, is renowned as one of the world's deepest canyons. Its breathtaking natural beauty and challenging hiking trails draw adventurers from all over the globe. As you trek along the paths of Tiger Leaping Gorge, you'll experience the roaring Jinsha River up close and enjoy stunning views of the majestic Jade Dragon Snow Mountain in the distance. Whether you're a nature enthusiast or a thrill-seeking hiker, this journey through Tiger Leaping Gorge is unforgettable.",-1)),e(u,{name:"tigerYulong",alt:"Gazing at Jade Dragon Snow Mountain from Tiger Leaping Gorge"}),e(o,{name:"Tiger Leaping Gorge"}),a[8]||(a[8]=n("h2",{id:"jade-dragon-snow-mountain-exploring-the-blue-moon-valley-revering-the-highest-peak-and-experiencing-the-grand-impression-lijiang-show",tabindex:"-1"},[t('Jade Dragon Snow Mountain: Exploring the Blue Moon Valley, Revering the Highest Peak, and Experiencing the Grand "Impression Lijiang" Show '),n("a",{class:"header-anchor",href:"#jade-dragon-snow-mountain-exploring-the-blue-moon-valley-revering-the-highest-peak-and-experiencing-the-grand-impression-lijiang-show","aria-label":'Permalink to "Jade Dragon Snow Mountain: Exploring the Blue Moon Valley, Revering the Highest Peak, and Experiencing the Grand "Impression Lijiang" Show"'},"​")],-1)),e(i,{to:"29:23"}),a[9]||(a[9]=n("p",null,`Jade Dragon Snow Mountain is a sacred peak for the Naxi people, renowned for its spectacular glaciers and diverse natural landscapes. Among its highlights, the Blue Moon Valley captivates visitors with its crystal-clear lakes and mesmerizing blue hues. Climbing to the highest peak of Jade Dragon Snow Mountain, Fansizuo, offers breathtaking panoramic views of the surrounding mountains and a profound sense of nature's grandeur. Additionally, "Impression Lijiang," one of China's largest outdoor performances, brings to life the history and culture of the Naxi people through stunning stage effects and vibrant performances. Whether you're exploring its natural beauty or immersing yourself in its cultural heritage, Jade Dragon Snow Mountain promises an unforgettable journey.`,-1)),e(o,{name:"Yulong Snow Mountain"}),a[10]||(a[10]=n("h2",{id:"yunnan-wild-mushroom-hot-pot-savoring-nature-s-bounty",tabindex:"-1"},[t("Yunnan Wild Mushroom Hot Pot: Savoring Nature's Bounty "),n("a",{class:"header-anchor",href:"#yunnan-wild-mushroom-hot-pot-savoring-nature-s-bounty","aria-label":`Permalink to "Yunnan Wild Mushroom Hot Pot: Savoring Nature's Bounty"`},"​")],-1)),e(i,{to:"42:19"}),a[11]||(a[11]=n("p",null,"Yunnan is renowned for its abundant wild mushroom resources, and the wild mushroom hot pot is one of the best ways to experience this unique flavor. Freshly picked varieties of wild mushrooms, such as matsutake, porcini, and chicken fungus, are cooked in either a clear broth or spicy Sichuan-style base. This allows you to savor the natural umami flavors of the mushrooms while enjoying the rich local cuisine. Not only is it delicious, but it’s also highly nutritious, making it an excellent choice for healthy eating. Whether enjoyed with family or friends, Yunnan wild mushroom hot pot offers an unforgettable culinary experience.",-1)),e(r,{link:"yunnan-specialty-cuisine"})]))}});export{v as __pageData,T as default};
