import{_ as t,c as o,b as a,ai as r}from"./chunks/framework.Dm3xLcfC.js";const u=JSON.parse('{"title":"プライバシーポリシー","description":"クッキーはあなたの好みを記憶し、ウェブサイトでの体験をパーソナライズします。当社は直接クッキーを使用せず、広告や分析サービスで第三者がクッキーを使用しています。","frontmatter":{"prev":false,"next":false,"sidebar":true,"title":"プライバシーポリシー","description":"クッキーはあなたの好みを記憶し、ウェブサイトでの体験をパーソナライズします。当社は直接クッキーを使用せず、広告や分析サービスで第三者がクッキーを使用しています。","ld+json":[{"@type":"BreadcrumbList","itemListElement":[{"@type":"ListItem","position":1,"name":"プライバシーポリシー","item":"https://www.etourbook.com/ja/privacy-policy"}]}],"head":[["link",{"rel":"alternate","href":"https://www.etourbook.com/about/privacy-policy","hreflang":"x-default"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/about/privacy-policy","hreflang":"en"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ja/about/privacy-policy","hreflang":"ja"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ko/about/privacy-policy","hreflang":"ko"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/th/about/privacy-policy","hreflang":"th"}],["link",{"rel":"canonical","href":"https://www.etourbook.com/ja/about/privacy-policy"}],["meta",{"name":"title","content":"プライバシーポリシー"}],["script",{"type":"application/ld+json"},"[{\\"@context\\":\\"https://schema.org/\\",\\"@type\\":\\"BreadcrumbList\\",\\"itemListElement\\":[{\\"@type\\":\\"ListItem\\",\\"position\\":1,\\"name\\":\\"プライバシーポリシー\\",\\"item\\":\\"https://www.etourbook.com/ja/privacy-policy\\"}]}]"]]},"headers":[],"relativePath":"ja/about/privacy-policy.md","filePath":"ja/about/privacy-policy.md","lastUpdated":1744376818000}'),l={name:"ja/about/privacy-policy.md"};function i(n,e,c,p,s,h){return a(),o("div",null,e[0]||(e[0]=[r('<h1 id="プライバシーポリシー" tabindex="-1">プライバシーポリシー <a class="header-anchor" href="#プライバシーポリシー" aria-label="Permalink to &quot;プライバシーポリシー&quot;">​</a></h1><h2 id="クッキー" tabindex="-1">クッキー <a class="header-anchor" href="#クッキー" aria-label="Permalink to &quot;クッキー&quot;">​</a></h2><p>クッキーとは、あなたのコンピュータのハードドライブ上に配置される小さなファイルです。このクッキーにより、あなたの好みに関する情報を記憶し、当社のウェブサイトでの体験をパーソナライズすることができます。</p><p>現在、当社は直接クッキーを使用しておりませんが、広告や分析サービスとして使用している第三者のサービスではクッキーが使用されています。</p><h2 id="第三者が提供する広告および分析サービス" tabindex="-1">第三者が提供する広告および分析サービス <a class="header-anchor" href="#第三者が提供する広告および分析サービス" aria-label="Permalink to &quot;第三者が提供する広告および分析サービス&quot;">​</a></h2><p>広告配信とウェブトラフィック分析のために、以下のサービスを利用しています：</p><ul><li><strong>Google Analytics</strong>: Google Analyticsのディスプレイ広告機能を使用して、サイト訪問者の人口統計データを収集し、訪問者やサイトトラフィックをより深く理解するために利用することがあります。ユーザーは、Googleアカウントの<a href="http://www.google.com/ads/preferences/" target="_blank" rel="noreferrer">広告設定</a>からGoogle Analyticsのディスプレイ広告機能の利用をオプトアウトすることができます。</li><li><strong>Google Ads</strong>: 当社のウェブサイト上で広告を表示するためにGoogleを利用する場合があります。Googleはユーザーが特定のページを閲覧したログを記録し、これによりユーザーの興味に合った商品やサービスに関する広告を、当社のサイトだけでなく他のサイトでも表示することができます。Googleは、興味ベースの広告を配信するためにDoubleClickクッキーを使用しています。ユーザーは、Googleアカウントの<a href="http://www.google.com/ads/preferences/" target="_blank" rel="noreferrer">広告設定</a>からDoubleClickクッキーの使用をオプトアウトすることができます。</li></ul>',7)]))}const d=t(l,[["render",i]]);export{u as __pageData,d as default};
