import{a as N}from"./chunks/assets.CyscBh63.js";import{i as X,f as Z,e as aa,g as ea}from"./chunks/theme.Stt0B6jc.js";import{g as F,a as na,r as ia,I as S}from"./chunks/refresh.Dlf6d8Fw.js";import{d as J}from"./chunks/constants.D8OrJcu7.js";import{C as L}from"./chunks/Container.B2oaiiMR.js";import{d as ta,s as oa,r as R,l as sa,o as ra,p as T,c as m,b as u,h as a,k as t,m as z,f as q,t as r,g as e,w as i,j as h,F as f,z as Y,i as P,X as la,_ as ua}from"./chunks/framework.Dm3xLcfC.js";const da={class:"container"},ha={class:"content"},ga={class:"label"},ma={class:"title"},ca={class:"description"},pa={class:"caption"},ka={class:"caption_text"},ba=["href"],ya={key:2},fa=["href"],va={key:5,class:"copyright"},Ca=["src"],ja=["poster"],wa=["data-src"],_a=["data-src"],xa={class:"wrapper"},qa={class:"category"},Sa={class:"label"},Ja={class:"action"},La={key:0,class:"article-list"},Pa={class:"article-list-left"},Ga={class:"article-top-wrapper"},Ta={class:"category"},za={class:"article-label"},Ma={class:"article-title"},Aa={class:"article-description"},Ha={class:"article-list-right"},Ia={class:"article-detail-wrapper"},Da={class:"category"},Ba={class:"article-label"},Ka={class:"article-title"},Va={class:"article-description-short"},Wa={class:"wrapper"},Na={class:"category"},Fa={class:"label"},Ra={class:"action"},Ya={class:"item-list"},Ea={class:"destination-name"},Oa={class:"wrapper"},Ua={class:"category"},$a={class:"label"},Qa={class:"item-list item-5"},Xa={class:"attraction-name"},Za={class:"wrapper"},ae={class:"category"},ee={class:"label"},ne={class:"action"},ie={class:"guide-list"},te={class:"article-detail-wrapper"},oe={class:"article-label"},se={class:"article-title"},ke=JSON.parse(`{"titleTemplate":":title: 中国週末旅行ガイド – ローカルおすすめスポット","description":"週末で行く中国旅✨厳選スポット・地元食・穴場情報｜短期滞在者向け「失敗しない」完全ガイド。現地リサーチャー直伝の時短プランで、2泊3日を120%楽しむコツ。","frontmatter":{"layout":"home","head":[["link",{"rel":"alternate","href":"https://www.etourbook.com/","hreflang":"x-default"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/","hreflang":"en"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ja/","hreflang":"ja"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ko/","hreflang":"ko"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/th/","hreflang":"th"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ms/","hreflang":"ms"}],["link",{"rel":"canonical","href":"https://www.etourbook.com/ja/"}]],"titleTemplate":":title: 中国週末旅行ガイド – ローカルおすすめスポット","description":"週末で行く中国旅✨厳選スポット・地元食・穴場情報｜短期滞在者向け「失敗しない」完全ガイド。現地リサーチャー直伝の時短プランで、2泊3日を120%楽しむコツ。","hero":{"title":"中国旅行ローカルガイド"},"topAttractions":[{"name":"Hongya Cave","city":"Chongqing","text":"洪崖洞","link":"hongya-cave"},{"name":"Yulong Snow Mountain","city":"Lijiang","text":"玉龙雪山","link":"yulong-snow-moutain"},{"name":"Chengdu Panda Research Base","city":"Chengdu","text":"成都パンダ研究基地","link":"chengdu-panda-research-base"},{"name":"Mutianyu Great Wall","city":"Beijing","text":"慕田峪長城","link":"mutianyu-great-wall"},{"name":"The Forbidden City","city":"Beijing","text":"紫禁城","link":"the-forbidden-city"}],"topDestinations":[{"name":"Beijing","text":"北京","link":"/beijing"},{"name":"Chongqing","text":"重慶","link":"/chongqing"},{"name":"Hangzhou","text":"杭州","link":"/hangzhou"},{"name":"Lijiang","text":"麗江","link":"/lijiang"}],"topArticles":[{"link":"japanese-friends-3-night-5-day-weekend-in-chongqing","category":["Chinese City Walk","Chinese Cultural Heritage","China Vlog"],"title":{"root":"Chongqing Travel: 3-Night, 5-Day Weekend Adventure with Japanese Friends","ja":"重慶旅行・3泊5日の週末体験 | 一騎と友人たちの探検記","ko":"중경 여행 · 주말 3박 5일 일본 친구들과의 모험","th":"การเดินทางที่ฉงชิ่ง: การผจญภัย 3 คืน 5 วันกับเพื่อนชาวญี่ปุ่น","ms":"Perjalanan Chongqing: Pengembaraan 3 Malam, 5 Hari Akhir Minggu dengan Rakan Jepun"},"videoId":"EKq1dThQRQ0","introduce":{"root":"Over the weekend, a group of Japanese friends embarked on a 3-night, 5-day trip to Chongqing. They explored the city's modern architecture and historical temples, indulged in local cuisine and culture, and learned about its rich history. As one of China's four municipalities, Chongqing boasts a population of over 30 million, making it one of the largest cities in the world. The video highlights Chongqing's historical significance, including its role as the wartime capital during World War II and the bombings by the Japanese army. The trip concluded with a delightful taste of local delicacies and breathtaking views of Chongqing's night skyline.","ja":"週末の3泊5日で、日本の友人たちは重慶を訪れました。彼らは重慶の現代的な建築や歴史的な寺院を巡り、地元の料理や文化を満喫しました。また、中国の四大直轄市である重慶の人口3000万人を超える大都市の規模や、第二次世界大戦中に国民党政府の臨時首都となり、日本軍による爆撃を受けた歴史についても触れています。最後には、地元の美食を堪能し、重慶の美しい夜景を楽しみました。","ko":"일본인 친구들 그룹이 주말을 이용해 3박 5일간 중경으로 여행을 떠났습니다. 그들은 중경의 현대적인 건축물과 역사적인 사찰을 탐방하고, 현지 음식과 문화를 체험했습니다. 또한 중경의 풍부한 역사를 배웠는데, 중국의 4대 직할시 중 하나로 인구 3천만 명 이상을 자랑하는 세계에서 가장 큰 도시 중 하나입니다. 영상에서는 중경이 제2차 세계 대전 당시 국민정부의 임시 수도였으며 일본군에 의해 폭격당했던 역사적 배경도 다루었습니다. 여행의 마지막에는 그들은 현지 특산물을 맛보고 중경의 환상적인 야경을 즐겼습니다.","th":"ในช่วงสุดสัปดาห์กลุ่มเพื่อนชาวญี่ปุ่นได้เริ่มต้นการเดินทาง 3 คืน 5 วันไปยังฉงชิ่ง พวกเขาสำรวจสถาปัตยกรรมสมัยใหม่และวัดเก่าแก่ของเมือง ได้ลิ้มลองอาหารและวัฒนธรรมท้องถิ่น และเรียนรู้เกี่ยวกับประวัติศาสตร์อันยาวนานของฉงชิ่ง เมืองนี้เป็นหนึ่งในสี่นครปกครองส่วนกลางของจีน มีประชากรมากกว่า 30 ล้านคน ทำให้เป็นเมืองที่ใหญ่ที่สุดแห่งหนึ่งของโลก วิดีโอนำเสนอความสำคัญทางประวัติศาสตร์ของฉงชิ่งรวมถึงบทบาทในฐานะเมืองหลวงชั่วคราวในช่วงสงครามโลกครั้งที่สองและการถูกโจมตีด้วยระเบิดจากกองกำลังทหารญี่ปุ่น การเดินทางจบลงอย่างสวยงามด้วยการลิ้มรสอาหารท้องถิ่นและชมทิวทัศน์ยามค่ำคืนที่น่าตื่นตาตื่นใจของฉงชิ่ง","ms":"Pada hujung minggu, sekumpulan rakan-rakan dari Jepun memulakan perjalanan 3 malam 5 hari ke Chongqing. Mereka meneroka seni bina moden dan kuil bersejarah bandar, menikmati masakan dan budaya tempatan, serta belajar tentang sejarah kaya Chongqing. Sebagai salah satu daripada empat daerah pentadbiran pusat di China, Chongqing memiliki populasi lebih daripada 30 juta penduduk, menjadikannya salah satu bandar terbesar di dunia. Video ini menunjukkan kepentingan sejarah Chongqing termasuk perananya sebagai ibu kota semasa Perang Dunia Kedua dan pengeboman oleh tentera Jepun. Perjalanan itu berakhir dengan mencicipi hidangan tempatan yang lazat dan pemandangan cakrawala malam Chongqing yang menakjubkan."},"description":{"root":"Join a group of Japanese friends on their 3-night, 5-day weekend getaway to Chongqing. Explore modern architecture, historic temples.","ja":"日本の友人と重慶へ週末旅行へ出かけませんか？現代的な建築と歴史的な寺院を巡り、地元の美食や文化を満喫。重慶が世界最大級の都市と称される所以をぜひ体感してください！","ko":"일본인 친구들이 주말에 떠난 3박 5일 중경 여행에 동행하세요. 현대적인 건축물과 역사적인 사찰을 탐방하고, 지역 음식과 문화를 체험하며 중경의 풍부한 역사를 알아보세요. 제2차 세계 대전과 관련된 중경의 과거와 현재를 함께 만나보세요.","th":"เข้าร่วมกับกลุ่มเพื่อนชาวญี่ปุ่นในการเดินทางพักผ่อน 3 คืน 5 วันที่ฉงชิ่ง สำรวจสถาปัตยกรรมสมัยใหม่ วัดเก่าแก่ และสัมผัสประสบการณ์วัฒนธรรมท้องถิ่นที่หลากหลาย","ms":"Sertai sekumpulan rakan-rakan dari Jepun dalam pengembaraan akhir minggu 3 malam 5 hari mereka ke Chongqing. Meneroka seni bina moden, kuil bersejarah, dan alami budaya serta masakan tempatan yang unik."},"city":["Chongqing"],"attraction":["Luohan Temple","Hongya Cave"],"preferLang":"ja","index":90,"interestLabel":"中国シティウォーク"},{"link":"joels-chengdu-adventure","category":["Pandas and Other Animals","China Vlog"],"title":{"root":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders","ja":"ジョエルの成都旅：パンダ、火鍋、文化の魅力","ko":"조엘의 청두 모험: 판다, 핫팟, 그리고 문화의 경이","th":"การผจญภัยของโจเอลในเฉิงตู: แพนด้า ฮอทพอท และความมหัศจรรย์ทางวัฒนธรรม","ms":"Petualangan Joel di Chengdu: Panda, Tumis Panas, dan Keajaiban Budaya"},"videoId":"UqmPUsnWg14","introduce":{"root":"British traveler Joel Friend revisits Chengdu, China, for another adventure. He visits the Panda Valley to observe giant pandas and learns about their conservation status and reintroduction program. Joel also checks out the red pandas and explores scenic spots in downtown Chengdu, enjoying traditional Chinese ethnic music. Next, he heads to the Jinsha Site Museum to delve into the ancient culture and history of the Shu people. Finally, Joel experiences Chengdu's daily life at Jinli Street, where he tries sugar painting, ear cleaning, and even robot-made coffee. Despite his previous unsuccessful attempt, Joel successfully enjoys Chengdu hotpot this time, sampling various dishes such as duck tongues and pig brains. To wrap up his trip, he visits a shopping mall and spots giant panda statues on the rooftops. Chengdu's relaxed lifestyle and panda culture leave a lasting impression on Joel.","ja":"イギリス人トラベラー、ジョエル・フレンドが再び中国成都を訪れる旅行記録です。彼は成都のパンダバレーでジャイアントパンダを観賞し、その生存状況や再導入計画について学びます。また、レッサーパンダも見学し、成都市内の観光地を訪れ、中国の民族音楽を楽しみました。その後、彼は金沙遺跡博物館に向かい、古代蜀人の文化と歴史について深く探ります。最後に、彼は錦里街で成都の日常生活を体験します。糖絵（とうが）や耳掃除、さらにはロボットによるコーヒー作りも試しました。前回うまくいかなかった成都の火鍋も、今回は成功し、鴨の舌や豚の脳みそなどさまざまな料理を堪能しました。さらに、彼はショッピングモールを訪れ、屋上にあるパンダの像を見ました。成都のゆったりとしたライフスタイルとパンダ文化がジョエルに深い印象を残しました。","ko":"영국 여행가 조엘 프렌드가 다시 중국 청두를 방문하여 또 다른 모험을 즐깁니다. 그는 판다 계곡을 방문하여 자이언트 판다를 관찰하고 그들의 보존 상태와 재도입 프로그램에 대해 배웁니다. 또한 레서 판다도 확인하고 청두 시내의 명소를 탐험하며 전통적인 중국 민족 음악을 즐깁니다. 다음으로 그는 진사 유적 박물관을 방문하여 고대 쓰 사람들의 문화와 역사를 깊이 탐구합니다. 마지막으로 조엘은 진리 거리에서 청두의 일상 생활을 경험하며 설탕 그림, 귀 청소, 심지어 로봇이 만든 커피도 시도합니다. 이전에 실패했던 청두 핫팟도 이번에는 성공적으로 즐기며 오리 혀, 돼지 뇌 등 다양한 요리를 맛봅니다. 여행을 마무리하면서 그는 쇼핑몰을 방문하고 지붕 위에 있는 거대한 판다 동상을 발견합니다. 청두의 여유로운 라이프스타일과 판다 문화는 조엘에게 깊은 인상을 남깁니다.","th":"โจเอล นักเดินทางชาวอังกฤษ เยี่ยมชมเฉิงตู ประเทศจีน อีกครั้งเพื่อผจญภัยใหม่ เขาไปเยี่ยมชมหุบเขาแพนด้าเพื่อสังเกตการณ์แพนด้าขนาดใหญ่และเรียนรู้เกี่ยวกับสถานะการอนุรักษ์และการฟื้นฟูประชากรแพนด้า นอกจากนี้เขายังสำรวจแพนด้าแดงและสำรวจสถานที่ท่องเที่ยวในเมืองเฉิงตู พร้อมทั้งเพลิดเพลินกับดนตรีพื้นเมืองจีน จากนั้นเขาไปเยี่ยมชมพิพิธภัณฑ์ไซต์จินซา เพื่อสำรวจวัฒนธรรมและประวัติศาสตร์โบราณของชนชาติซู ในที่สุดโจเอลได้สัมผัสชีวิตประจำวันในเฉิงตูที่ถนนจินหลี่ โดยลองทำภาพวาดน้ำตาล การทำความสะอาดหู และกาแฟที่ทำโดยหุ่นยนต์ แม้ว่าจะเคยไม่สำเร็จในการลองฮอทพอทมาก่อน แต่ครั้งนี้โจเอลสามารถเพลิดเพลินกับฮอทพอทเฉิงตูได้สำเร็จ ลองชิมเมนูต่างๆ เช่น ลิ้นเป็ดและสมองหมู เพื่อปิดทริป เขาได้เยี่ยมชมศูนย์การค้าและพบกับรูปปั้นแพนด้าขนาดใหญ่บนหลังคา ไลฟ์สไตล์ที่ผ่อนคลายและวัฒนธรรมแพนด้าของเฉิงตูได้สร้างความประทับใจให้แก่โจเอล","ms":"Pelancong British Joel Friend kembali mengunjungi Chengdu, China untuk satu lagi petualangan. Dia melawat Lembah Panda untuk memerhati panda raksasa dan belajar tentang status pemuliharaan mereka serta program pengenalan semula. Joel juga melihat panda merah dan meneroka tempat-tempat menarik di pusat bandar Chengdu, menikmati muzik etnik tradisional Cina. Seterusnya, dia menuju ke Muzium Tapak Jinsha untuk menyelidiki budaya dan sejarah kuno orang Shu. Akhirnya, Joel mengalami kehidupan harian di Chengdu di Jalan Jinli, di mana dia mencuba lukisan gula, pembersihan telinga, dan bahkan kopi yang dibuat oleh robot. Walaupun cubaan sebelumnya gagal, Joel berjaya menikmati tumis panas Chengdu kali ini, mencuba pelbagai hidangan seperti lidah itik dan otak babi. Untuk menamatkan perjalanannya, dia melawat pusat membeli-belah dan melihat patung panda raksasa di atas bumbung. Gaya hidup santai dan budaya panda Chengdu meninggalkan kesan mendalam kepada Joel."},"description":{"root":"Experience Joel's unforgettable journey in Chengdu, exploring panda reserves and ancient culture. Discover top attractions, delicious hotpot.","ja":"ジョエルが成都で体験する忘れられない旅。パンダ保護区の探索、魅力あふれる成都の観光スポットや美味しい火鍋、独自の伝統をぜひご自分の旅でも体験してください！","ko":"청두에서 조엘이 경험하는 잊지 못할 여행. 판다 보호구역 탐험, 매력적인 청두의 관광 명소와 맛있는 핫팟, 독특한 전통을 당신의 여행에서도 경험해보세요!","th":"สำรวจการเดินทางที่น่าจดจำของโจเอลในเฉิงตู สำรวจเขตสงวนแพนด้าและวัฒนธรรมโบราณ ค้นหาสถานที่ท่องเที่ยวยอดนิยมและฮอทพอทแสนอร่อย","ms":"Alami perjalanan tidak terlupakan Joel di Chengdu, meneroka tapak simpanan panda dan budaya kuno. Terokai tarikan utama, tumis panas lazat."},"city":["Chengdu"],"attraction":["Chengdu Panda Research Base"],"index":100,"interestLabel":"パンダを含む他の動物たち"},{"link":"chongqing-hot-pot","category":["Chinese Cuisine Culture"],"title":{"root":"Local Flavor & Must-Tries: Chongqing Hot Pot","ja":"地元の味と絶対に試すべきもの：重慶ホットポト","ko":"현지 맛과 꼭 먹어야 할 것들: 충칭 핫팟","th":"รสชาติท้องถิ่นและเมนูที่ต้องลอง: หม้อไฟฉงชิ่ง","ms":"Perisa Tempatan & Mesti Cuba: Rebusan Panas Chongqing"},"introduce":{"root":"Dive into the sizzling world of Chongqing hotpot, a culinary adventure blending flavor and culture. Born on the Yangtze River docks, this dish is now a social staple enjoyed year-round. Choose from mild clear soup to fiery red broth, and savor traditional ingredients like beef tripe and duck intestines. Navigate spice levels with dipping sauces and cooling desserts. Whether a first-timer or spice enthusiast, this guide equips you to eat like a local and embrace the vibrant Chongqing hotpot experience. Ignite your taste buds today!","ja":"重慶ホットポットは長江の港町発祥の料理で、味覚と文化を楽しむことができます。このガイドでは、辛さの秘密やマイルドなスープから本場の赤いスープまで、スープの選び方を紹介。牛肉のミノや鴨の腸などの伝統的食材や、ディップソース、デザートでの辛さ調節法も解説します。初心者から辛い料理好きまで、ローカル流の楽しみ方がわかります。味覚を刺激し、重慶の食文化を体験しましょう！","ko":"충칭 핫팟의 세계로 빠져보세요, 이는 맛과 문화가 결합된 요리 모험입니다. 양쯔강 항구에서 시작된 이 요리는 이제 연중 즐기는 사회적 스테이플입니다. 순한 클리어 수프부터 불타는 붉은 국물까지 다양한 선택지와 전통적인 재료인 소 허파와 오리 내장 등을 즐길 수 있습니다. 디핑 소스와 시원한 디저트로 매운 정도를 조절하세요. 초보자든 매운 음식 애호가든 이 가이드는 당신이 로컬처럼 먹고 활기찬 충칭 핫팟 경험을 즐길 수 있도록 도와줍니다.","th":"ดำดิ่งสู่โลกของหม้อไฟฉงชิ่ง ซึ่งเป็นการผจญภัยทางการทำอาหารที่ผสมผสานระหว่างรสชาติและวัฒนธรรม เริ่มต้นที่ท่าเรือแม่น้ำแยงซีเกียง ปัจจุบันนี้เป็นอาหารประจำที่ชาวเมืองรับประทานตลอดทั้งปี เลือกจากน้ำซุปใสไปจนถึงน้ำซุปแดงเผ็ดร้อน และเพลิดเพลินกับส่วนประกอบแบบดั้งเดิม เช่น กระเพาะวัวและลำไส้เป็ด ปรับระดับความเผ็ดด้วยซอสดิ๊พและของหวานเย็น ๆ ไม่ว่าคุณจะเป็นมือใหม่หรือคนชอบกินเผ็ด คู่มือนี้จะทำให้คุณสามารถรับประทานเหมือนคนท้องถิ่นและสนุกกับประสบการณ์หม้อไฟฉงชิ่งที่มีชีวิตชีวา จุดประกายรสชาติของคุณวันนี้!","ms":"Menyelam ke dalam dunia rebusan panas Chongqing, sebuah petualangan masakan yang menggabungkan perisa dan budaya. Bermula dari pelabuhan Sungai Yangtze, hidangan ini kini menjadi makanan sosial yang dinikmati sepanjang tahun. Pilih dari sup jernih ringan hingga kuah merah yang pedas, dan nikmati bahan-bahan tradisional seperti tripus lembu dan usus itik. Navigasikan tahap rasa pedas dengan sos celup dan pencuci mulut penyejuk. Sama ada pemula atau peminat rasa pedas, panduan ini akan melengkapkan anda untuk makan seperti penduduk tempatan dan menerima pengalaman rebusan panas Chongqing yang bersemangat. Hidupkan lidah anda hari ini!"},"description":{"root":"Discover Chongqing hot pot's history, flavors, and social dining experience. Must-try dishes, varieties, and dipping sauces. Perfect for gatherings.","ja":"重慶火鍋の独特な風味と試すべき美食を探索しましょう。重慶火鍋の歴史、種類、つけダレの選び方、そしてこの社交的な食事体験をどのように楽しむかについて学びましょう。","ko":"충칭 핫팟의 역사, 맛, 그리고 사회적 식사 경험을 탐험하세요. 꼭 시도해야 할 요리, 종류, 그리고 디핑 소스. 모임에 완벽합니다.","th":"สำรวจประวัติศาสตร์ รสชาติ และประสบการณ์การรับประทานอาหารทางสังคมของหม้อไฟฉงชิ่ง เมนูที่ต้องลอง หลากหลายประเภท และน้ำจิ้มที่ควรลอง เป็นที่เหมาะสำหรับการรวมกลุ่ม","ms":"Meneroka sejarah, perisa, dan pengalaman makan bersosial rebusan panas Chongqing. Hidangan wajib cuba, pelbagai jenis, dan sos celup. Sempurna untuk berkumpul."},"city":["Chongqing"],"attraction":[],"index":80,"interestLabel":"中国の食文化"},{"link":"korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","category":["Natural Scenery","Chinese Cultural Heritage","China Vlog"],"title":{"root":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","ja":"韓国教育長の退職旅：虎跳峽と玉龍雪山","ko":"한국 교육감의 퇴역 여행: 호점협과 용설산","th":"การเดินทางของอดีตผู้อำนวยการศึกษาเกาหลีสู่ช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกร","ms":"Perjalanan Ketua Pendidikan Berundur dari Korea ke Jurang Harimau Melompat dan Gunung Salji Naga Giok"},"videoId":"L8aGEwBb4uM","introduce":{"root":"After retiring as the education commissioner of South Korea, he traveled to Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan, China. During his journey, he experienced China's train culture, tasted local cuisine, visited traditional markets, and found comfortable accommodation in Lijiang. He also shared the challenges and breathtaking views of hiking up Jade Dragon Snow Mountain, along with a small tip about using sunscreen sticks. In Lachun Village, he enjoyed Korean dishes and admired the magnificent natural scenery in places like Walnut Leaf and Haoyilu. He visited Jade Dragon Snow Mountain, experienced altitude sickness, and successfully reached the summit. Throughout the trip, he immersed himself in the local culture and cuisine, forming deep friendships with the locals.","ja":"韓国教育長官が退職後、中国雲南省の虎跳峽（こびょうか）と玉龍雪山（ぎょくりゅうせつざん）へ旅行しました。旅の中で彼は中国の鉄道文化を体験し、地元の料理を味わい、伝統市場を見学し、麗江（りきょう）で快適な宿泊先を見つけました。また、玉龍雪山へのハイキングの困難さや美しい景色について語り、日焼け止めスティックを使った小さなコツも共有しました。ラ村では韓国料理を食べ、胡桃葉（ごうだよう）や好意路（こうぎろ）などで雄大な自然を楽しみました。彼は玉龍雪山を訪れ、高山病を経験しながらも無事に頂上に到達しました。旅を通じて、彼は現地の文化や料理を体験し、現地の人々との深い友情を築きました。","ko":"한국 교육감이 퇴직 후 중국 윈난성의 호점협과 용설산을 여행했습니다. 그는 여행 중에 중국 기차 문화를 체험하고 현지 음식을 맛보며 전통 시장을 방문했고, 리장에서 편안한 숙소를 찾았습니다. 또한 용설산 트레킹의 어려움과 아름다운 경치에 대해 이야기하며 자외선 차단제 스틱 사용법이라는 작은 팁도 공유했습니다. 라촌에서는 한국 요리를 먹었고, 호두엽과 해의로 같은 곳에서 웅대한 자연 풍경을 감상했습니다. 그는 용설산을 방문해 고산병을 경험했지만 결국 정상에 성공적으로 도달했습니다. 여행 동안 그는 현지 문화와 음식을 체험하고 주민들과 깊은 우정을 쌓았습니다.","th":"หลังจากเกษียณจากตำแหน่งผู้อำนวยการศึกษาของเกาหลีใต้ เขาได้เดินทางไปยังช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในมณฑลยูนนาน ประเทศจีน ในระหว่างการเดินทาง เขาได้สัมผัสวัฒนธรรมรถไฟของจีน ชิมอาหารท้องถิ่น เยี่ยมชมตลาดแบบดั้งเดิม และพบที่พักสบายๆ ในเมืองลี่เจียง เขายังแบ่งปันความท้าทายและความสวยงามของการเดินป่าขึ้นภูเขาน้ำแข็งหยกมังกร พร้อมคำแนะนำเล็กๆ น้อยๆ เกี่ยวกับการใช้แท่งครีมกันแดด ในหมู่บ้านลาชุน เขาได้เพลิดเพลินกับอาหารเกาหลีและชื่นชมทิวทัศน์ธรรมชาติอันงดงามที่สถานที่อย่างใบมะรุมและถนนเฮาอี้ลู่ เขาได้เยี่ยมชมภูเขาน้ำแข็งหยกมังกร ประสบกับอาการแพ้ความสูง และประสบความสำเร็จในการขึ้นถึงยอดเขา ตลอดการเดินทาง เขาได้สัมผัสกับวัฒนธรรมและอาหารท้องถิ่น และสร้างมิตรภาพที่ลึกซึ้งกับชาวบ้าน","ms":"Selepas bersara sebagai Pengarah Pendidikan Korea Selatan, beliau telah melancong ke Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan, China. Semasa perjalanannya, beliau mengalami budaya kereta api China, merasai masakan tempatan, melawat pasar tradisional, dan menemui penginapan yang selesa di Lijiang. Beliau juga berkongsi cabaran dan pemandangan menakjubkan semasa mendaki Gunung Salji Naga Giok, bersama dengan sedikit petua tentang menggunakan batang pelindung matahari. Di Kampung Lachun, beliau menikmati hidangan Korea dan memuji pemandangan alam semula jadi yang megah di tempat-tempat seperti Daun Kenari dan Jalan HaoYiLu. Beliau telah mengunjungi Gunung Salji Naga Giok, mengalami penyakit ketinggian, dan berjaya mencapai puncak gunung tersebut. Sepanjang perjalanan, beliau terbenam dalam budaya dan masakan tempatan, membina persahabatan yang mendalam dengan penduduk tempatan."},"description":{"root":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","ja":"中国雲南省の虎跳峽や玉龍雪山を巡る壮大な旅に参加しましょう。韓国教育長官も退職後楽しんだ冒険を通じ、地元の鉄道文化、美食、絶景を体験。ハイキングのコツもお届けします！","ko":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","th":"เข้าร่วมการผจญภัยที่น่าตื่นตาตื่นใจผ่านช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในยูนนาน สำรวจทิวทัศน์ที่งดงามและสัมผัสวัฒนธรรมท้องถิ่น อาหารอร่อย และเทคนิคการเดินป่า","ms":"Sertai Sugilway dalam pengembaraan menakjubkan melalui Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan. Alami landskap yang menakjubkan dan sertai pengalaman budaya tempatan, makanan lazat, serta tips pendakian."},"city":["Lijiang"],"attraction":["Shuhe Ancient Town","Tiger Leaping Gorge","Yulong Snow Mountain","Lijiang Ancient Town"],"preferLang":"ko","index":60,"interestLabel":"自然景観"},{"link":"italian-guy-loves-china-and-will-return-to-chongqing","category":["Chinese Cultural Heritage","China Vlog"],"title":{"root":"Italian Guy Loves China and Will Return to Chongqing","ja":"イタリアの男性は中国が大好きで、重慶に再び訪れる予定です","ko":"이탈리아 남성은 중국을 사랑하고 충칭에 다시 올 계획입니다","th":"ชายอิตาเลียนรักจีนและจะกลับมาที่ฉงชิ่งอีกครั้ง","ms":"Lelaki Itali Suka China dan Akan Kembali ke Chongqing"},"videoId":"Vb0m7_MIWbk","introduce":{"root":"Gabi's travel experience in Chongqing. He started from a café, then headed to the Taoist temple Laojun Cave on top of the mountain and enjoyed the beautiful scenery. Afterward, he visited an ancient town in Chongqing, experiencing the local culture and cuisine. Finally, he saw many unique Chinese motorcycle brands at a motorcycle dealership and expressed his love for China and his desire to explore the country by motorcycle.","ja":"ガビの重慶での旅行体験。彼はカフェから始め、山頂にある道教の廟である老君洞へ向かい、美しい景色を楽しみました。その後、重慶の古い町を訪れ、現地の文化や料理を体験しました。最後に、バイク販売店で多くの中国独特のバイクブランドを見つけて、中国への愛とバイクで中国を探検したいという願望を表現しました。","ko":"가비의 충칭 여행 경험입니다. 그는 카페에서 시작하여 산꼭대기의 도교 사원 라오준동으로 가서 아름다운 경관을 즐겼습니다. 그 후 그는 충칭의 고대 마을로 가서 현지 문화와 음식을 체험했습니다. 마지막으로 그는 오토바이 딜러에서 많은 중국 특유의 오토바이 브랜드를 보고 중국에 대한 사랑과 오토바이로 중국을 탐험하고 싶은 소망을 표현했습니다.","th":"ประสบการณ์การเดินทางของกาบีในฉงชิ่ง เขาเริ่มจากคาเฟ่ แล้วไปยังถ้ำเล่าจุนบนยอดเขาซึ่งเป็นวัดลัทธิเต๋า และเพลิดเพลินกับทิวทัศน์ที่สวยงาม จากนั้นเขาก็เยี่ยมชมเมืองโบราณในฉงชิ่ง ได้สัมผัสกับวัฒนธรรมและอาหารท้องถิ่น สุดท้ายเขาได้เห็นแบรนด์รถจักรยานยนต์ที่เป็นเอกลักษณ์ของจีนมากมายที่ร้านขายรถจักรยานยนต์ และแสดงความรักที่มีต่อจีนและความปรารถนาที่จะสำรวจประเทศโดยใช้รถจักรยานยนต์","ms":"Pengalaman pelancongan Gabi di Chongqing. Dia bermula dari sebuah kafe, kemudian menuju ke Gua Laojun yang merupakan kuil Taois di puncak gunung dan menikmati pemandangan indah. Selepas itu, dia melawat bandar kuno di Chongqing, mengalami budaya dan masakan tempatan. Akhirnya, dia melihat banyak jenama motosikal unik Cina di kedai motosikal dan mengungkapkan cintanya kepada China serta hasrat untuk meneroka negara tersebut dengan motosikal."},"description":{"root":"Discover the charm of Chongqing! Follow Italian traveler Gabi's journey from cafés to a Taoist temple, ancient towns with rich culture and cuisine.","ja":"重慶の魅力を発見しよう！イタリア人トラベラー、ガビの旅路を追体験。カフェから始まり、道教廟や古い町の文化・料理まで、そして中国独自のバイクブランドまで楽しめます。","ko":"충칭의 매력을 탐험하세요! 이탈리아 여행가 가비의 여정을 따라 카페, 도교 사원, 고대 마을의 문화와 음식까지 경험하고 중국 특유의 오토바이 브랜드를 만나보세요. 지금 바로 충칭 여행을 계획해보세요!","th":"ค้นพบมนต์ขลังของฉงชิ่ง! ตามรอยการเดินทางของนักเดินทางชาวอิตาเลียน กาบี จากคาเฟ่ วัดลัทธิเต๋า จนถึงเมืองโบราณที่เต็มไปด้วยวัฒนธรรมและอาหารอันหลากหลาย","ms":"Ketahui pesona Chongqing! Ikuti perjalanan pelancong Itali Gabi dari kafe hingga kuil Taois, bandar-bandar kuno dengan budaya dan masakan yang kaya."},"city":["Chongqing"],"attraction":["Laojun Cave"],"index":50,"interestLabel":"中国の文化遺産"},{"link":"irish-couple-explores-guangzhou-food-history-and-culture","category":["Chinese City Walk","Chinese Cultural Heritage","China Vlog"],"title":{"root":"Irish Couple Explores Guangzhou: Food, History, and Culture🇨🇳","ja":"アイルランド夫妻が広州を探索：食事、歴史、文化🇨🇳","ko":"아일랜드 부부의 광주 탐험: 음식, 역사, 문화🇨🇳","th":"คู่รักชาวไอริชสำรวจกว่างโจว: อาหาร ประวัติศาสตร์ และวัฒนธรรม🇨🇳","ms":"Pasangan Ireland Meneroka Guangzhou: Makanan, Sejarah, dan Budaya🇨🇳"},"videoId":"rqpoYHPoR0M","introduce":{"root":"A couple from Ireland shared their adventure in Guangzhou. With Vivian's help, they explored this entirely unfamiliar city and had an amazing day. First, they tried various local snacks, including pork or chicken-flavored cookies known as \\"chicken biscuits,\\" finding them incredibly delicious. Later, they successfully purchased tickets to the Chen Clan Academy, visiting this beautiful building, learning about the history of the Chen family, and admiring its exquisite carvings and decorations. They also visited Huayan Temple and Shamian Island, expressing amazement at China's culture and history. The blend of modernization and traditional culture in Guangzhou, along with its leisurely atmosphere, green spaces, and the beauty of the Pearl River, left a deep impression on them. This video not only showcases the charm of Guangzhou but also encourages viewers to continue exploring the unknown world.","ja":"アイルランドから来た夫婦が広州での冒険を体験しました。ヴィヴィアンの助けを借りて、彼らはこの全く知らない都市を探索し、驚きの一日を過ごしました。まず、彼らは地元のさまざまな小吃（スナック）を試し、特に「チキンビスケット」と呼ばれる豚肉や鶏肉味のビスケットの美味しさに感動しました。その後、成功裡に陳氏書院の入場券を購入し、その美しい建物を訪れ、陳家の歴史について学び、建物の精巧な彫刻と装飾を鑑賞しました。また、華厳寺と沙面島も訪れ、中国の文化と歴史に驚きを示しました。広州のモダンさと伝統文化の融合、のんびりとした雰囲気、緑豊かな空間、そして珠江の美しさは、彼らに深い印象を与えました。このビデオは、広州の魅力を紹介するだけでなく、観客に未知の世界をさらに探検するよう促しています。","ko":"아일랜드에서 온 부부가 광주에서의 모험을 경험했습니다. 비비안의 도움으로 그들은 이 낯선 도시를 탐험하고 놀라운 하루를 보냈습니다. 먼저, 그들은 다양한 현지 간식을 시도했는데, 특히 \\"치킨 비스킷\\"이라고 불리는 돼지고기나 닭고기 맛의 비스킷이 매우 맛있다는 것을 발견했습니다. 이후 그들은 성씨 서원의 입장권을 성공적으로 구매했으며, 이 아름다운 건축물을 방문하여 성 가문의 역사를 배우고 건물의 섬세한 조각과 장식을 감상했습니다. 또한 화엄사와 사면섬도 방문했으며, 중국의 문화와 역사에 감탄했습니다. 광주의 현대화와 전통문화의 융합, 여유로운 분위기, 녹지 공간, 그리고 주강의 아름다움은 그들에게 깊은 인상을 남겼습니다. 이 동영상은 광주의 매력을 보여줄 뿐만 아니라, 관객들이 더 많은 미지의 세계를 탐구하도록 영감을 줍니다.","th":"คู่รักชาวไอริชแบ่งปันการผจญภัยของพวกเขาในกว่างโจว โดยมีวิเวียนช่วยเหลือ พวกเขาได้สำรวจเมืองที่ไม่คุ้นเคยอย่างสมบูรณ์และมีวันที่น่าประทับใจมาก พวกเขาเริ่มจากการลองทานขนมขบเคี้ยวท้องถิ่นหลายชนิด รวมถึงคุกกี้รสหมูหรือไก่ที่เรียกว่า \\"คุกกี้ไก่\\" และพบว่าอร่อยมาก จากนั้นพวกเขาก็ซื้อบัตรเข้าชมสถาบันเฉินสำเร็จ แล้วไปเยี่ยมชมอาคารสวยงามแห่งนี้ เรียนรู้เกี่ยวกับประวัติศาสตร์ของครอบครัวเฉิน และชื่นชมแกะสลักและตกแต่งอันละเอียดอ่อน นอกจากนี้ยังได้ไปเยี่ยมชมวัดฮวาหยวนและเกาะซามีเอี้ยน โดยแสดงความประทับใจในวัฒนธรรมและประวัติศาสตร์ของจีน การผสมผสานระหว่างการพัฒนาสมัยใหม่และวัฒนธรรมดั้งเดิมในกว่างโจว พร้อมบรรยากาศสบายๆ พื้นที่สีเขียว และความงามของแม่น้ำเพิร์ล ทำให้พวกเขารู้สึกประทับใจอย่างลึกซึ้ง วิดีโอนี้ไม่เพียงแค่แสดงเสน่ห์ของกว่างโจวเท่านั้น แต่ยังกระตุ้นให้ผู้ชมออกไปสำรวจโลกที่ยังไม่รู้จักอีกด้วย","ms":"Pasangan dari Ireland berkongsi pengalaman petualangan mereka di Guangzhou. Dengan bantuan Vivian, mereka meneroka bandar yang sepenuhnya tidak dikenali ini dan mempunyai hari yang menakjubkan. Pertama, mereka mencuba pelbagai makanan ringan tempatan termasuk biskut rasa daging babi atau ayam yang dikenali sebagai \\"biskut ayam\\", mendapati mereka sangat lazat. Kemudian, mereka berjaya membeli tiket ke Akademi Klan Chen, melawat bangunan indah ini, belajar tentang sejarah keluarga Chen, dan mengagumi ukiran dan hiasan halusnya. Mereka juga melawat Kuil Huayan dan Pulau Shamian, mengungkapkan kagum terhadap budaya dan sejarah China. Campuran modenisasi dan budaya tradisional di Guangzhou, bersama dengan suasana santai, ruang hijau, dan keindahan Sungai Pearl meninggalkan kesan mendalam kepada mereka. Video ini bukan sahaja mempamerkan pesona Guangzhou tetapi juga mendorong penonton untuk meneroka dunia yang belum diketahui."},"description":{"root":"Discover the charm of Guangzhou: Couple's journey through food and culture. From ancient architecture to modern city life, experience the vibrant spirit!","ja":"広州の魅力を発見しましょう！アイルランド夫妻と一緒に食事と文化の旅へ出かけ、古代建築から現代都市まで、多様な顔を持つ広州の伝統、活気、そして独特な美食を堪能してください。","ko":"광주의 매력을 탐험하세요: 아일랜드 부부의 음식과 문화 여행. 고대 건축물에서 현대 도시까지 광주의 전통과 역동성을 경험해 보세요!","th":"ค้นพบมนต์ขลังของกว่างโจว: การเดินทางของคู่รักผ่านอาหารและวัฒนธรรม จากสถาปัตยกรรมโบราณจนถึงชีวิตเมืองสมัยใหม่ สัมผัสจิตวิญญาณที่มีชีวิตชีวา!","ms":"Ketahui pesona Guangzhou: Pengembaraan pasangan melalui makanan dan budaya. Dari seni bina kuno hingga kehidupan bandar moden, alami semangat yang hidup!"},"city":["Guangzhou"],"attraction":["Chen Clan Academy","Shamian Island","Hualin Temple"],"index":50,"interestLabel":"中国シティウォーク"}]},"headers":[],"params":{"lang":"ja","frontmatter":{"titleTemplate":":title: 中国週末旅行ガイド – ローカルおすすめスポット","description":"週末で行く中国旅✨厳選スポット・地元食・穴場情報｜短期滞在者向け「失敗しない」完全ガイド。現地リサーチャー直伝の時短プランで、2泊3日を120%楽しむコツ。","hero":{"title":"中国旅行ローカルガイド"},"topAttractions":[{"name":"Hongya Cave","city":"Chongqing","text":"洪崖洞","link":"hongya-cave"},{"name":"Yulong Snow Mountain","city":"Lijiang","text":"玉龙雪山","link":"yulong-snow-moutain"},{"name":"Chengdu Panda Research Base","city":"Chengdu","text":"成都パンダ研究基地","link":"chengdu-panda-research-base"},{"name":"Mutianyu Great Wall","city":"Beijing","text":"慕田峪長城","link":"mutianyu-great-wall"},{"name":"The Forbidden City","city":"Beijing","text":"紫禁城","link":"the-forbidden-city"}],"topDestinations":[{"name":"Beijing","text":"北京","link":"/beijing"},{"name":"Chongqing","text":"重慶","link":"/chongqing"},{"name":"Hangzhou","text":"杭州","link":"/hangzhou"},{"name":"Lijiang","text":"麗江","link":"/lijiang"}],"topArticles":[{"link":"japanese-friends-3-night-5-day-weekend-in-chongqing","category":["Chinese City Walk","Chinese Cultural Heritage","China Vlog"],"title":{"root":"Chongqing Travel: 3-Night, 5-Day Weekend Adventure with Japanese Friends","ja":"重慶旅行・3泊5日の週末体験 | 一騎と友人たちの探検記","ko":"중경 여행 · 주말 3박 5일 일본 친구들과의 모험","th":"การเดินทางที่ฉงชิ่ง: การผจญภัย 3 คืน 5 วันกับเพื่อนชาวญี่ปุ่น","ms":"Perjalanan Chongqing: Pengembaraan 3 Malam, 5 Hari Akhir Minggu dengan Rakan Jepun"},"videoId":"EKq1dThQRQ0","introduce":{"root":"Over the weekend, a group of Japanese friends embarked on a 3-night, 5-day trip to Chongqing. They explored the city's modern architecture and historical temples, indulged in local cuisine and culture, and learned about its rich history. As one of China's four municipalities, Chongqing boasts a population of over 30 million, making it one of the largest cities in the world. The video highlights Chongqing's historical significance, including its role as the wartime capital during World War II and the bombings by the Japanese army. The trip concluded with a delightful taste of local delicacies and breathtaking views of Chongqing's night skyline.","ja":"週末の3泊5日で、日本の友人たちは重慶を訪れました。彼らは重慶の現代的な建築や歴史的な寺院を巡り、地元の料理や文化を満喫しました。また、中国の四大直轄市である重慶の人口3000万人を超える大都市の規模や、第二次世界大戦中に国民党政府の臨時首都となり、日本軍による爆撃を受けた歴史についても触れています。最後には、地元の美食を堪能し、重慶の美しい夜景を楽しみました。","ko":"일본인 친구들 그룹이 주말을 이용해 3박 5일간 중경으로 여행을 떠났습니다. 그들은 중경의 현대적인 건축물과 역사적인 사찰을 탐방하고, 현지 음식과 문화를 체험했습니다. 또한 중경의 풍부한 역사를 배웠는데, 중국의 4대 직할시 중 하나로 인구 3천만 명 이상을 자랑하는 세계에서 가장 큰 도시 중 하나입니다. 영상에서는 중경이 제2차 세계 대전 당시 국민정부의 임시 수도였으며 일본군에 의해 폭격당했던 역사적 배경도 다루었습니다. 여행의 마지막에는 그들은 현지 특산물을 맛보고 중경의 환상적인 야경을 즐겼습니다.","th":"ในช่วงสุดสัปดาห์กลุ่มเพื่อนชาวญี่ปุ่นได้เริ่มต้นการเดินทาง 3 คืน 5 วันไปยังฉงชิ่ง พวกเขาสำรวจสถาปัตยกรรมสมัยใหม่และวัดเก่าแก่ของเมือง ได้ลิ้มลองอาหารและวัฒนธรรมท้องถิ่น และเรียนรู้เกี่ยวกับประวัติศาสตร์อันยาวนานของฉงชิ่ง เมืองนี้เป็นหนึ่งในสี่นครปกครองส่วนกลางของจีน มีประชากรมากกว่า 30 ล้านคน ทำให้เป็นเมืองที่ใหญ่ที่สุดแห่งหนึ่งของโลก วิดีโอนำเสนอความสำคัญทางประวัติศาสตร์ของฉงชิ่งรวมถึงบทบาทในฐานะเมืองหลวงชั่วคราวในช่วงสงครามโลกครั้งที่สองและการถูกโจมตีด้วยระเบิดจากกองกำลังทหารญี่ปุ่น การเดินทางจบลงอย่างสวยงามด้วยการลิ้มรสอาหารท้องถิ่นและชมทิวทัศน์ยามค่ำคืนที่น่าตื่นตาตื่นใจของฉงชิ่ง","ms":"Pada hujung minggu, sekumpulan rakan-rakan dari Jepun memulakan perjalanan 3 malam 5 hari ke Chongqing. Mereka meneroka seni bina moden dan kuil bersejarah bandar, menikmati masakan dan budaya tempatan, serta belajar tentang sejarah kaya Chongqing. Sebagai salah satu daripada empat daerah pentadbiran pusat di China, Chongqing memiliki populasi lebih daripada 30 juta penduduk, menjadikannya salah satu bandar terbesar di dunia. Video ini menunjukkan kepentingan sejarah Chongqing termasuk perananya sebagai ibu kota semasa Perang Dunia Kedua dan pengeboman oleh tentera Jepun. Perjalanan itu berakhir dengan mencicipi hidangan tempatan yang lazat dan pemandangan cakrawala malam Chongqing yang menakjubkan."},"description":{"root":"Join a group of Japanese friends on their 3-night, 5-day weekend getaway to Chongqing. Explore modern architecture, historic temples.","ja":"日本の友人と重慶へ週末旅行へ出かけませんか？現代的な建築と歴史的な寺院を巡り、地元の美食や文化を満喫。重慶が世界最大級の都市と称される所以をぜひ体感してください！","ko":"일본인 친구들이 주말에 떠난 3박 5일 중경 여행에 동행하세요. 현대적인 건축물과 역사적인 사찰을 탐방하고, 지역 음식과 문화를 체험하며 중경의 풍부한 역사를 알아보세요. 제2차 세계 대전과 관련된 중경의 과거와 현재를 함께 만나보세요.","th":"เข้าร่วมกับกลุ่มเพื่อนชาวญี่ปุ่นในการเดินทางพักผ่อน 3 คืน 5 วันที่ฉงชิ่ง สำรวจสถาปัตยกรรมสมัยใหม่ วัดเก่าแก่ และสัมผัสประสบการณ์วัฒนธรรมท้องถิ่นที่หลากหลาย","ms":"Sertai sekumpulan rakan-rakan dari Jepun dalam pengembaraan akhir minggu 3 malam 5 hari mereka ke Chongqing. Meneroka seni bina moden, kuil bersejarah, dan alami budaya serta masakan tempatan yang unik."},"city":["Chongqing"],"attraction":["Luohan Temple","Hongya Cave"],"preferLang":"ja","index":90,"interestLabel":"中国シティウォーク"},{"link":"joels-chengdu-adventure","category":["Pandas and Other Animals","China Vlog"],"title":{"root":"Joel's Chengdu Adventure: Pandas, Hotpot, and Cultural Wonders","ja":"ジョエルの成都旅：パンダ、火鍋、文化の魅力","ko":"조엘의 청두 모험: 판다, 핫팟, 그리고 문화의 경이","th":"การผจญภัยของโจเอลในเฉิงตู: แพนด้า ฮอทพอท และความมหัศจรรย์ทางวัฒนธรรม","ms":"Petualangan Joel di Chengdu: Panda, Tumis Panas, dan Keajaiban Budaya"},"videoId":"UqmPUsnWg14","introduce":{"root":"British traveler Joel Friend revisits Chengdu, China, for another adventure. He visits the Panda Valley to observe giant pandas and learns about their conservation status and reintroduction program. Joel also checks out the red pandas and explores scenic spots in downtown Chengdu, enjoying traditional Chinese ethnic music. Next, he heads to the Jinsha Site Museum to delve into the ancient culture and history of the Shu people. Finally, Joel experiences Chengdu's daily life at Jinli Street, where he tries sugar painting, ear cleaning, and even robot-made coffee. Despite his previous unsuccessful attempt, Joel successfully enjoys Chengdu hotpot this time, sampling various dishes such as duck tongues and pig brains. To wrap up his trip, he visits a shopping mall and spots giant panda statues on the rooftops. Chengdu's relaxed lifestyle and panda culture leave a lasting impression on Joel.","ja":"イギリス人トラベラー、ジョエル・フレンドが再び中国成都を訪れる旅行記録です。彼は成都のパンダバレーでジャイアントパンダを観賞し、その生存状況や再導入計画について学びます。また、レッサーパンダも見学し、成都市内の観光地を訪れ、中国の民族音楽を楽しみました。その後、彼は金沙遺跡博物館に向かい、古代蜀人の文化と歴史について深く探ります。最後に、彼は錦里街で成都の日常生活を体験します。糖絵（とうが）や耳掃除、さらにはロボットによるコーヒー作りも試しました。前回うまくいかなかった成都の火鍋も、今回は成功し、鴨の舌や豚の脳みそなどさまざまな料理を堪能しました。さらに、彼はショッピングモールを訪れ、屋上にあるパンダの像を見ました。成都のゆったりとしたライフスタイルとパンダ文化がジョエルに深い印象を残しました。","ko":"영국 여행가 조엘 프렌드가 다시 중국 청두를 방문하여 또 다른 모험을 즐깁니다. 그는 판다 계곡을 방문하여 자이언트 판다를 관찰하고 그들의 보존 상태와 재도입 프로그램에 대해 배웁니다. 또한 레서 판다도 확인하고 청두 시내의 명소를 탐험하며 전통적인 중국 민족 음악을 즐깁니다. 다음으로 그는 진사 유적 박물관을 방문하여 고대 쓰 사람들의 문화와 역사를 깊이 탐구합니다. 마지막으로 조엘은 진리 거리에서 청두의 일상 생활을 경험하며 설탕 그림, 귀 청소, 심지어 로봇이 만든 커피도 시도합니다. 이전에 실패했던 청두 핫팟도 이번에는 성공적으로 즐기며 오리 혀, 돼지 뇌 등 다양한 요리를 맛봅니다. 여행을 마무리하면서 그는 쇼핑몰을 방문하고 지붕 위에 있는 거대한 판다 동상을 발견합니다. 청두의 여유로운 라이프스타일과 판다 문화는 조엘에게 깊은 인상을 남깁니다.","th":"โจเอล นักเดินทางชาวอังกฤษ เยี่ยมชมเฉิงตู ประเทศจีน อีกครั้งเพื่อผจญภัยใหม่ เขาไปเยี่ยมชมหุบเขาแพนด้าเพื่อสังเกตการณ์แพนด้าขนาดใหญ่และเรียนรู้เกี่ยวกับสถานะการอนุรักษ์และการฟื้นฟูประชากรแพนด้า นอกจากนี้เขายังสำรวจแพนด้าแดงและสำรวจสถานที่ท่องเที่ยวในเมืองเฉิงตู พร้อมทั้งเพลิดเพลินกับดนตรีพื้นเมืองจีน จากนั้นเขาไปเยี่ยมชมพิพิธภัณฑ์ไซต์จินซา เพื่อสำรวจวัฒนธรรมและประวัติศาสตร์โบราณของชนชาติซู ในที่สุดโจเอลได้สัมผัสชีวิตประจำวันในเฉิงตูที่ถนนจินหลี่ โดยลองทำภาพวาดน้ำตาล การทำความสะอาดหู และกาแฟที่ทำโดยหุ่นยนต์ แม้ว่าจะเคยไม่สำเร็จในการลองฮอทพอทมาก่อน แต่ครั้งนี้โจเอลสามารถเพลิดเพลินกับฮอทพอทเฉิงตูได้สำเร็จ ลองชิมเมนูต่างๆ เช่น ลิ้นเป็ดและสมองหมู เพื่อปิดทริป เขาได้เยี่ยมชมศูนย์การค้าและพบกับรูปปั้นแพนด้าขนาดใหญ่บนหลังคา ไลฟ์สไตล์ที่ผ่อนคลายและวัฒนธรรมแพนด้าของเฉิงตูได้สร้างความประทับใจให้แก่โจเอล","ms":"Pelancong British Joel Friend kembali mengunjungi Chengdu, China untuk satu lagi petualangan. Dia melawat Lembah Panda untuk memerhati panda raksasa dan belajar tentang status pemuliharaan mereka serta program pengenalan semula. Joel juga melihat panda merah dan meneroka tempat-tempat menarik di pusat bandar Chengdu, menikmati muzik etnik tradisional Cina. Seterusnya, dia menuju ke Muzium Tapak Jinsha untuk menyelidiki budaya dan sejarah kuno orang Shu. Akhirnya, Joel mengalami kehidupan harian di Chengdu di Jalan Jinli, di mana dia mencuba lukisan gula, pembersihan telinga, dan bahkan kopi yang dibuat oleh robot. Walaupun cubaan sebelumnya gagal, Joel berjaya menikmati tumis panas Chengdu kali ini, mencuba pelbagai hidangan seperti lidah itik dan otak babi. Untuk menamatkan perjalanannya, dia melawat pusat membeli-belah dan melihat patung panda raksasa di atas bumbung. Gaya hidup santai dan budaya panda Chengdu meninggalkan kesan mendalam kepada Joel."},"description":{"root":"Experience Joel's unforgettable journey in Chengdu, exploring panda reserves and ancient culture. Discover top attractions, delicious hotpot.","ja":"ジョエルが成都で体験する忘れられない旅。パンダ保護区の探索、魅力あふれる成都の観光スポットや美味しい火鍋、独自の伝統をぜひご自分の旅でも体験してください！","ko":"청두에서 조엘이 경험하는 잊지 못할 여행. 판다 보호구역 탐험, 매력적인 청두의 관광 명소와 맛있는 핫팟, 독특한 전통을 당신의 여행에서도 경험해보세요!","th":"สำรวจการเดินทางที่น่าจดจำของโจเอลในเฉิงตู สำรวจเขตสงวนแพนด้าและวัฒนธรรมโบราณ ค้นหาสถานที่ท่องเที่ยวยอดนิยมและฮอทพอทแสนอร่อย","ms":"Alami perjalanan tidak terlupakan Joel di Chengdu, meneroka tapak simpanan panda dan budaya kuno. Terokai tarikan utama, tumis panas lazat."},"city":["Chengdu"],"attraction":["Chengdu Panda Research Base"],"index":100,"interestLabel":"パンダを含む他の動物たち"},{"link":"chongqing-hot-pot","category":["Chinese Cuisine Culture"],"title":{"root":"Local Flavor & Must-Tries: Chongqing Hot Pot","ja":"地元の味と絶対に試すべきもの：重慶ホットポト","ko":"현지 맛과 꼭 먹어야 할 것들: 충칭 핫팟","th":"รสชาติท้องถิ่นและเมนูที่ต้องลอง: หม้อไฟฉงชิ่ง","ms":"Perisa Tempatan & Mesti Cuba: Rebusan Panas Chongqing"},"introduce":{"root":"Dive into the sizzling world of Chongqing hotpot, a culinary adventure blending flavor and culture. Born on the Yangtze River docks, this dish is now a social staple enjoyed year-round. Choose from mild clear soup to fiery red broth, and savor traditional ingredients like beef tripe and duck intestines. Navigate spice levels with dipping sauces and cooling desserts. Whether a first-timer or spice enthusiast, this guide equips you to eat like a local and embrace the vibrant Chongqing hotpot experience. Ignite your taste buds today!","ja":"重慶ホットポットは長江の港町発祥の料理で、味覚と文化を楽しむことができます。このガイドでは、辛さの秘密やマイルドなスープから本場の赤いスープまで、スープの選び方を紹介。牛肉のミノや鴨の腸などの伝統的食材や、ディップソース、デザートでの辛さ調節法も解説します。初心者から辛い料理好きまで、ローカル流の楽しみ方がわかります。味覚を刺激し、重慶の食文化を体験しましょう！","ko":"충칭 핫팟의 세계로 빠져보세요, 이는 맛과 문화가 결합된 요리 모험입니다. 양쯔강 항구에서 시작된 이 요리는 이제 연중 즐기는 사회적 스테이플입니다. 순한 클리어 수프부터 불타는 붉은 국물까지 다양한 선택지와 전통적인 재료인 소 허파와 오리 내장 등을 즐길 수 있습니다. 디핑 소스와 시원한 디저트로 매운 정도를 조절하세요. 초보자든 매운 음식 애호가든 이 가이드는 당신이 로컬처럼 먹고 활기찬 충칭 핫팟 경험을 즐길 수 있도록 도와줍니다.","th":"ดำดิ่งสู่โลกของหม้อไฟฉงชิ่ง ซึ่งเป็นการผจญภัยทางการทำอาหารที่ผสมผสานระหว่างรสชาติและวัฒนธรรม เริ่มต้นที่ท่าเรือแม่น้ำแยงซีเกียง ปัจจุบันนี้เป็นอาหารประจำที่ชาวเมืองรับประทานตลอดทั้งปี เลือกจากน้ำซุปใสไปจนถึงน้ำซุปแดงเผ็ดร้อน และเพลิดเพลินกับส่วนประกอบแบบดั้งเดิม เช่น กระเพาะวัวและลำไส้เป็ด ปรับระดับความเผ็ดด้วยซอสดิ๊พและของหวานเย็น ๆ ไม่ว่าคุณจะเป็นมือใหม่หรือคนชอบกินเผ็ด คู่มือนี้จะทำให้คุณสามารถรับประทานเหมือนคนท้องถิ่นและสนุกกับประสบการณ์หม้อไฟฉงชิ่งที่มีชีวิตชีวา จุดประกายรสชาติของคุณวันนี้!","ms":"Menyelam ke dalam dunia rebusan panas Chongqing, sebuah petualangan masakan yang menggabungkan perisa dan budaya. Bermula dari pelabuhan Sungai Yangtze, hidangan ini kini menjadi makanan sosial yang dinikmati sepanjang tahun. Pilih dari sup jernih ringan hingga kuah merah yang pedas, dan nikmati bahan-bahan tradisional seperti tripus lembu dan usus itik. Navigasikan tahap rasa pedas dengan sos celup dan pencuci mulut penyejuk. Sama ada pemula atau peminat rasa pedas, panduan ini akan melengkapkan anda untuk makan seperti penduduk tempatan dan menerima pengalaman rebusan panas Chongqing yang bersemangat. Hidupkan lidah anda hari ini!"},"description":{"root":"Discover Chongqing hot pot's history, flavors, and social dining experience. Must-try dishes, varieties, and dipping sauces. Perfect for gatherings.","ja":"重慶火鍋の独特な風味と試すべき美食を探索しましょう。重慶火鍋の歴史、種類、つけダレの選び方、そしてこの社交的な食事体験をどのように楽しむかについて学びましょう。","ko":"충칭 핫팟의 역사, 맛, 그리고 사회적 식사 경험을 탐험하세요. 꼭 시도해야 할 요리, 종류, 그리고 디핑 소스. 모임에 완벽합니다.","th":"สำรวจประวัติศาสตร์ รสชาติ และประสบการณ์การรับประทานอาหารทางสังคมของหม้อไฟฉงชิ่ง เมนูที่ต้องลอง หลากหลายประเภท และน้ำจิ้มที่ควรลอง เป็นที่เหมาะสำหรับการรวมกลุ่ม","ms":"Meneroka sejarah, perisa, dan pengalaman makan bersosial rebusan panas Chongqing. Hidangan wajib cuba, pelbagai jenis, dan sos celup. Sempurna untuk berkumpul."},"city":["Chongqing"],"attraction":[],"index":80,"interestLabel":"中国の食文化"},{"link":"korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","category":["Natural Scenery","Chinese Cultural Heritage","China Vlog"],"title":{"root":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","ja":"韓国教育長の退職旅：虎跳峽と玉龍雪山","ko":"한국 교육감의 퇴역 여행: 호점협과 용설산","th":"การเดินทางของอดีตผู้อำนวยการศึกษาเกาหลีสู่ช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกร","ms":"Perjalanan Ketua Pendidikan Berundur dari Korea ke Jurang Harimau Melompat dan Gunung Salji Naga Giok"},"videoId":"L8aGEwBb4uM","introduce":{"root":"After retiring as the education commissioner of South Korea, he traveled to Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan, China. During his journey, he experienced China's train culture, tasted local cuisine, visited traditional markets, and found comfortable accommodation in Lijiang. He also shared the challenges and breathtaking views of hiking up Jade Dragon Snow Mountain, along with a small tip about using sunscreen sticks. In Lachun Village, he enjoyed Korean dishes and admired the magnificent natural scenery in places like Walnut Leaf and Haoyilu. He visited Jade Dragon Snow Mountain, experienced altitude sickness, and successfully reached the summit. Throughout the trip, he immersed himself in the local culture and cuisine, forming deep friendships with the locals.","ja":"韓国教育長官が退職後、中国雲南省の虎跳峽（こびょうか）と玉龍雪山（ぎょくりゅうせつざん）へ旅行しました。旅の中で彼は中国の鉄道文化を体験し、地元の料理を味わい、伝統市場を見学し、麗江（りきょう）で快適な宿泊先を見つけました。また、玉龍雪山へのハイキングの困難さや美しい景色について語り、日焼け止めスティックを使った小さなコツも共有しました。ラ村では韓国料理を食べ、胡桃葉（ごうだよう）や好意路（こうぎろ）などで雄大な自然を楽しみました。彼は玉龍雪山を訪れ、高山病を経験しながらも無事に頂上に到達しました。旅を通じて、彼は現地の文化や料理を体験し、現地の人々との深い友情を築きました。","ko":"한국 교육감이 퇴직 후 중국 윈난성의 호점협과 용설산을 여행했습니다. 그는 여행 중에 중국 기차 문화를 체험하고 현지 음식을 맛보며 전통 시장을 방문했고, 리장에서 편안한 숙소를 찾았습니다. 또한 용설산 트레킹의 어려움과 아름다운 경치에 대해 이야기하며 자외선 차단제 스틱 사용법이라는 작은 팁도 공유했습니다. 라촌에서는 한국 요리를 먹었고, 호두엽과 해의로 같은 곳에서 웅대한 자연 풍경을 감상했습니다. 그는 용설산을 방문해 고산병을 경험했지만 결국 정상에 성공적으로 도달했습니다. 여행 동안 그는 현지 문화와 음식을 체험하고 주민들과 깊은 우정을 쌓았습니다.","th":"หลังจากเกษียณจากตำแหน่งผู้อำนวยการศึกษาของเกาหลีใต้ เขาได้เดินทางไปยังช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในมณฑลยูนนาน ประเทศจีน ในระหว่างการเดินทาง เขาได้สัมผัสวัฒนธรรมรถไฟของจีน ชิมอาหารท้องถิ่น เยี่ยมชมตลาดแบบดั้งเดิม และพบที่พักสบายๆ ในเมืองลี่เจียง เขายังแบ่งปันความท้าทายและความสวยงามของการเดินป่าขึ้นภูเขาน้ำแข็งหยกมังกร พร้อมคำแนะนำเล็กๆ น้อยๆ เกี่ยวกับการใช้แท่งครีมกันแดด ในหมู่บ้านลาชุน เขาได้เพลิดเพลินกับอาหารเกาหลีและชื่นชมทิวทัศน์ธรรมชาติอันงดงามที่สถานที่อย่างใบมะรุมและถนนเฮาอี้ลู่ เขาได้เยี่ยมชมภูเขาน้ำแข็งหยกมังกร ประสบกับอาการแพ้ความสูง และประสบความสำเร็จในการขึ้นถึงยอดเขา ตลอดการเดินทาง เขาได้สัมผัสกับวัฒนธรรมและอาหารท้องถิ่น และสร้างมิตรภาพที่ลึกซึ้งกับชาวบ้าน","ms":"Selepas bersara sebagai Pengarah Pendidikan Korea Selatan, beliau telah melancong ke Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan, China. Semasa perjalanannya, beliau mengalami budaya kereta api China, merasai masakan tempatan, melawat pasar tradisional, dan menemui penginapan yang selesa di Lijiang. Beliau juga berkongsi cabaran dan pemandangan menakjubkan semasa mendaki Gunung Salji Naga Giok, bersama dengan sedikit petua tentang menggunakan batang pelindung matahari. Di Kampung Lachun, beliau menikmati hidangan Korea dan memuji pemandangan alam semula jadi yang megah di tempat-tempat seperti Daun Kenari dan Jalan HaoYiLu. Beliau telah mengunjungi Gunung Salji Naga Giok, mengalami penyakit ketinggian, dan berjaya mencapai puncak gunung tersebut. Sepanjang perjalanan, beliau terbenam dalam budaya dan masakan tempatan, membina persahabatan yang mendalam dengan penduduk tempatan."},"description":{"root":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","ja":"中国雲南省の虎跳峽や玉龍雪山を巡る壮大な旅に参加しましょう。韓国教育長官も退職後楽しんだ冒険を通じ、地元の鉄道文化、美食、絶景を体験。ハイキングのコツもお届けします！","ko":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","th":"เข้าร่วมการผจญภัยที่น่าตื่นตาตื่นใจผ่านช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในยูนนาน สำรวจทิวทัศน์ที่งดงามและสัมผัสวัฒนธรรมท้องถิ่น อาหารอร่อย และเทคนิคการเดินป่า","ms":"Sertai Sugilway dalam pengembaraan menakjubkan melalui Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan. Alami landskap yang menakjubkan dan sertai pengalaman budaya tempatan, makanan lazat, serta tips pendakian."},"city":["Lijiang"],"attraction":["Shuhe Ancient Town","Tiger Leaping Gorge","Yulong Snow Mountain","Lijiang Ancient Town"],"preferLang":"ko","index":60,"interestLabel":"自然景観"},{"link":"italian-guy-loves-china-and-will-return-to-chongqing","category":["Chinese Cultural Heritage","China Vlog"],"title":{"root":"Italian Guy Loves China and Will Return to Chongqing","ja":"イタリアの男性は中国が大好きで、重慶に再び訪れる予定です","ko":"이탈리아 남성은 중국을 사랑하고 충칭에 다시 올 계획입니다","th":"ชายอิตาเลียนรักจีนและจะกลับมาที่ฉงชิ่งอีกครั้ง","ms":"Lelaki Itali Suka China dan Akan Kembali ke Chongqing"},"videoId":"Vb0m7_MIWbk","introduce":{"root":"Gabi's travel experience in Chongqing. He started from a café, then headed to the Taoist temple Laojun Cave on top of the mountain and enjoyed the beautiful scenery. Afterward, he visited an ancient town in Chongqing, experiencing the local culture and cuisine. Finally, he saw many unique Chinese motorcycle brands at a motorcycle dealership and expressed his love for China and his desire to explore the country by motorcycle.","ja":"ガビの重慶での旅行体験。彼はカフェから始め、山頂にある道教の廟である老君洞へ向かい、美しい景色を楽しみました。その後、重慶の古い町を訪れ、現地の文化や料理を体験しました。最後に、バイク販売店で多くの中国独特のバイクブランドを見つけて、中国への愛とバイクで中国を探検したいという願望を表現しました。","ko":"가비의 충칭 여행 경험입니다. 그는 카페에서 시작하여 산꼭대기의 도교 사원 라오준동으로 가서 아름다운 경관을 즐겼습니다. 그 후 그는 충칭의 고대 마을로 가서 현지 문화와 음식을 체험했습니다. 마지막으로 그는 오토바이 딜러에서 많은 중국 특유의 오토바이 브랜드를 보고 중국에 대한 사랑과 오토바이로 중국을 탐험하고 싶은 소망을 표현했습니다.","th":"ประสบการณ์การเดินทางของกาบีในฉงชิ่ง เขาเริ่มจากคาเฟ่ แล้วไปยังถ้ำเล่าจุนบนยอดเขาซึ่งเป็นวัดลัทธิเต๋า และเพลิดเพลินกับทิวทัศน์ที่สวยงาม จากนั้นเขาก็เยี่ยมชมเมืองโบราณในฉงชิ่ง ได้สัมผัสกับวัฒนธรรมและอาหารท้องถิ่น สุดท้ายเขาได้เห็นแบรนด์รถจักรยานยนต์ที่เป็นเอกลักษณ์ของจีนมากมายที่ร้านขายรถจักรยานยนต์ และแสดงความรักที่มีต่อจีนและความปรารถนาที่จะสำรวจประเทศโดยใช้รถจักรยานยนต์","ms":"Pengalaman pelancongan Gabi di Chongqing. Dia bermula dari sebuah kafe, kemudian menuju ke Gua Laojun yang merupakan kuil Taois di puncak gunung dan menikmati pemandangan indah. Selepas itu, dia melawat bandar kuno di Chongqing, mengalami budaya dan masakan tempatan. Akhirnya, dia melihat banyak jenama motosikal unik Cina di kedai motosikal dan mengungkapkan cintanya kepada China serta hasrat untuk meneroka negara tersebut dengan motosikal."},"description":{"root":"Discover the charm of Chongqing! Follow Italian traveler Gabi's journey from cafés to a Taoist temple, ancient towns with rich culture and cuisine.","ja":"重慶の魅力を発見しよう！イタリア人トラベラー、ガビの旅路を追体験。カフェから始まり、道教廟や古い町の文化・料理まで、そして中国独自のバイクブランドまで楽しめます。","ko":"충칭의 매력을 탐험하세요! 이탈리아 여행가 가비의 여정을 따라 카페, 도교 사원, 고대 마을의 문화와 음식까지 경험하고 중국 특유의 오토바이 브랜드를 만나보세요. 지금 바로 충칭 여행을 계획해보세요!","th":"ค้นพบมนต์ขลังของฉงชิ่ง! ตามรอยการเดินทางของนักเดินทางชาวอิตาเลียน กาบี จากคาเฟ่ วัดลัทธิเต๋า จนถึงเมืองโบราณที่เต็มไปด้วยวัฒนธรรมและอาหารอันหลากหลาย","ms":"Ketahui pesona Chongqing! Ikuti perjalanan pelancong Itali Gabi dari kafe hingga kuil Taois, bandar-bandar kuno dengan budaya dan masakan yang kaya."},"city":["Chongqing"],"attraction":["Laojun Cave"],"index":50,"interestLabel":"中国の文化遺産"},{"link":"irish-couple-explores-guangzhou-food-history-and-culture","category":["Chinese City Walk","Chinese Cultural Heritage","China Vlog"],"title":{"root":"Irish Couple Explores Guangzhou: Food, History, and Culture🇨🇳","ja":"アイルランド夫妻が広州を探索：食事、歴史、文化🇨🇳","ko":"아일랜드 부부의 광주 탐험: 음식, 역사, 문화🇨🇳","th":"คู่รักชาวไอริชสำรวจกว่างโจว: อาหาร ประวัติศาสตร์ และวัฒนธรรม🇨🇳","ms":"Pasangan Ireland Meneroka Guangzhou: Makanan, Sejarah, dan Budaya🇨🇳"},"videoId":"rqpoYHPoR0M","introduce":{"root":"A couple from Ireland shared their adventure in Guangzhou. With Vivian's help, they explored this entirely unfamiliar city and had an amazing day. First, they tried various local snacks, including pork or chicken-flavored cookies known as \\"chicken biscuits,\\" finding them incredibly delicious. Later, they successfully purchased tickets to the Chen Clan Academy, visiting this beautiful building, learning about the history of the Chen family, and admiring its exquisite carvings and decorations. They also visited Huayan Temple and Shamian Island, expressing amazement at China's culture and history. The blend of modernization and traditional culture in Guangzhou, along with its leisurely atmosphere, green spaces, and the beauty of the Pearl River, left a deep impression on them. This video not only showcases the charm of Guangzhou but also encourages viewers to continue exploring the unknown world.","ja":"アイルランドから来た夫婦が広州での冒険を体験しました。ヴィヴィアンの助けを借りて、彼らはこの全く知らない都市を探索し、驚きの一日を過ごしました。まず、彼らは地元のさまざまな小吃（スナック）を試し、特に「チキンビスケット」と呼ばれる豚肉や鶏肉味のビスケットの美味しさに感動しました。その後、成功裡に陳氏書院の入場券を購入し、その美しい建物を訪れ、陳家の歴史について学び、建物の精巧な彫刻と装飾を鑑賞しました。また、華厳寺と沙面島も訪れ、中国の文化と歴史に驚きを示しました。広州のモダンさと伝統文化の融合、のんびりとした雰囲気、緑豊かな空間、そして珠江の美しさは、彼らに深い印象を与えました。このビデオは、広州の魅力を紹介するだけでなく、観客に未知の世界をさらに探検するよう促しています。","ko":"아일랜드에서 온 부부가 광주에서의 모험을 경험했습니다. 비비안의 도움으로 그들은 이 낯선 도시를 탐험하고 놀라운 하루를 보냈습니다. 먼저, 그들은 다양한 현지 간식을 시도했는데, 특히 \\"치킨 비스킷\\"이라고 불리는 돼지고기나 닭고기 맛의 비스킷이 매우 맛있다는 것을 발견했습니다. 이후 그들은 성씨 서원의 입장권을 성공적으로 구매했으며, 이 아름다운 건축물을 방문하여 성 가문의 역사를 배우고 건물의 섬세한 조각과 장식을 감상했습니다. 또한 화엄사와 사면섬도 방문했으며, 중국의 문화와 역사에 감탄했습니다. 광주의 현대화와 전통문화의 융합, 여유로운 분위기, 녹지 공간, 그리고 주강의 아름다움은 그들에게 깊은 인상을 남겼습니다. 이 동영상은 광주의 매력을 보여줄 뿐만 아니라, 관객들이 더 많은 미지의 세계를 탐구하도록 영감을 줍니다.","th":"คู่รักชาวไอริชแบ่งปันการผจญภัยของพวกเขาในกว่างโจว โดยมีวิเวียนช่วยเหลือ พวกเขาได้สำรวจเมืองที่ไม่คุ้นเคยอย่างสมบูรณ์และมีวันที่น่าประทับใจมาก พวกเขาเริ่มจากการลองทานขนมขบเคี้ยวท้องถิ่นหลายชนิด รวมถึงคุกกี้รสหมูหรือไก่ที่เรียกว่า \\"คุกกี้ไก่\\" และพบว่าอร่อยมาก จากนั้นพวกเขาก็ซื้อบัตรเข้าชมสถาบันเฉินสำเร็จ แล้วไปเยี่ยมชมอาคารสวยงามแห่งนี้ เรียนรู้เกี่ยวกับประวัติศาสตร์ของครอบครัวเฉิน และชื่นชมแกะสลักและตกแต่งอันละเอียดอ่อน นอกจากนี้ยังได้ไปเยี่ยมชมวัดฮวาหยวนและเกาะซามีเอี้ยน โดยแสดงความประทับใจในวัฒนธรรมและประวัติศาสตร์ของจีน การผสมผสานระหว่างการพัฒนาสมัยใหม่และวัฒนธรรมดั้งเดิมในกว่างโจว พร้อมบรรยากาศสบายๆ พื้นที่สีเขียว และความงามของแม่น้ำเพิร์ล ทำให้พวกเขารู้สึกประทับใจอย่างลึกซึ้ง วิดีโอนี้ไม่เพียงแค่แสดงเสน่ห์ของกว่างโจวเท่านั้น แต่ยังกระตุ้นให้ผู้ชมออกไปสำรวจโลกที่ยังไม่รู้จักอีกด้วย","ms":"Pasangan dari Ireland berkongsi pengalaman petualangan mereka di Guangzhou. Dengan bantuan Vivian, mereka meneroka bandar yang sepenuhnya tidak dikenali ini dan mempunyai hari yang menakjubkan. Pertama, mereka mencuba pelbagai makanan ringan tempatan termasuk biskut rasa daging babi atau ayam yang dikenali sebagai \\"biskut ayam\\", mendapati mereka sangat lazat. Kemudian, mereka berjaya membeli tiket ke Akademi Klan Chen, melawat bangunan indah ini, belajar tentang sejarah keluarga Chen, dan mengagumi ukiran dan hiasan halusnya. Mereka juga melawat Kuil Huayan dan Pulau Shamian, mengungkapkan kagum terhadap budaya dan sejarah China. Campuran modenisasi dan budaya tradisional di Guangzhou, bersama dengan suasana santai, ruang hijau, dan keindahan Sungai Pearl meninggalkan kesan mendalam kepada mereka. Video ini bukan sahaja mempamerkan pesona Guangzhou tetapi juga mendorong penonton untuk meneroka dunia yang belum diketahui."},"description":{"root":"Discover the charm of Guangzhou: Couple's journey through food and culture. From ancient architecture to modern city life, experience the vibrant spirit!","ja":"広州の魅力を発見しましょう！アイルランド夫妻と一緒に食事と文化の旅へ出かけ、古代建築から現代都市まで、多様な顔を持つ広州の伝統、活気、そして独特な美食を堪能してください。","ko":"광주의 매력을 탐험하세요: 아일랜드 부부의 음식과 문화 여행. 고대 건축물에서 현대 도시까지 광주의 전통과 역동성을 경험해 보세요!","th":"ค้นพบมนต์ขลังของกว่างโจว: การเดินทางของคู่รักผ่านอาหารและวัฒนธรรม จากสถาปัตยกรรมโบราณจนถึงชีวิตเมืองสมัยใหม่ สัมผัสจิตวิญญาณที่มีชีวิตชีวา!","ms":"Ketahui pesona Guangzhou: Pengembaraan pasangan melalui makanan dan budaya. Dari seni bina kuno hingga kehidupan bandar moden, alami semangat yang hidup!"},"city":["Guangzhou"],"attraction":["Chen Clan Academy","Shamian Island","Hualin Temple"],"index":50,"interestLabel":"中国シティウォーク"}]}},"relativePath":"ja/index.md","filePath":"[lang]/index.md","lastUpdated":1744376818000}`),re={name:"ja/index.md"}