import{A as o}from"./chunks/ArticleCard.BjgkQzSz.js";import{Y as i,A as r}from"./chunks/YoutubePlayTo.mUujLgG2.js";import{C as s}from"./chunks/CityCard.B-UzRITV.js";import{P as u}from"./chunks/Photo.CJtI8asC.js";import{c as l,b as g,k as e,h as n,j as t}from"./chunks/framework.Dm3xLcfC.js";import"./chunks/index.Bn2AxVtY.js";import"./chunks/assets.CyscBh63.js";import"./chunks/theme.Stt0B6jc.js";import"./chunks/index.xOiVIUhm.js";import"./chunks/index.CIGM0Yps.js";const v=JSON.parse(`{"title":"한국 교육감의 퇴역 여행: 호점협과 용설산","description":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","frontmatter":{"layout":"composition","head":[["link",{"rel":"alternate","href":"https://www.etourbook.com/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"x-default"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"en"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ja/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"ja"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ko/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"ko"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/th/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"th"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ms/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","hreflang":"ms"}],["link",{"rel":"canonical","href":"https://www.etourbook.com/ko/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain"}],["meta",{"name":"title","content":"한국 교육감의 퇴역 여행: 호점협과 용설산"}],["meta",{"property":"og:title","content":"한국 교육감의 퇴역 여행: 호점협과 용설산"}],["meta",{"property":"og:description","content":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:url","content":"https://www.etourbook.com/ko/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain"}]],"title":"한국 교육감의 퇴역 여행: 호점협과 용설산","description":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","og":{"og:title":"한국 교육감의 퇴역 여행: 호점협과 용설산","og:description":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","og:type":"article"},"article":{"link":"korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","category":["Natural Scenery","Chinese Cultural Heritage","China Vlog"],"title":{"root":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","ja":"韓国教育長の退職旅：虎跳峽と玉龍雪山","ko":"한국 교육감의 퇴역 여행: 호점협과 용설산","th":"การเดินทางของอดีตผู้อำนวยการศึกษาเกาหลีสู่ช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกร","ms":"Perjalanan Ketua Pendidikan Berundur dari Korea ke Jurang Harimau Melompat dan Gunung Salji Naga Giok"},"videoId":"L8aGEwBb4uM","introduce":{"root":"After retiring as the education commissioner of South Korea, he traveled to Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan, China. During his journey, he experienced China's train culture, tasted local cuisine, visited traditional markets, and found comfortable accommodation in Lijiang. He also shared the challenges and breathtaking views of hiking up Jade Dragon Snow Mountain, along with a small tip about using sunscreen sticks. In Lachun Village, he enjoyed Korean dishes and admired the magnificent natural scenery in places like Walnut Leaf and Haoyilu. He visited Jade Dragon Snow Mountain, experienced altitude sickness, and successfully reached the summit. Throughout the trip, he immersed himself in the local culture and cuisine, forming deep friendships with the locals.","ja":"韓国教育長官が退職後、中国雲南省の虎跳峽（こびょうか）と玉龍雪山（ぎょくりゅうせつざん）へ旅行しました。旅の中で彼は中国の鉄道文化を体験し、地元の料理を味わい、伝統市場を見学し、麗江（りきょう）で快適な宿泊先を見つけました。また、玉龍雪山へのハイキングの困難さや美しい景色について語り、日焼け止めスティックを使った小さなコツも共有しました。ラ村では韓国料理を食べ、胡桃葉（ごうだよう）や好意路（こうぎろ）などで雄大な自然を楽しみました。彼は玉龍雪山を訪れ、高山病を経験しながらも無事に頂上に到達しました。旅を通じて、彼は現地の文化や料理を体験し、現地の人々との深い友情を築きました。","ko":"한국 교육감이 퇴직 후 중국 윈난성의 호점협과 용설산을 여행했습니다. 그는 여행 중에 중국 기차 문화를 체험하고 현지 음식을 맛보며 전통 시장을 방문했고, 리장에서 편안한 숙소를 찾았습니다. 또한 용설산 트레킹의 어려움과 아름다운 경치에 대해 이야기하며 자외선 차단제 스틱 사용법이라는 작은 팁도 공유했습니다. 라촌에서는 한국 요리를 먹었고, 호두엽과 해의로 같은 곳에서 웅대한 자연 풍경을 감상했습니다. 그는 용설산을 방문해 고산병을 경험했지만 결국 정상에 성공적으로 도달했습니다. 여행 동안 그는 현지 문화와 음식을 체험하고 주민들과 깊은 우정을 쌓았습니다.","th":"หลังจากเกษียณจากตำแหน่งผู้อำนวยการศึกษาของเกาหลีใต้ เขาได้เดินทางไปยังช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในมณฑลยูนนาน ประเทศจีน ในระหว่างการเดินทาง เขาได้สัมผัสวัฒนธรรมรถไฟของจีน ชิมอาหารท้องถิ่น เยี่ยมชมตลาดแบบดั้งเดิม และพบที่พักสบายๆ ในเมืองลี่เจียง เขายังแบ่งปันความท้าทายและความสวยงามของการเดินป่าขึ้นภูเขาน้ำแข็งหยกมังกร พร้อมคำแนะนำเล็กๆ น้อยๆ เกี่ยวกับการใช้แท่งครีมกันแดด ในหมู่บ้านลาชุน เขาได้เพลิดเพลินกับอาหารเกาหลีและชื่นชมทิวทัศน์ธรรมชาติอันงดงามที่สถานที่อย่างใบมะรุมและถนนเฮาอี้ลู่ เขาได้เยี่ยมชมภูเขาน้ำแข็งหยกมังกร ประสบกับอาการแพ้ความสูง และประสบความสำเร็จในการขึ้นถึงยอดเขา ตลอดการเดินทาง เขาได้สัมผัสกับวัฒนธรรมและอาหารท้องถิ่น และสร้างมิตรภาพที่ลึกซึ้งกับชาวบ้าน","ms":"Selepas bersara sebagai Pengarah Pendidikan Korea Selatan, beliau telah melancong ke Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan, China. Semasa perjalanannya, beliau mengalami budaya kereta api China, merasai masakan tempatan, melawat pasar tradisional, dan menemui penginapan yang selesa di Lijiang. Beliau juga berkongsi cabaran dan pemandangan menakjubkan semasa mendaki Gunung Salji Naga Giok, bersama dengan sedikit petua tentang menggunakan batang pelindung matahari. Di Kampung Lachun, beliau menikmati hidangan Korea dan memuji pemandangan alam semula jadi yang megah di tempat-tempat seperti Daun Kenari dan Jalan HaoYiLu. Beliau telah mengunjungi Gunung Salji Naga Giok, mengalami penyakit ketinggian, dan berjaya mencapai puncak gunung tersebut. Sepanjang perjalanan, beliau terbenam dalam budaya dan masakan tempatan, membina persahabatan yang mendalam dengan penduduk tempatan."},"description":{"root":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","ja":"中国雲南省の虎跳峽や玉龍雪山を巡る壮大な旅に参加しましょう。韓国教育長官も退職後楽しんだ冒険を通じ、地元の鉄道文化、美食、絶景を体験。ハイキングのコツもお届けします！","ko":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","th":"เข้าร่วมการผจญภัยที่น่าตื่นตาตื่นใจผ่านช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในยูนนาน สำรวจทิวทัศน์ที่งดงามและสัมผัสวัฒนธรรมท้องถิ่น อาหารอร่อย และเทคนิคการเดินป่า","ms":"Sertai Sugilway dalam pengembaraan menakjubkan melalui Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan. Alami landskap yang menakjubkan dan sertai pengalaman budaya tempatan, makanan lazat, serta tips pendakian."},"city":["Lijiang"],"attraction":["Shuhe Ancient Town","Tiger Leaping Gorge","Yulong Snow Mountain","Lijiang Ancient Town"],"preferLang":"ko","index":60},"attractions":[{"link":"lijiang-ancient-town","name":"Lijiang Ancient Town","nameChinese":"丽江","namePinyin":"lì jiāng","city":"Lijiang","location":"26.876096896416577, 100.23341124532006","rating":4.7,"ticketPrice":0,"opensHour":[0,24],"visitDuration":[2,3],"address":{"root":"50 meters south of the intersection of Xinhua Street and Jinhong Road, Gucheng District, Lijiang City","ja":null},"nameLocale":{"root":"Lijiang Ancient Town","ja":"丽江古城","ko":"리장고성","th":"เมืองโบราณลี่เจียง","ms":"Bandar Lijiang Kuno"},"introduce":{"root":"Canals & Naxi culture in UNESCO old town.","ja":"水路の街、世界遺産の古都。","ko":"유네스코 세계유산에 등재된 수로와 나시 문화의 고성.","th":"เมืองโบราณที่มีคลองและวัฒนธรรมนาซี ในพื้นที่มรดกโลกยูเนสโก","ms":"Kanil dan budaya Naxi di bandar kuno UNESCO."},"description":{"root":"Explore Lijiang Ancient Town, a UNESCO site known as the 'Venice of the Orient.' Discover its 800-year history, canals, and unique architecture. (Discover Lijiang Ancient Town, a UNESCO World Heritage site often called the 'Venice of the East,' featuring 800 years of history, intricate canals, and distinctive architecture.)","ja":"世界遺産に登録された「東洋のヴェネツィア」として知られる麗江古城を訪れてみませんか。800年にわたる歴史、独特な建築様式、そして複雑な運河システムが魅力です。","ko":"유네스코 세계유산으로 알려진 '동양의 베니스' 리장고성을 탐험하세요. 800년의 역사를 가진 이곳에는 복잡한 수로와 독특한 건축물이 있습니다!","th":"สำรวจเมืองโบราณลี่เจียง แหล่งมรดกโลกของยูเนสโกที่ได้รับสมญาว่า \\"เวนิสแห่งตะวันออก\\" มีประวัติศาสตร์ยาวนาน 800 ปี คลองที่ซับซ้อน และสถาปัตยกรรมที่เป็นเอกลักษณ์","ms":"Lawati Bandar Lijiang Kuno, tapak Warisan Dunia UNESCO yang dikenali sebagai \\"Venice Timur.\\" Ia menampilkan sejarah selama 800 tahun, saliran rumit, dan seni bina unik."},"review":{"root":"In the old town, small lanes and streams crisscross, and every little path is worth exploring. However, it's quite crowded with a strong commercial vibe.","ja":"古い町では小さな道と小川が複雑に絡んでいて、どの小道も探してみる価値があります。でも、観光客が多くて商業的な感じが強いです。","ko":"고성 안에는 작은 길과 개울이 얽혀 있어서 모든 골목이 탐험할 만해요. 하지만 관광객이 많고 상업적인 느낌이 강해요.","th":"ในเมืองโบราณ มีถนนเล็ก ๆ และลำธารที่ข้ามกันไปมา ทุกเส้นทางมีค่าแก่การสำรวจ แต่มันค่อนข้างแออัดและมีบรรยากาศเชิงพาณิชย์ค่อนข้างมาก","ms":"Di dalam bandar lama, lorong-lorong kecil dan anak sungai bersilang, dan setiap jalan kecil mempunyai nilai penjelajahan tersendiri. Walau bagaimanapun, ia agak sesak dengan suasana komersial yang kuat."}},{"link":"shuhe-ancient-town","name":"Shuhe Ancient Town","nameChinese":"束河","namePinyin":"shù hé","city":"Lijiang","nameLocale":{"root":"Shuhe Ancient Town","ja":"束河古镇","ko":"수허고성","th":"เมืองโบราณซูเหอ","ms":"Bandar Shuhe Kuno"},"introduce":{"root":"Tea Horse Road gem with Naxi traditions.","ja":"茶馬古道の要衝、ナシ族の町。","ko":"차마고도의 중요한 위치를 차지하는 나시족의 고성.","th":"เมืองที่สำคัญบนเส้นทางชามาเก่าแก่ และเป็นแหล่งวัฒนธรรมนาซี","ms":"Gemerlapan di Laluan Teh Kuda dengan tradisi Naxi."},"description":{"root":"Visit Shuhe Ancient Town, a well-preserved stop on the historic Tea Horse Road. Admire Jade Dragon Snow Mountain and embrace Naxi culture. (Discover Shuhe Ancient Town, a beautifully preserved site along the historic Tea Horse Road. Enjoy views of Jade Dragon Snow Mountain and immerse yourself in Naxi culture.)","ja":"麗江北部の束河古镇へ訪れてみませんか。煎茶馬古道の要所で、ナシ族文化の「生きた化石」。玉龍雪山の美景や、静かな古城の夜を満喫できる特別な場所です。","ko":"수허고성을 방문하세요. 역사적인 차마고도에 잘 보존된 장소로, 옥룡설산의 아름다운 경치와 함께 나시 문화를 체험할 수 있는 특별한 장소입니다!","th":"สำรวจเมืองโบราณซูเหอ เมืองที่ได้รับการอนุรักษ์อย่างดีบนเส้นทางชามาเก่าแก่ ชมความงามของภูเขาหิมะมังกรหยกและสัมผัสวัฒนธรรมนาซี","ms":"Lawati Bandar Shuhe Kuno, sebuah tapak yang terpelihara dengan baik di sepanjang Laluan Teh Kuda bersejarah. Nikmati pemandangan Gunung Salji Naga Giok dan tenggelam dalam budaya Naxi."},"review":{"root":"In Shuhe, you can enjoy the quiet beauty of the old town and its surrounding landscapes. It's less crowded than Lijiang but still offers plenty to see and do.","ja":"束河では、古い町とその周辺の美しい景観を楽しむことができます。麗江よりも静かですが、見どころがたくさんあります。","ko":"수허에서는 고성과 그 주변의 아름다운 풍경을 조용히 즐길 수 있습니다. 리장보다 덜 붐비지만, 볼거리가 많아요.","th":"ที่ซูเหอ คุณสามารถเพลิดเพลินกับความสวยงามเงียบสงบของเมืองโบราณและทัศนียภาพโดยรอบ มันไม่พลุกพล่านเท่าลี่เจียงแต่ยังคงมีสถานที่ท่องเที่ยวให้ดูมากมาย","ms":"Di Shuhe, anda boleh menikmati keindahan tenang bandar lama dan landskap sekitarnya. Ia kurang sesak berbanding Lijiang tetapi masih menawarkan banyak perkara untuk dilihat dan dilakukan."},"location":"26.91652881591715, 100.20180535253954","rating":4.5,"ticketPrice":0,"opensHour":[0,24],"visitDuration":[1,2],"address":{"root":"Shuhe Road, Gucheng District, Lijiang City","ja":null}},{"link":"tiger-leaping-gorge","name":"Tiger Leaping Gorge","nameChinese":"虎跳峡","namePinyin":"hǔ tiào xiá","city":"Lijiang","nameLocale":{"root":"Tiger Leaping Gorge","ja":"虎跳峡","ko":"호조협","th":"ช่องเขาเสือกระโดด","ms":"Tekukur Harimau"},"introduce":{"root":"World's deepest canyon with raging rapids.","ja":"世界最深の峡谷、激流あり。","ko":"세계에서 가장 깊은 협곡 중 하나로, 격류가 흐르는 곳.","th":"ช่องเขาที่ลึกที่สุดในโลก มีกระแสน้ำเชี่ยวกราก","ms":"Lembah terdalam di dunia dengan aliran deras."},"description":{"root":"Hike Tiger Leaping Gorge, one of the world’s deepest canyons near Lijiang. Marvel at breathtaking views of Jade Dragon and Haba Snow Mountains. (Explore Tiger Leaping Gorge, one of the world’s deepest canyons near Lijiang. Enjoy stunning views of Jade Dragon and Haba Snow Mountains.)","ja":"世界最深の峡谷之一・虎跳峽と「長江第一湾」を訪れてみませんか。麗江の玉龍雪山と哈巴雪山に囲まれた絶景地で、標識付きトレッキングコースで大自然の壮大さを満喫できます。","ko":"리장 근처 세계에서 가장 깊은 협곡 중 하나인 호조협을 트레킹하세요. 옥룡설산과 하바설산의 장엄한 경치를 감상할 수 있습니다!","th":"สำรวจช่องเขาเสือกระโดด หนึ่งในช่องเขาที่ลึกที่สุดในโลกใกล้กับเมืองลี่เจียง เพลิดเพลินกับทัศนียภาพอันตระการตาของภูเขาหิมะมังกรหยกและฮาบ่า","ms":"Lawati Tekukur Harimau, salah satu lembah terdalam di dunia berhampiran Lijiang. Nikmati pemandangan menakjubkan Gunung Salji Naga Giok dan Haba."},"review":{"root":"The trek is challenging but rewarding, offering incredible views of the gorge and surrounding mountains. Be prepared for steep paths and rugged terrain.","ja":"トレッキングはチャレンジングですが、その見返りは素晴らしいです。峡谷や周辺の山々の絶景が待っています。急な坂道や険しい地形に備えてください。","ko":"트레킹은 도전적이지만 보람차며, 협곡과 주변 산들의 놀라운 경치를 제공합니다. 가파른 길과 거친 지형에 대비하세요.","th":"การเดินป่าท้าทายแต่คุ้มค่ามาก โดยให้วิวที่น่าทึ่งของช่องเขาและเทือกเขาโดยรอบ ควรเตรียมพร้อมสำหรับทางลาดชันและพื้นที่ขรุขระ","ms":"Pendakian adalah mencabar tetapi memberi ganjaran, menawarkan pemandangan luar biasa dari tebing dan gunung-gunung sekitarnya. Bersiap sedia untuk laluan curam dan medan yang kasar."},"location":"27.174093200598822, 100.09885439940953","ticketPrice":73,"rating":4.6,"opensHour":[8.5,17.5],"visitDuration":[2,3],"address":{"root":"Tiger Leaping Gorge(Hutiaoxia Town), Shangri-La County, Diqing Prefecture","ja":null}},{"link":"yulong-snow-moutain","name":"Yulong Snow Mountain","nameChinese":"玉龙雪山","namePinyin":"yù lóng xuě shān","city":"Lijiang","nameLocale":{"root":"Yulong Snow Mountain","ja":"玉龙雪山","ko":"옥룡설산","th":"ภูเขาหิมะหยูหลง","ms":"Gunung Salji Yulong"},"introduce":{"root":"Sacred snow-capped peaks & glacier adventures.","ja":"聖なる雪山と氷河の公園。","ko":"신성한 설산과 빙하 모험의 장소.","th":"ยอดเขาศักดิ์สิทธิ์และผจญภัยบนธารน้ำแข็ง","ms":"Puncak bersalji yang suci dan pengembaraan glasier."},"description":{"root":"Climb Yulong Snow Mountain, a sacred peak of the Naxi people in Lijiang. Explore Glacier Park and admire its snow-capped summits. (Discover Yulong Snow Mountain, a sacred peak revered by the Naxi people in Lijiang. Explore the Glacier Park and marvel at its snow-covered summits.)","ja":"麗江の世界遺産・玉龍雪山を訪れてみませんか。ナシ族の聖山で、氷河公園を散策し、13の雪覆い峰々や標高5,596メートルの扇子陡からの絶景をぜひお楽しみください。","ko":"리장에 위치한 나시족의 성산인 옥룡설산을 방문하세요. 빙하공원을 탐험하고 눈덮힌 산봉우리를 감상할 수 있습니다!","th":"สำรวจภูเขาหิมะหยูหลง ยอดเขาศักดิ์สิทธิ์ของชนชาตินาซีในลี่เจียง ชมสวนธารน้ำแข็งและทัศนียภาพอันตระการตาของยอดเขาที่ปกคลุมไปด้วยหิมะ","ms":"Lawati Gunung Salji Yulong, puncak suci bagi etnik Naxi di Lijiang. Eksplorasi Taman Glasier dan kagumi puncak-puncak berlapis salji."},"review":{"root":"The views from the mountain are absolutely stunning, especially when you reach the higher elevations. Be prepared for some physical exertion, but it's well worth it.","ja":"山からの眺めは絶対に素晴らしいです。特に高い場所に到達したときには言葉を失います。体力を使うことを覚悟してください。しかし、それだけの価値があります。","ko":"산에서 내려다보는 풍경은 정말 놀랍습니다, 특히 고도가 높아질수록 더욱 그렇습니다. 체력 소모를 각오해야 하지만, 그만한 가치가 있어요.","th":"วิวจากยอดเขานั้นสวยงามอย่างยิ่ง โดยเฉพาะเมื่อคุณไปถึงบริเวณที่สูงขึ้น ควรเตรียมพร้อมสำหรับการใช้แรงงานทางกาย แต่มันคุ้มค่าแน่นอน","ms":"Pemandangan dari gunung adalah luar biasa, terutama apabila anda mencapai ketinggian yang lebih tinggi. Bersiap sedia untuk beberapa usaha fizikal, tetapi ia sangat berbaloi."},"location":"27.1014838299386, 100.19730515172101","ticketPrice":60,"rating":4.5,"opensHour":[7.5,16.5],"visitDuration":8,"address":{"root":"15 km north of Lijiang Old Town, Yulong County, Lijiang City","ja":null}}]},"headers":[],"params":{"lang":"ko","article":"korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","frontmatter":{"title":"한국 교육감의 퇴역 여행: 호점협과 용설산","description":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","og":{"og:title":"한국 교육감의 퇴역 여행: 호점협과 용설산","og:description":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","og:type":"article"},"article":{"link":"korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain","category":["Natural Scenery","Chinese Cultural Heritage","China Vlog"],"title":{"root":"A Retired Korean Education Chief's Journey to Tiger Leaping Gorge and Jade Dragon Snow Mountain","ja":"韓国教育長の退職旅：虎跳峽と玉龍雪山","ko":"한국 교육감의 퇴역 여행: 호점협과 용설산","th":"การเดินทางของอดีตผู้อำนวยการศึกษาเกาหลีสู่ช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกร","ms":"Perjalanan Ketua Pendidikan Berundur dari Korea ke Jurang Harimau Melompat dan Gunung Salji Naga Giok"},"videoId":"L8aGEwBb4uM","introduce":{"root":"After retiring as the education commissioner of South Korea, he traveled to Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan, China. During his journey, he experienced China's train culture, tasted local cuisine, visited traditional markets, and found comfortable accommodation in Lijiang. He also shared the challenges and breathtaking views of hiking up Jade Dragon Snow Mountain, along with a small tip about using sunscreen sticks. In Lachun Village, he enjoyed Korean dishes and admired the magnificent natural scenery in places like Walnut Leaf and Haoyilu. He visited Jade Dragon Snow Mountain, experienced altitude sickness, and successfully reached the summit. Throughout the trip, he immersed himself in the local culture and cuisine, forming deep friendships with the locals.","ja":"韓国教育長官が退職後、中国雲南省の虎跳峽（こびょうか）と玉龍雪山（ぎょくりゅうせつざん）へ旅行しました。旅の中で彼は中国の鉄道文化を体験し、地元の料理を味わい、伝統市場を見学し、麗江（りきょう）で快適な宿泊先を見つけました。また、玉龍雪山へのハイキングの困難さや美しい景色について語り、日焼け止めスティックを使った小さなコツも共有しました。ラ村では韓国料理を食べ、胡桃葉（ごうだよう）や好意路（こうぎろ）などで雄大な自然を楽しみました。彼は玉龍雪山を訪れ、高山病を経験しながらも無事に頂上に到達しました。旅を通じて、彼は現地の文化や料理を体験し、現地の人々との深い友情を築きました。","ko":"한국 교육감이 퇴직 후 중국 윈난성의 호점협과 용설산을 여행했습니다. 그는 여행 중에 중국 기차 문화를 체험하고 현지 음식을 맛보며 전통 시장을 방문했고, 리장에서 편안한 숙소를 찾았습니다. 또한 용설산 트레킹의 어려움과 아름다운 경치에 대해 이야기하며 자외선 차단제 스틱 사용법이라는 작은 팁도 공유했습니다. 라촌에서는 한국 요리를 먹었고, 호두엽과 해의로 같은 곳에서 웅대한 자연 풍경을 감상했습니다. 그는 용설산을 방문해 고산병을 경험했지만 결국 정상에 성공적으로 도달했습니다. 여행 동안 그는 현지 문화와 음식을 체험하고 주민들과 깊은 우정을 쌓았습니다.","th":"หลังจากเกษียณจากตำแหน่งผู้อำนวยการศึกษาของเกาหลีใต้ เขาได้เดินทางไปยังช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในมณฑลยูนนาน ประเทศจีน ในระหว่างการเดินทาง เขาได้สัมผัสวัฒนธรรมรถไฟของจีน ชิมอาหารท้องถิ่น เยี่ยมชมตลาดแบบดั้งเดิม และพบที่พักสบายๆ ในเมืองลี่เจียง เขายังแบ่งปันความท้าทายและความสวยงามของการเดินป่าขึ้นภูเขาน้ำแข็งหยกมังกร พร้อมคำแนะนำเล็กๆ น้อยๆ เกี่ยวกับการใช้แท่งครีมกันแดด ในหมู่บ้านลาชุน เขาได้เพลิดเพลินกับอาหารเกาหลีและชื่นชมทิวทัศน์ธรรมชาติอันงดงามที่สถานที่อย่างใบมะรุมและถนนเฮาอี้ลู่ เขาได้เยี่ยมชมภูเขาน้ำแข็งหยกมังกร ประสบกับอาการแพ้ความสูง และประสบความสำเร็จในการขึ้นถึงยอดเขา ตลอดการเดินทาง เขาได้สัมผัสกับวัฒนธรรมและอาหารท้องถิ่น และสร้างมิตรภาพที่ลึกซึ้งกับชาวบ้าน","ms":"Selepas bersara sebagai Pengarah Pendidikan Korea Selatan, beliau telah melancong ke Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan, China. Semasa perjalanannya, beliau mengalami budaya kereta api China, merasai masakan tempatan, melawat pasar tradisional, dan menemui penginapan yang selesa di Lijiang. Beliau juga berkongsi cabaran dan pemandangan menakjubkan semasa mendaki Gunung Salji Naga Giok, bersama dengan sedikit petua tentang menggunakan batang pelindung matahari. Di Kampung Lachun, beliau menikmati hidangan Korea dan memuji pemandangan alam semula jadi yang megah di tempat-tempat seperti Daun Kenari dan Jalan HaoYiLu. Beliau telah mengunjungi Gunung Salji Naga Giok, mengalami penyakit ketinggian, dan berjaya mencapai puncak gunung tersebut. Sepanjang perjalanan, beliau terbenam dalam budaya dan masakan tempatan, membina persahabatan yang mendalam dengan penduduk tempatan."},"description":{"root":"Join Sugilway on breathtaking adventures through Tiger Leaping Gorge and Jade Dragon Snow Mountain in Yunnan. Experience the stunning landscapes.","ja":"中国雲南省の虎跳峽や玉龍雪山を巡る壮大な旅に参加しましょう。韓国教育長官も退職後楽しんだ冒険を通じ、地元の鉄道文化、美食、絶景を体験。ハイキングのコツもお届けします！","ko":"Sugilway와 함께 중국 윈난성의 호점협과 용설산을 여행하며 아름다운 자연과 현지 문화를 경험해보세요. 한국 교육감의 퇴역 후 여정에서 기차 문화, 지역 음식, 등산 팁 등을 만나보세요!","th":"เข้าร่วมการผจญภัยที่น่าตื่นตาตื่นใจผ่านช่องเขาเสือกระโดดและภูเขาน้ำแข็งหยกมังกรในยูนนาน สำรวจทิวทัศน์ที่งดงามและสัมผัสวัฒนธรรมท้องถิ่น อาหารอร่อย และเทคนิคการเดินป่า","ms":"Sertai Sugilway dalam pengembaraan menakjubkan melalui Jurang Harimau Melompat dan Gunung Salji Naga Giok di Yunnan. Alami landskap yang menakjubkan dan sertai pengalaman budaya tempatan, makanan lazat, serta tips pendakian."},"city":["Lijiang"],"attraction":["Shuhe Ancient Town","Tiger Leaping Gorge","Yulong Snow Mountain","Lijiang Ancient Town"],"preferLang":"ko","index":60},"attractions":[{"link":"lijiang-ancient-town","name":"Lijiang Ancient Town","nameChinese":"丽江","namePinyin":"lì jiāng","city":"Lijiang","location":"26.876096896416577, 100.23341124532006","rating":4.7,"ticketPrice":0,"opensHour":[0,24],"visitDuration":[2,3],"address":{"root":"50 meters south of the intersection of Xinhua Street and Jinhong Road, Gucheng District, Lijiang City","ja":null},"nameLocale":{"root":"Lijiang Ancient Town","ja":"丽江古城","ko":"리장고성","th":"เมืองโบราณลี่เจียง","ms":"Bandar Lijiang Kuno"},"introduce":{"root":"Canals & Naxi culture in UNESCO old town.","ja":"水路の街、世界遺産の古都。","ko":"유네스코 세계유산에 등재된 수로와 나시 문화의 고성.","th":"เมืองโบราณที่มีคลองและวัฒนธรรมนาซี ในพื้นที่มรดกโลกยูเนสโก","ms":"Kanil dan budaya Naxi di bandar kuno UNESCO."},"description":{"root":"Explore Lijiang Ancient Town, a UNESCO site known as the 'Venice of the Orient.' Discover its 800-year history, canals, and unique architecture. (Discover Lijiang Ancient Town, a UNESCO World Heritage site often called the 'Venice of the East,' featuring 800 years of history, intricate canals, and distinctive architecture.)","ja":"世界遺産に登録された「東洋のヴェネツィア」として知られる麗江古城を訪れてみませんか。800年にわたる歴史、独特な建築様式、そして複雑な運河システムが魅力です。","ko":"유네스코 세계유산으로 알려진 '동양의 베니스' 리장고성을 탐험하세요. 800년의 역사를 가진 이곳에는 복잡한 수로와 독특한 건축물이 있습니다!","th":"สำรวจเมืองโบราณลี่เจียง แหล่งมรดกโลกของยูเนสโกที่ได้รับสมญาว่า \\"เวนิสแห่งตะวันออก\\" มีประวัติศาสตร์ยาวนาน 800 ปี คลองที่ซับซ้อน และสถาปัตยกรรมที่เป็นเอกลักษณ์","ms":"Lawati Bandar Lijiang Kuno, tapak Warisan Dunia UNESCO yang dikenali sebagai \\"Venice Timur.\\" Ia menampilkan sejarah selama 800 tahun, saliran rumit, dan seni bina unik."},"review":{"root":"In the old town, small lanes and streams crisscross, and every little path is worth exploring. However, it's quite crowded with a strong commercial vibe.","ja":"古い町では小さな道と小川が複雑に絡んでいて、どの小道も探してみる価値があります。でも、観光客が多くて商業的な感じが強いです。","ko":"고성 안에는 작은 길과 개울이 얽혀 있어서 모든 골목이 탐험할 만해요. 하지만 관광객이 많고 상업적인 느낌이 강해요.","th":"ในเมืองโบราณ มีถนนเล็ก ๆ และลำธารที่ข้ามกันไปมา ทุกเส้นทางมีค่าแก่การสำรวจ แต่มันค่อนข้างแออัดและมีบรรยากาศเชิงพาณิชย์ค่อนข้างมาก","ms":"Di dalam bandar lama, lorong-lorong kecil dan anak sungai bersilang, dan setiap jalan kecil mempunyai nilai penjelajahan tersendiri. Walau bagaimanapun, ia agak sesak dengan suasana komersial yang kuat."}},{"link":"shuhe-ancient-town","name":"Shuhe Ancient Town","nameChinese":"束河","namePinyin":"shù hé","city":"Lijiang","nameLocale":{"root":"Shuhe Ancient Town","ja":"束河古镇","ko":"수허고성","th":"เมืองโบราณซูเหอ","ms":"Bandar Shuhe Kuno"},"introduce":{"root":"Tea Horse Road gem with Naxi traditions.","ja":"茶馬古道の要衝、ナシ族の町。","ko":"차마고도의 중요한 위치를 차지하는 나시족의 고성.","th":"เมืองที่สำคัญบนเส้นทางชามาเก่าแก่ และเป็นแหล่งวัฒนธรรมนาซี","ms":"Gemerlapan di Laluan Teh Kuda dengan tradisi Naxi."},"description":{"root":"Visit Shuhe Ancient Town, a well-preserved stop on the historic Tea Horse Road. Admire Jade Dragon Snow Mountain and embrace Naxi culture. (Discover Shuhe Ancient Town, a beautifully preserved site along the historic Tea Horse Road. Enjoy views of Jade Dragon Snow Mountain and immerse yourself in Naxi culture.)","ja":"麗江北部の束河古镇へ訪れてみませんか。煎茶馬古道の要所で、ナシ族文化の「生きた化石」。玉龍雪山の美景や、静かな古城の夜を満喫できる特別な場所です。","ko":"수허고성을 방문하세요. 역사적인 차마고도에 잘 보존된 장소로, 옥룡설산의 아름다운 경치와 함께 나시 문화를 체험할 수 있는 특별한 장소입니다!","th":"สำรวจเมืองโบราณซูเหอ เมืองที่ได้รับการอนุรักษ์อย่างดีบนเส้นทางชามาเก่าแก่ ชมความงามของภูเขาหิมะมังกรหยกและสัมผัสวัฒนธรรมนาซี","ms":"Lawati Bandar Shuhe Kuno, sebuah tapak yang terpelihara dengan baik di sepanjang Laluan Teh Kuda bersejarah. Nikmati pemandangan Gunung Salji Naga Giok dan tenggelam dalam budaya Naxi."},"review":{"root":"In Shuhe, you can enjoy the quiet beauty of the old town and its surrounding landscapes. It's less crowded than Lijiang but still offers plenty to see and do.","ja":"束河では、古い町とその周辺の美しい景観を楽しむことができます。麗江よりも静かですが、見どころがたくさんあります。","ko":"수허에서는 고성과 그 주변의 아름다운 풍경을 조용히 즐길 수 있습니다. 리장보다 덜 붐비지만, 볼거리가 많아요.","th":"ที่ซูเหอ คุณสามารถเพลิดเพลินกับความสวยงามเงียบสงบของเมืองโบราณและทัศนียภาพโดยรอบ มันไม่พลุกพล่านเท่าลี่เจียงแต่ยังคงมีสถานที่ท่องเที่ยวให้ดูมากมาย","ms":"Di Shuhe, anda boleh menikmati keindahan tenang bandar lama dan landskap sekitarnya. Ia kurang sesak berbanding Lijiang tetapi masih menawarkan banyak perkara untuk dilihat dan dilakukan."},"location":"26.91652881591715, 100.20180535253954","rating":4.5,"ticketPrice":0,"opensHour":[0,24],"visitDuration":[1,2],"address":{"root":"Shuhe Road, Gucheng District, Lijiang City","ja":null}},{"link":"tiger-leaping-gorge","name":"Tiger Leaping Gorge","nameChinese":"虎跳峡","namePinyin":"hǔ tiào xiá","city":"Lijiang","nameLocale":{"root":"Tiger Leaping Gorge","ja":"虎跳峡","ko":"호조협","th":"ช่องเขาเสือกระโดด","ms":"Tekukur Harimau"},"introduce":{"root":"World's deepest canyon with raging rapids.","ja":"世界最深の峡谷、激流あり。","ko":"세계에서 가장 깊은 협곡 중 하나로, 격류가 흐르는 곳.","th":"ช่องเขาที่ลึกที่สุดในโลก มีกระแสน้ำเชี่ยวกราก","ms":"Lembah terdalam di dunia dengan aliran deras."},"description":{"root":"Hike Tiger Leaping Gorge, one of the world’s deepest canyons near Lijiang. Marvel at breathtaking views of Jade Dragon and Haba Snow Mountains. (Explore Tiger Leaping Gorge, one of the world’s deepest canyons near Lijiang. Enjoy stunning views of Jade Dragon and Haba Snow Mountains.)","ja":"世界最深の峡谷之一・虎跳峽と「長江第一湾」を訪れてみませんか。麗江の玉龍雪山と哈巴雪山に囲まれた絶景地で、標識付きトレッキングコースで大自然の壮大さを満喫できます。","ko":"리장 근처 세계에서 가장 깊은 협곡 중 하나인 호조협을 트레킹하세요. 옥룡설산과 하바설산의 장엄한 경치를 감상할 수 있습니다!","th":"สำรวจช่องเขาเสือกระโดด หนึ่งในช่องเขาที่ลึกที่สุดในโลกใกล้กับเมืองลี่เจียง เพลิดเพลินกับทัศนียภาพอันตระการตาของภูเขาหิมะมังกรหยกและฮาบ่า","ms":"Lawati Tekukur Harimau, salah satu lembah terdalam di dunia berhampiran Lijiang. Nikmati pemandangan menakjubkan Gunung Salji Naga Giok dan Haba."},"review":{"root":"The trek is challenging but rewarding, offering incredible views of the gorge and surrounding mountains. Be prepared for steep paths and rugged terrain.","ja":"トレッキングはチャレンジングですが、その見返りは素晴らしいです。峡谷や周辺の山々の絶景が待っています。急な坂道や険しい地形に備えてください。","ko":"트레킹은 도전적이지만 보람차며, 협곡과 주변 산들의 놀라운 경치를 제공합니다. 가파른 길과 거친 지형에 대비하세요.","th":"การเดินป่าท้าทายแต่คุ้มค่ามาก โดยให้วิวที่น่าทึ่งของช่องเขาและเทือกเขาโดยรอบ ควรเตรียมพร้อมสำหรับทางลาดชันและพื้นที่ขรุขระ","ms":"Pendakian adalah mencabar tetapi memberi ganjaran, menawarkan pemandangan luar biasa dari tebing dan gunung-gunung sekitarnya. Bersiap sedia untuk laluan curam dan medan yang kasar."},"location":"27.174093200598822, 100.09885439940953","ticketPrice":73,"rating":4.6,"opensHour":[8.5,17.5],"visitDuration":[2,3],"address":{"root":"Tiger Leaping Gorge(Hutiaoxia Town), Shangri-La County, Diqing Prefecture","ja":null}},{"link":"yulong-snow-moutain","name":"Yulong Snow Mountain","nameChinese":"玉龙雪山","namePinyin":"yù lóng xuě shān","city":"Lijiang","nameLocale":{"root":"Yulong Snow Mountain","ja":"玉龙雪山","ko":"옥룡설산","th":"ภูเขาหิมะหยูหลง","ms":"Gunung Salji Yulong"},"introduce":{"root":"Sacred snow-capped peaks & glacier adventures.","ja":"聖なる雪山と氷河の公園。","ko":"신성한 설산과 빙하 모험의 장소.","th":"ยอดเขาศักดิ์สิทธิ์และผจญภัยบนธารน้ำแข็ง","ms":"Puncak bersalji yang suci dan pengembaraan glasier."},"description":{"root":"Climb Yulong Snow Mountain, a sacred peak of the Naxi people in Lijiang. Explore Glacier Park and admire its snow-capped summits. (Discover Yulong Snow Mountain, a sacred peak revered by the Naxi people in Lijiang. Explore the Glacier Park and marvel at its snow-covered summits.)","ja":"麗江の世界遺産・玉龍雪山を訪れてみませんか。ナシ族の聖山で、氷河公園を散策し、13の雪覆い峰々や標高5,596メートルの扇子陡からの絶景をぜひお楽しみください。","ko":"리장에 위치한 나시족의 성산인 옥룡설산을 방문하세요. 빙하공원을 탐험하고 눈덮힌 산봉우리를 감상할 수 있습니다!","th":"สำรวจภูเขาหิมะหยูหลง ยอดเขาศักดิ์สิทธิ์ของชนชาตินาซีในลี่เจียง ชมสวนธารน้ำแข็งและทัศนียภาพอันตระการตาของยอดเขาที่ปกคลุมไปด้วยหิมะ","ms":"Lawati Gunung Salji Yulong, puncak suci bagi etnik Naxi di Lijiang. Eksplorasi Taman Glasier dan kagumi puncak-puncak berlapis salji."},"review":{"root":"The views from the mountain are absolutely stunning, especially when you reach the higher elevations. Be prepared for some physical exertion, but it's well worth it.","ja":"山からの眺めは絶対に素晴らしいです。特に高い場所に到達したときには言葉を失います。体力を使うことを覚悟してください。しかし、それだけの価値があります。","ko":"산에서 내려다보는 풍경은 정말 놀랍습니다, 특히 고도가 높아질수록 더욱 그렇습니다. 체력 소모를 각오해야 하지만, 그만한 가치가 있어요.","th":"วิวจากยอดเขานั้นสวยงามอย่างยิ่ง โดยเฉพาะเมื่อคุณไปถึงบริเวณที่สูงขึ้น ควรเตรียมพร้อมสำหรับการใช้แรงงานทางกาย แต่มันคุ้มค่าแน่นอน","ms":"Pemandangan dari gunung adalah luar biasa, terutama apabila anda mencapai ketinggian yang lebih tinggi. Bersiap sedia untuk beberapa usaha fizikal, tetapi ia sangat berbaloi."},"location":"27.1014838299386, 100.19730515172101","ticketPrice":60,"rating":4.5,"opensHour":[7.5,16.5],"visitDuration":8,"address":{"root":"15 km north of Lijiang Old Town, Yulong County, Lijiang City","ja":null}}]}},"relativePath":"ko/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain.md","filePath":"[lang]/articles/[article].md","lastUpdated":1739695371000}`),d={name:"ko/articles/korean-edu-chief-retirement-trip-tiger-gorge-dragon-snow-mountain.md"},T=Object.assign(d,{setup(m){return(h,a)=>(g(),l("div",null,[e(s,{name:"Lijiang"}),a[0]||(a[0]=n("h2",{id:"다리에서-리장까지-중국-기차-여행-체험",tabindex:"-1"},[t("다리에서 리장까지: 중국 기차 여행 체험 "),n("a",{class:"header-anchor",href:"#다리에서-리장까지-중국-기차-여행-체험","aria-label":'Permalink to "다리에서 리장까지: 중국 기차 여행 체험"'},"​")],-1)),e(i,{to:"0:00"}),a[1]||(a[1]=n("p",null,"다리에서 리장까지의 기차 여행은 독특한 경험으로 가득 찬 여행입니다. 이 길을 따라 창산 산과 얼하이 호수의 장엄한 풍경을 즐길 수 있으며, 윈난성의 고유한 민족 문화를 체험할 수 있습니다. 기차 안에서는 현지 승객들과 교류하며 그들의 생활 방식과 문화 전통에 대해 배울 수 있습니다. 또한 기차에서 제공되는 식사는 윈난의 맛있는 음식을 맛볼 수 있는 기회를 제공합니다. 이 여행은 단순히 두 아름다운 도시를 연결하는 것뿐만 아니라 중국의 기차 문화를 더 깊이 이해하는 데도 도움이 됩니다.",-1)),a[2]||(a[2]=n("h2",{id:"술루-구-진-소란스러움을-벗어나-평온함을-찾는-곳-리장-고진보다-조용한-곳",tabindex:"-1"},[t("술루 구 진: 소란스러움을 벗어나 평온함을 찾는 곳, 리장 고진보다 조용한 곳 "),n("a",{class:"header-anchor",href:"#술루-구-진-소란스러움을-벗어나-평온함을-찾는-곳-리장-고진보다-조용한-곳","aria-label":'Permalink to "술루 구 진: 소란스러움을 벗어나 평온함을 찾는 곳, 리장 고진보다 조용한 곳"'},"​")],-1)),e(i,{to:"4:00"}),a[3]||(a[3]=n("p",null,"리장 북쪽에 위치한 술루 구 진은 옛날 차말길을 따라 숨겨진 보석 같은 곳입니다. 복잡하고 상업화된 리장 고진과 달리, 술루는 더 조용하고 본질적인 경험을 제공합니다. 여기에서는 적은 인파와 잘 보존된 난시 문화를 만날 수 있습니다. 자갈길을 따라 걷다 보면 평온함을 느낄 수 있고, 오래된 건축물을 감상하며 마을을 관통하는 맑은 개울을 즐길 수 있습니다. 혼란스러움에서 벗어나 평온함을 찾고자 하는 분들에게 술루 구 진은 완벽한 목적지입니다.",-1)),e(r,{name:"Shuhe Ancient Town"}),a[4]||(a[4]=n("h2",{id:"리장-고진-중이-시장-본격적인-난시-문화-체험",tabindex:"-1"},[t("리장 고진 중이 시장: 본격적인 난시 문화 체험 "),n("a",{class:"header-anchor",href:"#리장-고진-중이-시장-본격적인-난시-문화-체험","aria-label":'Permalink to "리장 고진 중이 시장: 본격적인 난시 문화 체험"'},"​")],-1)),e(i,{to:"6:53"}),a[5]||(a[5]=n("p",null,"중이 시장은 리장 고진에서 가장 특색 있는 장소 중 하나로, 다양한 로컬 특산품, 공예품 및 전통 음식을 제공합니다. 신선한 농산물부터 정교한 은 세공품, 독특한 난시 의상 및 공예품까지, 이 시장은 몰입형 문화 체험을 제공합니다. 여기서는 현지 상인들과 교류하며 본격적인 거리 음식을 맛보고 전통 민속 공연을 즐길 수 있습니다. 중이 시장은 쇼핑하기 좋은 장소일 뿐만 아니라 리장의 문화와 생활 방식을 더 깊이 이해하는 이상적인 장소입니다.",-1)),e(r,{name:"Lijiang Ancient Town"}),a[6]||(a[6]=n("h2",{id:"호랑이-절벽-협곡-세계에서-가장-깊은-협곡을-걸으며-옥룡설산의-경치-감상",tabindex:"-1"},[t("호랑이 절벽 협곡: 세계에서 가장 깊은 협곡을 걸으며 옥룡설산의 경치 감상 "),n("a",{class:"header-anchor",href:"#호랑이-절벽-협곡-세계에서-가장-깊은-협곡을-걸으며-옥룡설산의-경치-감상","aria-label":'Permalink to "호랑이 절벽 협곡: 세계에서 가장 깊은 협곡을 걸으며 옥룡설산의 경치 감상"'},"​")],-1)),e(i,{to:"13:11"}),a[7]||(a[7]=n("p",null,"호랑이 절벽 협곡은 리장과 샹그릴라 사이에 위치해 있으며, 세계에서 가장 깊은 협곡 중 하나로 알려져 있습니다. 그 아름다움과 도전적인 등산로는 전 세계의 모험가들을 끌어들입니다. 호랑이 절벽 협곡을 걸으며 진사 강의 웅장한 모습을 가까이에서 감상하고 멀리서 바라보이는 웅장한 옥룡설산의 풍경을 즐길 수 있습니다. 자연 애호가이든 스릴 넘치는 등반을 좋아하는 사람이든, 이 협곡을 통과하는 여행은 잊을 수 없는 경험이 될 것입니다.",-1)),e(u,{name:"tigerYulong",alt:"호랑이 절벽 협곡에서 옥룡설산 바라보기"}),e(r,{name:"Tiger Leaping Gorge"}),a[8]||(a[8]=n("h2",{id:"옥룡설산-블루문-계곡-탐험-최고봉-등반-그리고-대규모-임프레션-리장-공연-체험",tabindex:"-1"},[t('옥룡설산: 블루문 계곡 탐험, 최고봉 등반, 그리고 대규모 "임프레션 리장" 공연 체험 '),n("a",{class:"header-anchor",href:"#옥룡설산-블루문-계곡-탐험-최고봉-등반-그리고-대규모-임프레션-리장-공연-체험","aria-label":'Permalink to "옥룡설산: 블루문 계곡 탐험, 최고봉 등반, 그리고 대규모 "임프레션 리장" 공연 체험"'},"​")],-1)),e(i,{to:"29:23"}),a[9]||(a[9]=n("p",null,'옥룡설산은 난시 사람들에게 성스러운 산으로 알려져 있으며, 그 화려한 빙하와 다양한 자연 경관으로 유명합니다. 특히 블루문 계곡은 맑은 호수와 매혹적인 파란색으로 방문객들을 사로잡습니다. 옥룡설산의 최고봉인 판시조우에 오르면 주변 산들의 경치를 한눈에 볼 수 있으며, 자연의 위대함을 느낄 수 있습니다. 또한 중국에서 가장 큰 실외 공연 중 하나인 "임프레션 리장"은 화려한 무대 효과와 활기찬 공연을 통해 난시 사람들의 역사와 문화를 생생하게 보여줍니다. 자연의 아름다움을 탐험하거나 문화 유산에 몰입하든 간에, 옥룡설산은 잊을 수 없는 여행을 선사합니다.',-1)),e(r,{name:"Yulong Snow Mountain"}),a[10]||(a[10]=n("h2",{id:"윈난-야생-버섯-핫팟-자연의-선물을-맛보다",tabindex:"-1"},[t("윈난 야생 버섯 핫팟: 자연의 선물을 맛보다 "),n("a",{class:"header-anchor",href:"#윈난-야생-버섯-핫팟-자연의-선물을-맛보다","aria-label":'Permalink to "윈난 야생 버섯 핫팟: 자연의 선물을 맛보다"'},"​")],-1)),e(i,{to:"42:19"}),a[11]||(a[11]=n("p",null,"윈난은 풍부한 야생 버섯 자원으로 유명하며, 야생 버섯 핫팟은 이러한 독특한 맛을 경험하는 최고의 방법 중 하나입니다. 신선하게 수확된 마쓰타케, 포르치니, 닭의 설사버섯 등의 야생 버섯은 맑은 국물 또는 매운 쓰촨식 베이스에서 요리되어 버섯의 자연스러운 감칠맛을 즐길 수 있게 해줍니다. 이것은 맛있을 뿐만 아니라 매우 영양가가 높아 건강한 식사를 위한 훌륭한 선택입니다. 가족이나 친구와 함께 즐기든, 윈난 야생 버섯 핫팟은 잊지 못할 미각 경험을 제공합니다.",-1)),e(o,{link:"yunnan-specialty-cuisine"})]))}});export{v as __pageData,T as default};
