import{_ as e,c as a,b as l,ai as i}from"./chunks/framework.Dm3xLcfC.js";const g=JSON.parse(`{"title":"อาหารจีน","titleTemplate":":title คู่มือ | eTourBook","description":"วัฒนธรรมการทำอาหารในแต่ละภูมิภาคของจีนมีความแตกต่างกันอย่างมาก บทความนี้จะแนะนำอาหารชั้นยอด 8 ประเภทของจีนที่จะทำให้คุณประทับใจเกินคาด","frontmatter":{"layout":"guide","category":"Learn","index":100,"title":"อาหารจีน","titleTemplate":":title คู่มือ | eTourBook","description":"วัฒนธรรมการทำอาหารในแต่ละภูมิภาคของจีนมีความแตกต่างกันอย่างมาก บทความนี้จะแนะนำอาหารชั้นยอด 8 ประเภทของจีนที่จะทำให้คุณประทับใจเกินคาด","ld+json":[{"@type":"FAQPage","mainEntity":[{"@type":"Question","name":"แปดสำรับอาหารใหญ่ของจีนคืออะไร?","acceptedAnswer":{"@type":"Answer","text":"อาณาเขตอันกว้างใหญ่ของจีนประกอบด้วยสภาพภูมิศาสตร์และภูมิอากาศที่หลากหลาย ซึ่งส่งเสริมให้มีทรัพยากรอาหารที่หลากหลาย แปดสำรับอาหารภูมิภาคหลัก หรือ 'แปดสำรับอาหารใหญ่' ได้แก่ ซานตง เสฉวน กวางตุ้ง เจียงซู ฝูเจี้ยน เจ้อเจียง หูหนาน และอันฮุย โดยแต่ละสำรับมีรสชาติ ส่วนผสม และเทคนิคการปรุงอาหารที่เป็นเอกลักษณ์เฉพาะตัว"}}]}],"head":[["link",{"rel":"alternate","href":"https://www.etourbook.com/guides/chinese-cuisine","hreflang":"x-default"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/guides/chinese-cuisine","hreflang":"en"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ja/guides/chinese-cuisine","hreflang":"ja"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/ko/guides/chinese-cuisine","hreflang":"ko"}],["link",{"rel":"alternate","href":"https://www.etourbook.com/th/guides/chinese-cuisine","hreflang":"th"}],["link",{"rel":"canonical","href":"https://www.etourbook.com/th/guides/chinese-cuisine"}],["meta",{"name":"title","content":"อาหารจีน"}],["script",{"type":"application/ld+json"},"[{\\"@context\\":\\"https://schema.org/\\",\\"@type\\":\\"FAQPage\\",\\"mainEntity\\":[{\\"@type\\":\\"Question\\",\\"name\\":\\"แปดสำรับอาหารใหญ่ของจีนคืออะไร?\\",\\"acceptedAnswer\\":{\\"@type\\":\\"Answer\\",\\"text\\":\\"อาณาเขตอันกว้างใหญ่ของจีนประกอบด้วยสภาพภูมิศาสตร์และภูมิอากาศที่หลากหลาย ซึ่งส่งเสริมให้มีทรัพยากรอาหารที่หลากหลาย แปดสำรับอาหารภูมิภาคหลัก หรือ 'แปดสำรับอาหารใหญ่' ได้แก่ ซานตง เสฉวน กวางตุ้ง เจียงซู ฝูเจี้ยน เจ้อเจียง หูหนาน และอันฮุย โดยแต่ละสำรับมีรสชาติ ส่วนผสม และเทคนิคการปรุงอาหารที่เป็นเอกลักษณ์เฉพาะตัว\\"}}]}]"]]},"headers":[],"relativePath":"th/guides/chinese-cuisine.md","filePath":"th/guides/chinese-cuisine.md","lastUpdated":1745138428000}`),n={name:"th/guides/chinese-cuisine.md"};function d(r,t,s,o,h,c){return l(),a("div",null,t[0]||(t[0]=[i('<h1 id="แปดสํารับอาหารใหญ่ของจีน" tabindex="-1">แปดสำรับอาหารใหญ่ของจีน <a class="header-anchor" href="#แปดสํารับอาหารใหญ่ของจีน" aria-label="Permalink to &quot;แปดสำรับอาหารใหญ่ของจีน&quot;">​</a></h1><blockquote><p>จากที่ราบกว้างใหญ่ มีอาหารประจำถิ่นที่มีชื่อเสียงแปดแบบ</p></blockquote><p>อาณาเขตอันกว้างใหญ่ของจีนประกอบด้วยสภาพภูมิศาสตร์และภูมิอากาศที่หลากหลาย ซึ่งส่งเสริมให้มีทรัพยากรอาหารที่หลากหลาย ภูมิภาคที่มีความแตกต่างทางภูมิอากาศอย่างมาก เช่น ทางเหนือและใต้ รวมถึงพื้นที่ชายฝั่งและภายในประเทศ มีวิถีการบริโภคอาหารที่แตกต่างกันโดยสิ้นเชิง</p><h2 id="แปดสํารับอาหารใหญ่" tabindex="-1">แปดสำรับอาหารใหญ่ <a class="header-anchor" href="#แปดสํารับอาหารใหญ่" aria-label="Permalink to &quot;แปดสำรับอาหารใหญ่&quot;">​</a></h2><p>จีนมีแปดสำรับอาหารหลัก หรือ &#39;แปดสำรับอาหารใหญ่&#39; ซึ่งได้แก่:</p><table tabindex="0"><thead><tr><th style="text-align:left;">อาหาร</th><th style="text-align:left;">รสชาติ</th><th style="text-align:left;">ส่วนผสมพิเศษ</th><th style="text-align:left;">พื้นที่</th><th style="text-align:left;">เมือง</th></tr></thead><tbody><tr><td style="text-align:left;">อาหารซานตง</td><td style="text-align:left;">สด อ่อนนุ่ม มีรสชาติ</td><td style="text-align:left;">อาหารทะเล สัตว์ปีก ผัก</td><td style="text-align:left;">ตอนเหนือ</td><td style="text-align:left;">เจิ้น, ชิงเต่า</td></tr><tr><td style="text-align:left;">อาหารเสฉวน</td><td style="text-align:left;">ชา เผ็ด หอม สด ข้นและมัน</td><td style="text-align:left;">พริก พริกเสฉวน ถั่วดำหมัก</td><td style="text-align:left;">ตะวันตกเฉียงใต้</td><td style="text-align:left;">เฉิงตู, ฉงชิ่ง</td></tr><tr><td style="text-align:left;">อาหารกวางตุ้ง</td><td style="text-align:left;">เบา อร่อย</td><td style="text-align:left;">อาหารทะเล เนื้อสัตว์ เกม</td><td style="text-align:left;">ตอนใต้</td><td style="text-align:left;">กว่างโจว, เซินเจิ้น</td></tr><tr><td style="text-align:left;">อาหารเจียงซู</td><td style="text-align:left;">สด หวาน</td><td style="text-align:left;">ปลาแม่น้ำ เนื้อสัตว์ ผัก</td><td style="text-align:left;">ตะวันออก</td><td style="text-align:left;">หยางโจว, ซูโจว, หนานจิง</td></tr><tr><td style="text-align:left;">อาหารฝูเจี้ยน</td><td style="text-align:left;">สด มีซุปที่เข้มข้น</td><td style="text-align:left;">อาหารทะเล สิ่งของหายาก</td><td style="text-align:left;">ตะวันออกเฉียงใต้</td><td style="text-align:left;">ฝูโจว, ซีเหมิน</td></tr><tr><td style="text-align:left;">อาหารเจ้อเจียง</td><td style="text-align:left;">สด มีรสชาติดั้งเดิม</td><td style="text-align:left;">อาหารทะเล ปลาแม่น้ำ ยอดไผ่</td><td style="text-align:left;">ตะวันออก</td><td style="text-align:left;">หางโจว</td></tr><tr><td style="text-align:left;">อาหารหูหนาน</td><td style="text-align:left;">เผ็ดเปรี้ยวเผ็ด</td><td style="text-align:left;">พริก ผักดอง</td><td style="text-align:left;">ตอนกลางตอนใต้</td><td style="text-align:left;">ฉางชา</td></tr><tr><td style="text-align:left;">อาหารอันฮุย</td><td style="text-align:left;">มีรสชาติเข้มข้น มีซุปที่ข้น</td><td style="text-align:left;">สิ่งของหายาก ขาหมู ใบชา</td><td style="text-align:left;">ตอนกลาง</td><td style="text-align:left;">หวงซาน, เหฟึ่ย</td></tr></tbody></table><h3 id="อาหารซานตง" tabindex="-1">อาหารซานตง <a class="header-anchor" href="#อาหารซานตง" aria-label="Permalink to &quot;อาหารซานตง&quot;">​</a></h3><p>อาหารซานตงเน้นการรักษารสชาติเดิมของวัตถุดิบด้วยการปรุงแต่งอย่างพอเหมาะและเน้นการคงไว้ซึ่งคุณลักษณะแท้จริงของวัตถุดิบ ประวัติศาสตร์ของอาหารซานตงยาวนานย้อนไปถึงสมัยชุนชิว และมีความเชื่อมโยงลึกซึ้งกับวัฒนธรรมขงจื๊อ</p><h3 id="อาหารเสฉวน" tabindex="-1">อาหารเสฉวน <a class="header-anchor" href="#อาหารเสฉวน" aria-label="Permalink to &quot;อาหารเสฉวน&quot;">​</a></h3><p>อาหารเสฉวนโด่งดังด้วยคุณลักษณะที่เรียกว่า &quot;หนึ่งร้อยจาน หนึ่งร้อยรส&quot; โดยมีรสชาติรวมถึงความเผ็ดร้อน มึนชา หอม อ่อนหวาน เค็ม และรสอูมามิ โดยเฉพาะรสชาติเผ็ดและมึนชานั้นเป็นที่สุด</p><p>อาหารเสฉวนใช้วัตถุปรุงรสหลากหลาย เช่น พริกเสฉวน พริกแห้ง ถั่วดำหมัก พริกดอง น้ำส้มสายชู ซอสถั่วเหลือง และอื่นๆ บางชนิดมีรสชาติพิเศษ อาหารเสฉวนยังใช้เครื่องเคียงและของทานเล่นหลากหลายเพื่อให้ได้ความสมบูรณ์ในด้านสี กลิ่น รส และลักษณะภายนอก อาหารเสฉวนที่มีชื่อเสียงได้แก่ กงป้าวไก่ มาโผเต้าหู้ เนื้อวัวต้มเสฉวน หรือเนื้อวัวน้ำต้ม หมูทอดสองครั้ง และหั่นเนื้อปอดคู่สามีภรรยา</p><p>นอกจากนี้ยังแบ่งออกเป็นสไตล์ <strong>เฉิงตู</strong> และสไตล์ <strong>ฉงชิ่ง</strong> ซึ่งแต่ละแบบมีลักษณะเฉพาะของตนเอง</p><h4 id="สไตล์ฉงชิ่ง" tabindex="-1">สไตล์ฉงชิ่ง <a class="header-anchor" href="#สไตล์ฉงชิ่ง" aria-label="Permalink to &quot;สไตล์ฉงชิ่ง&quot;">​</a></h4><p>สไตล์ฉงชิ่งโด่งดังด้วยรสชาติเผ็ดและมึนชา โดยเฉพาะการใช้พริกเสฉวนและพริก</p><p>[<a href="/articles/chongqing-jianghu-cuisine">ลองชิมมาล่า อาหารเจียงหูฉงชิ่ง</a>]</p><h3 id="อาหารกวางตุ้ง" tabindex="-1">อาหารกวางตุ้ง <a class="header-anchor" href="#อาหารกวางตุ้ง" aria-label="Permalink to &quot;อาหารกวางตุ้ง&quot;">​</a></h3><p>อาหารกวางตุ้งมีชื่อเสียงในการเลือกวัตถุดิบที่หลากหลาย คนกวางตุ้งให้ความสำคัญกับความสดใหม่ของวัตถุดิบตั้งแต่เนื้อสัตว์บนบกจนถึงปลาและอาหารทะเลจากมหาสมุทร อาหารกวางตุ้งยังใช้วัตถุดิบหายากมากมาย การทำติ่มซำเป็นหนึ่งในลักษณะเด่นของอาหารกวางตุ้ง การดื่มชาตอนเช้าเป็นส่วนสำคัญของวิถีชีวิตกวางตุ้ง ติ่มซำมีหลากหลาย มีการทำที่ประณีตและให้รสชาติที่หลากหลายทั้งรสเค็มและหวาน</p><h3 id="อาหารเจียงซู" tabindex="-1">อาหารเจียงซู <a class="header-anchor" href="#อาหารเจียงซู" aria-label="Permalink to &quot;อาหารเจียงซู&quot;">​</a></h3><p>อาหารเจียงซู (อาหารซูโจว) เป็นที่รู้จักในความประณีตละเอียดอ่อน โดยพ่อครัวแสดงทักษะที่ยอดเยี่ยมในการใช้มีด ซุปในอาหารเจียงซูมีชื่อเสียงเป็นพิเศษ และภูมิภาคยังเป็นที่รู้จักสำหรับของว่าง</p><h3 id="อาหารฝูเจี้ยน" tabindex="-1">อาหารฝูเจี้ยน <a class="header-anchor" href="#อาหารฝูเจี้ยน" aria-label="Permalink to &quot;อาหารฝูเจี้ยน&quot;">​</a></h3><p>มณฑลฝูเจี้ยนตั้งอยู่บนชายฝั่งตะวันออกเฉียงใต้ของจีนและมีทรัพยากรอาหารทะเลที่อุดมสมบูรณ์ รวมถึงสัตว์ป่าและเห็ดราที่หลากหลายจากภูเขา อาหารฝูเจี้ยนมักจะมีรสชาติที่เบา และเน้นการรักษารสชาติเดิมของวัตถุดิบ</p><h3 id="อาหารเจ้อเจียง" tabindex="-1">อาหารเจ้อเจียง <a class="header-anchor" href="#อาหารเจ้อเจียง" aria-label="Permalink to &quot;อาหารเจ้อเจียง&quot;">​</a></h3><p>อาหารเจ้อเจียงเน้นรสชาติแท้จริงของวัตถุดิบ โดยใช้มือเบาในการปรุงรส และหลีกเลี่ยงการใช้เครื่องเทศหรือเครื่องปรุงมากเกินไป เพื่อเน้นความสดใหม่ของวัตถุดิบ</p><h3 id="อาหารหูหนาน" tabindex="-1">อาหารหูหนาน <a class="header-anchor" href="#อาหารหูหนาน" aria-label="Permalink to &quot;อาหารหูหนาน&quot;">​</a></h3><p>อาหารหูหนานเป็นที่รู้จักในความเผ็ดร้อน แต่ต่างจากรสชาติเผ็ดและชาของอาหารเสฉวน อาหารหูหนานมักใช้พริกแห้งและพริกสดซึ่งทำให้มีความเผ็ดร้อนที่ตรงและเข้มข้นมากกว่า</p><h2 id="ความแตกต่างระหว่างตะวันออกและตะวันตก" tabindex="-1">ความแตกต่างระหว่างตะวันออกและตะวันตก <a class="header-anchor" href="#ความแตกต่างระหว่างตะวันออกและตะวันตก" aria-label="Permalink to &quot;ความแตกต่างระหว่างตะวันออกและตะวันตก&quot;">​</a></h2><p>อาหารจีนเน้นวิธีการให้ความร้อนอย่างรวดเร็ว โดยใช้เทคนิคเช่น การผัด การต้ม การนึ่ง และการเคี่ยว มันเน้นการใช้เครื่องปรุงเช่น ซีอิ๊ว น้ำส้มสายชู น้ำตาล เกลือ และผงห้ารส อาหารมีการผสมผสานรสชาติเปรี้ยว หวาน ขม เผ็ด และเค็ม ทำให้มีรสชาติหลากหลาย ส่วนผสมที่เลือกใช้ก็หลากหลายเช่นกัน รวมถึงอาหารทะเล เนื้อสัตว์ ผัก ผลิตภัณฑ์เต้าหู้ และเห็ด รวมถึงส่วนผสมที่ไม่พบบ่อยในอาหารตะวันตก เช่น หนังเต้าหู้ รากบัว และยอดไผ่</p><p>การทำอาหารจีนมีความสามารถในการใช้วัตถุดิบที่หลากหลาย โดยส่วนต่าง ๆ ของวัตถุดิบเดียวกันมักจะถูกเตรียมด้วยวิธีการปรุงอาหารที่ต่างกัน ยกตัวอย่างเช่น ไก่ นอกจากเนื้อไก่และปีกไก่แล้ว ส่วนต่าง ๆ เช่น เท้าไก่ กระเพาะไก่ หัวใจไก่ ตับไก่ ลำไส้ไก่ และเลือดไก่ สามารถนำมาทำเป็นอาหารได้หลากหลาย</p><h3 id="อาหารหลัก" tabindex="-1">อาหารหลัก <a class="header-anchor" href="#อาหารหลัก" aria-label="Permalink to &quot;อาหารหลัก&quot;">​</a></h3><p>อาหารหลักของจีนประกอบด้วยข้าว บะหมี่ มันโถว และเปาซี่ ซึ่งเป็นพื้นฐานของการรับประทานอาหารประจำวันทั่วประเทศ</p><h3 id="อาหารแบ่งกิน" tabindex="-1">อาหารแบ่งกิน <a class="header-anchor" href="#อาหารแบ่งกิน" aria-label="Permalink to &quot;อาหารแบ่งกิน&quot;">​</a></h3><p>ในวัฒนธรรมจีนแบบดั้งเดิม จะเน้นอาหารแบบครอบครัวที่ทุกคนมารวมตัวกันบนโต๊ะกลมใหญ่ และแต่ละจานจะถูกวางไว้ตรงกลางสำหรับแบ่งกันรับประทาน แต่ละคนจะตักข้าวของตนเอง จากนั้นใช้ตะเกียบหยิบอาหารลงในชามของตนเองทีละนิด</p><p>มื้ออาหารมักจะมีหลายจานและซุปหนึ่งชาม ซุปเป็นส่วนสำคัญของการรับประทานอาหารประจำวัน การดื่มซุปขณะรับประทานอาหารเป็นธรรมเนียมในอาหารจีน ในอาหารประจำวันที่บ้าน ไม่ว่าจะเป็นสามจานหนึ่งซุป สี่จานหนึ่งซุป หรือหกจานหนึ่งซุป จะต้องมีซุปหนึ่งชามเสมอ</p>',33)]))}const p=e(n,[["render",d]]);export{g as __pageData,p as default};
